css:
@-moz-document domain(hs.fi) {

html, body {
    background-color: #333333;
}

.content-wrapper {
    background-color:  #1C1B1B;
}
/* Front page background */
.teaser .teaser-text-wrapper, .articlepack-item.full, .teaser .teaser-links, .most-recent, .most-read, .right-position .is-list .vignette, .is-list .content, .is-list .is-most-read-articles-list .rank, .most-recent .is-most-recent-articles-list .timestamp, .is-list, .supersaa-widget.theme-hs .supersaa-widget-content, .live-schedule-content, .live-schedule, .hsnl-container, .breaking-news ul > li a, .hsnl-container  {
	background-color: black;
}

/* "Juuri nyt" label in breaking news */
.breaking-news ul > li .label {
    color: white;
}

/* "Ei vielÃ¤ kommentteja...", "Kirjaudu sisÃ¤Ã¤n..." text */
div.is-comments-widget p.no-messages, div.is-comments-widget div.not-logged-in-header, div.is-comments-widget div.not-logged-in-text {
    color: white;
}

/* Tilaajille -artikkelin otsikko, ingressi, kirjoittajan nimi */
.article-xl.single-article.xl-semifull .article-title, .article-xl.single-article.xl-semifull .article-ingress, .article-xl.single-article.xl-semifull .author a {
    color: white;
}

/* Comics title color */
span.hscomicnav-item-title {
    color: black;
}

/* "Haluatko lukea artikkelin" jne. */
.article-paywall .hardwall-text, .article-paywall .actions .action.login {
    color: white;
}

/* Related articles background */
.teaser-links-articlepack {
    background-color: black;
}

/* Article, comments, footer background */
.content-wrapper.article-body-container, article.single-article, .teaser-links, .content-wrapper.article-middle, .article-middle .is-list .vignette, .is-list.six-most-popular .most-popular, .article-middle .is-list, .content-wrapper.article-bottom, div.is-comments-widget > ul.comments .comment-content, .hs-header.sitewide, .main-header .header-inner-wrapper, header.main-header.sitewide, div.is-comments-widget > ul.comments li, #mobile-navigation .level-one, div.is-comments-widget blockquote, .main-header, .main-footer {
	background-color: #1C1B1B;
}

/* Link to article bottom color & width */
article.single-article .body a {
    border-bottom: 2px solid white;  
}

/* Dropdown menu "Uutiset" background, fixed header */
.section-or-tag-header h2, .is-list .vignette, .secondary-header {
    background-color: black;
}

/* Luetuimmat bottom */
.is-list .is-most-read-articles-list .list-item:nth-child(1),  .most-read .list-item, .article-bottom .is-list, .article-bottom .is-list .vignette {
    background-color: black;
}

/* Quoutations background */
article.single-article .body blockquote.ext-quote {
    background-color: #333333; 
}

/* Article bottom border */
article.single-article .related-links {
	border-bottom: 10px solid #1C1B1B;
    
}

/* Tilaa uutiskirje text */
#description {
    color: #4196A4;
}
            
/* Fonts */
.teaser h2, h3, .articlepack-item .articlepack-link, .articlepack-title, .is-list .vignette, .is-list .tabs li a, .is-list .list-item > a p, .is-list .content, .supersaa-widget.theme-hs .supers-header .supersaa-title, .supers-header-row .city, .supers-header-row .time, .temperature, .wind-speed, .wind-speed-value, .live-schedule-item-time, .live-schedule-item-name, .live-schedule-item-description, .hsnl-title, .hsnl-item-title, article.single-article .article-metasection .author a, article.single-article .body .votsikko, article.single-article .body a.person, article.single-article .body, article.single-article .body a, h1.article-title, article.single-article .taglist .hs-subjects-header, article.single-article .taglist .tag, article.single-article .taglist .hs-tag-link, article.single-article .hs-follow-tag, .is-list.six-most-popular h3, span, div.is-comments-widget .commenting-header-info h2, div.is-comments-widget span.creator.nickname /*div.is-comments-widget .title span.comment-count*/, .comment-content, div.is-comments-widget header.comments-header, div.is-comments-widget div.buttons > a.show-more-comments-not-logged-in, #mobile-navigation .level-one > li.inline-mobile-nav a, #mobile-navigation .level-one .has-subs a, .fact-box-body, .fact-box-title, div.is-comments-widget blockquote, .main-footer .footer-link, article.single-article .body h2.mid-weight, .hsnl-grid-3col .hsnl-grid-item, a, .article-xl.single-article.xl-picture-top .xl-header-picture-top .article-title, .article-xl.single-article.xl-picture-top .xl-header-picture-top .article-ingress, .article-xl.single-article.xl-picture-top .author a,  article.single-article .body ul.sndp-ul, article.single-article .body ol.sndp-ol, article.single-article .person-meta ul.sndp-ul, article.single-article .person-meta ol.sndp-ol, .live-post .livepost-title, .live-post-byline time {
    color: white;
}

/* Comment sorter */
div.is-comments-widget header.comments-header .select-container .select select {
    color: white;
    border-color: white;
}
  
}
