css:
@-moz-document url-prefix("https://web.hike.in/") {
/* chat menu 3 dots */
.mdc-menu .mdc-list {
    color: white;
    background: #21252b
}

/* fill white in all svg */
#sticker-bg-icon {
    fill: #00ACFF !important;
}

/* main chat background */
.reverseChat,
.gh_gk {
    background: #21252b
}

/* my messages */
.gh_gi .gh_gj .gh_gs .gh_ii.gh_iu.gh_iw {
    background-color: #00ACFF;
}

/* my messages time */
.gh_gi .gh_gj .gh_gs .gh_ii.gh_iw .gh_g0 .gh_h1 {
    color: white;
}

/* theirs messages */
.hj_hk .hj_hl .hj_hu .hj_jh.hj_jt.hj_ju {
    background-color: #009688;
}

/* their message time */
.hj_hk .hj_hl .hj_hu .hj_h2 .hj_i0 .hj_jf {
    color: white;
}

/* their message name */
.hj_hk .hj_hl .hj_ht {
    color: white;
}

/* their message text */
.as_b {
    color: white;
}

/* left sidebar bacground */
.b8_b9 .b8_cr .b8_cs * {
    background: #21252b;
}

/* left sidebar text color */
.b8_b9 .b8_cr .b8_cs * {
    color: aliceblue;
}

/* top app bar including left sidebar top */
.mdc-top-app-bar__row {
    color: white;
    background: #00acff;
}

/* right top bar */
.ej_ez, .ej_e0 {
    background: #00acff;
}

/* message bar background */
.l0_l2 .l0_l3 {
    background-color: #21252b;
}

/* right profile top half bg */
.mdc-card {
    background: #21252b;
}

/* right rest of sidebar bg */
.gaIZGz {
    background: #21252b;
}

/* right people names */
.mdc-list-item__primary-text {
    color: white
}

/* right profile name text */
.ej_fp {
    color: white;
}

/* main heading middle of page */
.f6_kr .mdc-top-app-bar__title {
    color: white;
}

/* main heading subtext */
.gw_mq .mdc-top-app-bar__title .gw_ms {
    color: white;
}

/* right cross button */
.ej_e3, .ej_e4 {
    color: white;
}

/* right header text */
.ej_ez, .ej_e0 {
    color: white;
}

/* setting button */
.mdc-top-app-bar .mdc-top-app-bar__action-item,
.mdc-top-app-bar .mdc-top-app-bar__navigation-icon,
.mdc-top-app-bar__title {
    color: white;
}

/* read by */
.gh_gi .gh_gj .gh_gs .gh_gu .gh_gw {
    color: white;
}

/* right sidebar options */
.ej_en .ej_eo {
    color: white;
}

/* start screen main bg color */
.ku_kv {
    background: #21252b;
}

/* "click on a chat to start messaging" */
.ku_kv .ku_kw .ku_kx {
    color: white
}

#add-members-icon,
#shared-media-icon,
#mute-chat-icon,
#msg-read,
#msg-delivered,
#msg-pending,
#msg-sent {
    fill: white;
}

/* bottom popuop corners background */
.l0_l2 .l0_l5 .l0_l6 {
    background: #21252b;
}

/* login page left section */
.i_j .i_k {
    background: #21252b;
}

/* login page left section text */
.i_ap {
    color: white;
}

/* login page right section */
.i_w {
    color: white;
    background: #21252b;
}

/* login page right section floating labels */
.mdc-select:not(.mdc-select--disabled) .mdc-floating-label {
    color: white;
}

.mdc-text-field:not(.mdc-text-field--disabled) .mdc-floating-label {
    color: white;
}

.mdc-select:not(.mdc-select--disabled) .mdc-select__native-control, .mdc-select:not(.mdc-select--disabled) .mdc-select__selected-text {
    color: white;
}

.mdc-text-field:not(.mdc-text-field--disabled) .mdc-text-field__input {
    color: white;
}

/* login page right section bottom bar */
.i_aa {
    background: #21252b;
}

.i_aa .i_ab, .i_aa .i_ae {
    color: white;
}

/* login page bg */
.a5_b {
    background: black;
}


/* MAIN CHAT */
/* main chat page background */
.as_b {
    background: black;
}

/* date labels */
.hj_hk .hj_hl .hj_hp.hj_hr {
    color: white;
}

/* top bar subtitle */
.ku_mk .mdc-top-app-bar__title .ku_mm {
    color: white;
}

/* right sidebar top */
.cg_cw, .cg_cx {
    color: white;
    background: #00acff;
}

/* right sidebar close icon */
.cg_c0, .cg_c1 {
    color: white;
}

/* right sidebar first half option text */
.cg_ck .cg_cl {
    color: white;
}
}
