css:
@-moz-document url-prefix("https://vimetop.ru") {
.hk-pg-wrapper {
    background: #121212;
}
.card {
    background-color: #303030;
}
.text-dark {
    color: #fff !important;
}
.stats-row {
    background: #303030;
    color: #fff;
    border-bottom: 1px #404040 double;
}
.stats-row span {
    background: #303030;
    color: #fff !important;
}
h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
    color: #fff;
}

h5 {
    color: #ccc;
}

.vt-color {
    color: #ABCDEF !important;
}

.card {
    border: none;
    border-radius: 0;
}

.card-header {
    background: #202020 !important;
    border: none;
    border-radius: 0 !important;
}
.text-warning {
    opacity: 0.8;
    color: #ccc !important;
}

.rounded {
    border-radius: 0 !important;
}

.offline {
    display: none;
}

.bg-white {
    background-color: #303030 !important;
}

.nav.nav-light.nav-tabs .nav-link.active, .nav.nav-link:hover, .nav.nav-light.nav-tabs .nav-link.active:hover, .nav.nav-light.nav-tabs .nav-link.active:focus {
    color: #fff;
}

.nav.nav-light.nav-tabs .nav-link {
    color: #777;
    border-color: transparent;
}

.list-group-item {
    border: none;
    background: none;
}

.list-group-item:hover {
    background: #444;
}

body {
    color: #ccc;
}

.mc-text {
    font-family: inherit;
    font-size: 15px !important;
}

.border-success, .border-green {
    border-color: #444 !important;
}

.btn-group.btn-group-rounded > .btn {
    border: none;
}

#block-like > button > i {
    padding: 0px 5px 0px 5px;
}

#block-like > .btn-outline-danger {
    display: flex;
    flex-direction: row-reverse;
}

}
