css:
@-moz-document domain("nintenderos.com") {
body,
.m-sidebar,
.login-fancy,.login-fancy label,
#searchform,#searchform * /*div buscador*/
{
    background-color:#333;
    color:lightgray;
}

.c-post-list__content, /*oscurecer cada noticia*/
.page-numbers.current, /*Nº pagina actual*/
.custom-content.active,
.login-fancy input,
select, /*drop-down de filtrar por fecha*/
.c-header, /*barra superior*/
.form-element *, /*buscador integrado*/
.c-post-author__container::before,.c-post-author__content * /*firma autor articulos*/
{
    background-color:#222 !important;
    color:lightgray !important;
}

.reply {
    border-bottom: 1px solid lightgray;
}

.c-post-single__title,.c-post-single__subtitle, /*titulo noticia dentro de noticia*/
.c-post-featured__title-link,.c-post-list__title-link,.c-post-list__author,
.evento-home .title,
.c-post-single .tags a,
.h-subtitle,.supertitle,.analisis-home h3,.lanzamiento h3,.tabla_ran h3,
.dsq-widget-item a,.dsq-widget-user /*Barra lateral comentarios*/
{
    color:#eee;
}

.c-post-categories__list /*Categorías de noticias (encima de cada titular)*/{
    background:transparent !important;
}

a{color:#adadff;}
a:visited{color:#ffa1cb;}
}

@-moz-document url-prefix("https://www.nintenderos.com/foro-nintendo") {
.bbp-topics .topic, /* lista temas foro */
.c-post-single .bbp-body > div,.user-desde /*mensajes foro*/
{
    background-color:#222 !important;
    color:lightgray !important;
}
/*temas fijados en lista de temas*/
.bbp-topics ul.super-sticky 
{
        background-color:#32312a !important;
}
 /*cabecera lista tema*/
.bbp-header,.bbp-footer,.page-numbers
{
        background-color:#111 !important;
}
/*linea separatoria entre mensajes*/
.reply {
    border-bottom: 1px solid lightgray;
}
}
