css:
/* For Firefox 3.5+, I recommend ‘Rich Man’s New Tab Button’ <http://userstyles.org/styles/19333>. */


@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#content .tabs-container .box-inherit.scrollbox-innerbox {
/* Space on the right side of the full tab bar */
	padding-right: 20px;

/* Greyscale background ‘New Tab’ image */
	background-repeat: no-repeat;
	background-position: right;
}
#main-window:not([stylish-platform^="Linux"]) #content .tabs-container .box-inherit.scrollbox-innerbox {
	background-image: url(data:image/png;base64,);
}
#main-window[stylish-platform^="Linux"] #content .tabs-container .box-inherit.scrollbox-innerbox {
	background-image: url(data:image/png;base64,);
}
