css:
@-moz-document url-prefix("https://juejin.im"), url-prefix("https://juejin.im") {
body {
 background-color: #D0D0D0 !important;
}
.view-container {
 min-height: 100vh;
}
.container {
 max-width: 1600px;
}
.main-area,
.entry-public-main {
 width: 1300px !important;
 max-width: 1600px !important;
}

/* 右边：推介、下载、广告 & 左边：喜欢、分享等按钮*/
.panel-btn,.share-title,
.wechat-sidebar-block,
.recommend,
.app-download-sidebar-block,
.index-book-collect{
 display: none;
}
/* 右边：相关文章、目录、关于作者 */
.catalog-block,
.related-entry-sidebar-block,
.author-block{
 background-color: rgba(243, 245, 247, .5) !important;
}
.catalog-block:hover,
.related-entry-sidebar-block:hover {
 background-color: rgba(243, 245, 247, .9) !important;
}
}
