css:
@-moz-document domain(f-list.net) {

#Header {
    height: 62px;
}

#Logo {
    position: absolute;
    left: 20px;
    top: 22px;
    width: 200px;
    height: 65px;
    background-size: 200px auto;
}

#TopAdZone {
    height: 60px;
    padding-right: 5px;
    padding-top: 5px;
    overflow: hidden;
}

#TopAdZone > ins > a > img {
    height: calc(100% - 3px);
    width: auto;
    display: none;
}
  
 .ui-flexmenu-body {
    max-height: 500px;
}


#Header {
    height: 66px;
}

#Frontpage_Stats {
    top: 60px;
    left: 260px;
}

span.ui-flex-label {
    background: #000000;
}

.Character_PageAvatar {
    text-align: center;
    width: 100%;
}

.Character_PageAvatar img {
    margin: auto;
}

.charactionmenu a {
    background-image: none !important;
    padding: 5px 2px;
    text-align: center;
}

.Character_Fetishlist a.Character_CustomFetish {
    font-weight: normal;
}

.charactionmenu a:nth-child(odd) {
	margin-right: 0px;
	width: 58px;
}

.charactionmenu a:nth-child(even) {
	margin-left: 2px;
	margin-right: 2px;
	width: 78px;
}

.charactionmenu a:hover {
    background-color: #315227;
}
  

#Frontpage_OnlineFriends a {
    height: 180px;
    width: 150px;
    display: inline-block;
    text-align: center;
    border: 1px solid #464646;
    margin: 0px !important;
    margin-bottom: 2px !important;
}

#Frontpage_OnlineFriends span {
    font-style: italic;
    float: right;
    margin: 10px;
}

#Frontpage_OnlineFriends img {
    width: 100px;
    height: 100px;
    vertical-align: middle;
    clear: both;
    margin: 5px 21px;
}

a.bookmarkdel {
    background-color: #005a8c;
    color: white;
}

select#Character_QuickCompareSelect {
    width: 150px !important;
}

.thumbbox > a > img.thumbnail {
    min-width: 100px !important;
    height: auto !important;
    display: inline-block;
    vertical-align: middle !important;
}

.character_image_preview_icon {
    background-size: 50px;
    margin-left: 20px;
    margin-top: 20px;
    margin-bottom: -20px;
}

.character_image_icon {
    width: 150px;
    height: 150px;
    border-radius: 5px;
    margin-left: 0px;
    background: #2e2828;
}

.character_image_actions {
    width: 50px;
    float: right;
    margin-top: -100px;
    text-align: center;
}

.character_image_icon > .character_image_usedby {
    text-align: center;
	padding-top: 0px;
}

#existingIcons > .character_image_icon {
    width: 150px;
    height: 150px;
    border-radius: 5px;
    margin-left: 0px;
    background: #2e2828;
}

#existingIcons > .character_image_icon > .character_image_preview_icon {
    background-size: 50px;
    margin-left: 20px;
    margin-top: 20px;
    margin-bottom: -20px;
}

#existingIcons > .character_image_icon > .character_image_actions {
    width: 50px;
    float: right;
    margin-top: -100px;
    text-align: center;
}

#existingIcons > .character_image_icon > .character_image_icon > .character_image_usedby {
    text-align: center;
	padding-top: 0px;
}

#existingInlines > .character_image > .character_image_preview {
    width: 180px;
}
	
#existingInlines > .character_image >  .character_image_actions {
    margin-top: 0px;
}

#existingInlines > .character_image {
    overflow: auto;
}

#bookmarks_content > .TrackingCharacter {
    float: left;
    width: calc(20% - 22px);
    height: 50px;
    border: 1px solid black;
}

#bookmarks_content > .TrackingCharacter > img {
    width: 50px;
    float: left;
    margin-right: 10px;
    height: 50px;
    margin-top: 0px;
}

a#fbtab_threads, a#fbtab_personals {
    display: none;
}

#SettingsAccordion > div:nth-child(1) > div, #SettingsAccordion > div:nth-child(2) > div, #SettingsAccordion > div:nth-child(3) > div {
    height: 400px !important;
}


#SettingsAccordion > div:nth-child(4) > div {
	height: 3948px !important;
}

div[class^="defriend"] {
    width: calc(33% - 21px) !important;
    display: inline-block;
    min-width: 500px;
    margin-right: 3px;
}

div[class^="defriend"] > .avatardest > img {
    display: none;
}

#Note_Box .avatarsource, #Note_Box .avatardest {
    width: 70px;
}

#Note_Box .avatarsource {
    float: left;
    margin-right: 5px;
}

#Note_Box > a > .list-highlight {
    height: 70px;
}

td#Content > a.ResultListing {
    display: inline-block;
    float: left;
    clear: none;
    min-width: 180px;
    margin-left: 5px;
    margin-bottom: 3px;
    background: #4c4646;
}

td#Content > a:nth-last-child(1) {
    clear: both;
    margin-top: 20px;
    width: calc(100% - 20px);
}

td#Content > a.ResultListing:hover {
    background: #005a8c;
}

#friendform > #frienddata > div[class^="defriend"] {
    min-width: calc(100% - 15px);
	}
	
#CharacterImageList > div.character_image > .character_image_actions {
    margin-top: 0px;
}

}
