css:
@-moz-document domain("2e.aonprd.com") {
body,
html {
    font-size: 14px;
    background:#333!important;
    line-height: 1;

}

body,
h1,
h2,
h3,
h4,
h5,
h6,
input,
button,
textarea,
.menutitle h1,
.menutitle h2,
.menutitle h3 {
    font-family: "Trebuchet MS", "Times New Roman", serif;
    color: #bba;
}
h1,
h2,
h3,
.menutitle h1,
.menutitle h2 {
    font-size: 1.25rem;
    padding: 0.25em!important;
    margin: 0!important;

}
.menutitle {
    display:none;
}
.theme-toggle-wrapper{
    display:none;
}
body.dark,body.light,#page {
    color: #bba;
    border: 0!important;
}
#main-wrapper{
    background: #333!important;
}
body.dark .external-link,
body.light .external-link{
    color: #ddd;
}
#main-menu,
#main-menu a {
    background: 0;
    font-family: "Trebuchet MS", "Times New Roman", serif;
    color: #bba;
    padding: 0;
    line-height:1;
}
body.dark #page,body.light #page {
    border: 0;
    background: #333;
    max-width:1080px;
    width:100%;
    display:block;
    margin:0 auto;
}
img[alt="flourish"], #main-menu img.flourish {
    display: none
}
img {
    margin:0!important;
    padding:0!important;
    vertical-align:middle;
}
body.dark #main-wrapper,
#main-wrapper {
    background: #333;
    margin: 0;
    margin-left: 200px;
}
.old {
    margin-left: 0 !important;
}
#wrapper {
    margin: 0;
    width: 100%;
}
#main-menu #minMaxMenu {
    display: none;
}
#main-menu {
    background: 0;
    text-align: left;
    padding: 0;
    margin: 0;
    width: 200px;
}
.brand {
    display:none!important;
}
#main-menu.old {
    padding: 0;
    background: none;
    width: 200px;
}
#main-menu a {
    text-align: left;
    font-size: 14px;
    margin: 0;
}
#main-menu.old .nav-parent {
    margin: 0;
    padding-left: 0;
}
#main-menu .nav-parent {
    background: 0;
    padding: 0;
}
.splash {
    display: none;
}
body {
    margin: 0;
    padding: 0;
}
hr {
    border: 0;
    margin:0;
    padding:0;
    height:0;
}
h1.title {
    background: #998;
    color: #333;
}
h1.title a {
    color: #333 !important;
}
body.dark h2.title,
body.dark h3.title,
body.light h2.title,
body.light h3.title{
    background: #444;
    color: #bba;
}
b,
a {
    color: #eed !important;
}
#main {
    line-height: 1.5;
}
h1 span,
h2 span,
h3 span {
    padding-right: 0.25rem;
}
.trait {
    background: #7669;
    border: 0!important;    
    color: #ccb;
    margin-right: 0.25rem;
}
tr {
    background-color: #333 !important;
    border-bottom: 3px solid #333 !important;
}
table,
td {
    color: #bba;
    border: 0 !important;
}
td {
    border: 0;
}
body.dark .sidebar,
body.light .sidebar {
    background: #222;
    border: 5px solid #333;
    border-radius: 8px;
}
body.light .actiondark {
    filter:invert(1);
}
.search {
    padding: 0;
    margin: 0;
        height: 30px;

    width: 100%;
}
.search-input,button {
    box-sizing: border-box !important;
    height: 30px;
    padding: 3px !important;
    background:transparent;
    font-size:16px;
    border: 0 !important;
    border-radius: 0 0 0 0 !important;
}
.search-input {
    width:100%!important;
    left:0;
}
#footer{
    display:none!important;
}
input,select{
    background:#333;
    color:#ddd;
    border:1px solid #fff2;
    font-size: 18px;
    padding: 10px;
}
option{
    background:#333;
}
body.light .sidebar-nofloat,
body.dark .sidebar-nofloat {
    border: none;
    background-color: #444;
}
a {
    color: inherit !important;
}
img[alt="Reaction"],
img[alt="Single Action"],
img[alt="Free Action"],
img[alt="Two Actions"],
img[alt="Three Actions"]
{
    filter: invert(1);
    margin-top:-5px!important;
}
ul {
    margin:0;
}
input {
    padding:0.25rem;
    background:#fff2;
}
.search-input {
    margin:0!important;
    padding:0!important;
    font-size:14px;
    outline:0;
}
.search-input input {
    width:100%;
}
button {
    border:0!important;
    cursor:pointer;
    filter: invert(1);
    background:#ccc;
    margin:0!important;
    padding:0!important;
    position:relative!important;
    float:right;
}
#ctl00_MainContent_DetailedOutput {
    display:block;
    margin:0 auto;
}
}
