css:
@-moz-document url-prefix("https://buildbot.neolinks.com") {
body{
    background: url(https://www.toptal.com/designers/subtlepatterns/patterns/footer_lodyas.png) !important;
    background: url(https://www.toptal.com/designers/subtlepatterns/patterns/grey_wash_wall.png) !important;
    background: url("https://www.toptal.com/designers/subtlepatterns/patterns/black-Linen.png")!important;
    
    color:#fff !important;
}

a:link, a:visited, a:active {
    color: #aaa;
}

td a:link,td a:visited,td a:active {
    color: #222;
}

td.Activity, td.Change, td.Builder {
    color: #aaa;
    background-color: #ffffff30;
}

td.Time {
    border: #ffffff30;
    color: #aaa;
    background-color: #ffffff30;
}


.LastBuild.success {
    background-color: #3c6382;
}
.success {
    color: #000;
    background-color: #2ed573;
    border:2px solid #00000011 !important;
}
.warnings {
    color: #FFFFFF;
    background-color: #ffa502;
    border:2px solid #00000011 !important;
}
.running, .waiting, td.building {
    color: #000;
    background-color: #7bed9f;
    border:2px solid #00000011 !important;
}

.start ,.LastBuild {
    border:2px solid #00000011 !important;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
}
.start {
    color: #eee;
    background-color: #ffffff11;
}
.Builder a:link,.Builder a:visited,.Builder a:active,.start a:link,.start a:visited,.start a:active {
    color: #bbb;
}

table {
    border-spacing: 3px 0px;
}

td.Change {
    border-radius: 0px;
    background-color: #079992;
}

span.stdout {
    color: #fad390;
}

span.header {
    color: #82ccdd;
}

p a:link, p a:visited, p a:active {
    border-radius: 10px;
    padding: 3px;
    background: #795548;
    color: #fff;
    text-decoration: none;
    height: fit-content;
    display: block;
    width: fit-content;
    float: left;
    padding: 3px;
    margin-left: 6px;
}

p{
    display: flex;
    padding: 10px;
    overflow: auto;
}
b{
    display: block;
    height: fit-content;
    padding: 10px;
}
p::-webkit-scrollbar-track
{
	/*-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);*/
	/*background-color: #F5F5F5;*/
}

p::-webkit-scrollbar
{
	height: 6px;
	background-color: #ffffff11;
}

p::-webkit-scrollbar-thumb
{
	background-color: #7955489c;
}



}
