css:
@-moz-document domain(basecamp.com) {
body {
    background: #2c3e50;
}
#main-content {
    background-color: #2c3e50;
}
.panel.panel--perma,.sheet {
    background-color: #34495e !important;
    color: white !important;
    box-shadow: none !important;
}
.panel--perma, .panel--project {
    background: #2c3e50 !important;
    color: white !important;
    box-shadow: none !important;
}
.trix-contained-input trix-editor {
    color: black;
}
.project-avatars__edit::after {
    box-shadow: none;
}
.panel--perma, .panel--project {
    color: #34495e !important;
}
.card {
    background: #34495e !important;
    color: White;
    
}
.todo-progress__ratio {
    color: #aaa;
}
.link-to-archived-children {
    background: transparent;
}
.thread__subscriptions {
    border: none;
}
.break > span {
    background: transparent !important;
    color: white;
}

.report-menu__item.report-menu__item--selected {
    background: #fff !important;
    color: #34495e;
}
.recordable {
    background: transparent;
    color: white;
}

.report-menu__item {
    background: #34495e;
    color: white;
}
.perma__menu {
    color: #2c3e50;
}
.thread-entry {
    background: #2c3e50;
}
.thread__subscribers,
.thread__self-subscription {
    background: transparent;
}
.u-position-context {
    background: transparent;
    box-shadow: none;
}
#my_navigation {
    background: #34495e;
}
.u-display-ib {
    background: transparent;
}
html[data-theme="charcoal"]:not([data-bridge-configuration~="ios"]):not([data-bridge-configuration~="android"]) .nav__link,
html[data-theme="charcoal-alt"]:not([data-bridge-configuration~="ios"]):not([data-bridge-configuration~="android"]) .nav__link {
    color: white;
}
.perma-toolbar + .recordable {
    background: transparent;
    color: white;
}
}
