css:
@-moz-document url-prefix("http://127.0.0.1") {
body {
    font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue, Roboto, sans-serif;
    color: #ffffff !important;
}
.wcDocker,
.wcFrameFlasher,
.wcFrameShadower,
.wcFrameTitleBar,
.wcModalBlocker,
.wcPanelBackground,
.wcPanelBackground .wcCenter {
    background-color: #1f1f1f;
}
.negative-space {
    background: #222;
}
.card {
    background-color: #484d56;
}
.card-header {
    background-color: #1b1e1e;
    border-bottom: 1px solid #dde0e6;
}
.navbar {
    background-color: #393a3d;
}
.nav-tabs {
    background-color: #1b1e1e;
    border-bottom: 1px solid #1b1e1e;
    border-top: 1px solid #484d56;
}
.nav.nav-tabs .nav-link {
    color: #9aa1aa;
}
.nav.nav-tabs .nav-link.active {
    border-bottom: 3px solid #ffffff!important;
    border-bottom-width: 3px !important;
    border-bottom-style: solid !important;
    border-bottom-color: rgb(124, 182, 228) !important;
    color: #ffffff;
}

.CodeMirror-line {
    background-color: #484d56 !important;
}

.wcFrameButtonBar {
    background-color: #1f1f1f;
}
.wcFrameTitle,
.wcPanelTab {
    color: #ffffff;
}
.aciTree .aciTreeButton,
.aciTree .aciTreeColumn,
.aciTree .aciTreeIcon,
.aciTree .aciTreeItem,
.aciTree .aciTreePush,
.aciTree .aciTreeText {
    color: #ffffff;
}

.aciTree .aciTreeSelected .aciTreeLine,
.aciTree .aciTreeSelected .aciTreeLine .aciTreeItem,
.aciTree.aciTreeFocus .aciTreeFocus .aciTreeLine {
    background-color: #3f4042!important;
    border-color: #3f4042;
    border: none!important;
    border-right: 0px
}

.aciTree .aciTreeLine.aciTreeHover .aciTreeItem {
    background-color: #3c3c3f!important;
}

.aciTree .aciTreeLine.aciTreeHover {
    background-color: #3c3c3f!important;
}

.table {
    background-color: #1f1f1f;
    color: #ffffff
}
.table .tr .hover .td {
    background: #3c3c3f
}

.backgrid thead td,
.backgrid thead th {
    background: #3f4042;
    background-color: #3f4042!important;
    text-align: left;
}

table tr th button {
    color: #ffffffff;
}

tr:hover {
    background-color: #3f4042!important;
    color: #ffffffff!important;
}

.dropdown-menu {
    background: #1f1f1f!important;
}
.dropdown-item {
    color: #ffffff;
    background: #1f1f1f;
}

.dropdown-menu > li > a:hover,
.dropdown-menu > li > a:focus {
    background-image: none !important;
}
.dropdown-menu > li > a:hover,
.dropdown-menu > li > a:focus {
    background-color: #3f4042;
}

.editor-toolbar {
    background: #1f1f1f;
}

.pg-el-container,
.wcLayout,
.wcWide,
.wcTall,
.wcPanelTabContent,
.wcDocker,
.wcFrameFlasher,
.wcFrameShadower,
.wcFrameTitleBar,
.wcModalBlocker,
.wcPanelBackground,
.wcPanelBackground .wcCenter {
    background-color: #1f1f1f;
}

.alert-info {
    background-color: #3f4042;
    border-color: #999999;
    color: #ffffff;
}

.wcFrameTitle,
.wcPanelTab {

    color: #ffffff;
}
.wcPanelTabActive {
    color: #ddd;
    border-bottom: 3px solid #999999;
}
.wcPanelTab:hover {
    color: #ddd;
}

.datagrid .slick-header .slick-header-columns,
.grid-canvas {
    background: #1f1f1f;
    font-weight: 600;
    color: #ffffffff;
}
.ui-widget-content.slick-row.even,
.ui-widget-content.slick-row.odd {
    background-color: #1f1f1f;
    font-weight: 600;
    color: #ffffffff;
}

.CodeMirror-scroll,
.CodeMirror-gutters {
    background-color: #1f1f1f;
    color: #ffffffff;
}

.CodeMirror-linenumber.CodeMirror-gutter-elt {
    color: #ffffffff;
    text-shadow: -1px 0 #00000047, 0 1px #00000047, 1px 0 #00000047, 0 -1px #00000047;
}

span.CodeMirror-selectedtext {
    background: #000;
}

.cm-s-default .cm-keyword {
    font-weight: 600;
    color: #317dff;
    text-shadow: -1px 0 #00000047, 0 1px #00000047, 1px 0 #00000047, 0 -1px #00000047;
    
}

.cm-s-default .cm-variable-2{
    color: #31baff;
    text-shadow: -1px 0 #00000047, 0 1px #00000047, 1px 0 #00000047, 0 -1px #00000047;
}

.obj_properties .backform-tab>.tab-content {
    background-color: #3f4042;
}

.alertify .ajs-body .alertify .ajs-dialog{
    color: #ffffffff;
    background-color: #3f4042;
}

.alertify .ajs-header, .alertify .ajs-header:hover {
    background-color: #1b1e1e;
}

.select2-container--default .select2-results__option--highlighted[aria-selected] {
    background-color: #999999;
    color: #000
}
.select2-dropdown{
    color: #000!important;
}

fieldset.inline-fieldset{
     background-color: #1b1e1e;
}


.btn-primary {
    color: #fff;
    background-color: #3f4042;
    border-color: #3f4042;
}

.btn-primary:hover {
    background-color: #3f4042b8;
    border-color: #3f4042b8;
}

.btn-primary.disabled, .btn-primary:disabled {
    color: #fff;
    background-color: #999999;
    border-color: #999999;
}
}
