css:
@-moz-document domain("stackedit.io") {
/**
 * Set main colors
 */
:root{
    --white: #ffffffde;
    --bg: #282828;
    --bg0_h: #1d2021;
    --bg0: #282828;
    --bg1: #3c3836;
    --bg2: #504945;
    --bg3: #665c54;
    --fg: #ebdbb2;
    --fg1: #ebdbb2;
    --fg2: #d5c4a1;
    --red: #cc241d;
    --yellow: #d79921;
    --orange: #d65d0e;
    --gray: #928374;
    --blue: #458588;
}


/*
 * Page settings
 */
body, code, pre {
    background-color: var(--bg);
	font-size: small !important;
    caret-color: var(--white) !important; 
}
#preview-contents {
    font-size: small !important;
}

h1, h2, h3, h4, h5, h6 {
     margin:1.8em 0;
     line-height:1.33;
     color: var(--white);
}


/**
 * Markdown syntax
 */
.markdown-highlighting .img, 
.markdown-highlighting .imgref,
.markdown-highlighting .link,
.markdown-highlighting .linkref{
    color: var(--gray)
}

.markdown-highlighting .keyword {
 color: var(--gray);
 font-weight:600;
}

.markdown-highlighting .img .cl-underlined-text,
.markdown-highlighting .imgref .cl-underlined-text,
.markdown-highlighting .link .cl-underlined-text,
.markdown-highlighting .linkref .cl-underlined-text {
    color: var(--gray);
}

.markdown-highlighting .pre,
.markdown-highlighting .pre * {
    line-height:1.33;
    color: var(--white);
}
.markdown-highlighting .pre [class*=language-] {
    color:#7f7f7f;
}

.markdown-highlighting .entity {
    color:#7f7f7f;
}

.markdown-highlighting .cl,
.markdown-highlighting .hr {
    color: var(--white);
}

.markdown-highlighting .blockquote,
.markdown-highlighting {
     color: var(--white);
     caret-color: var(--white);
}

.markdown-highlighting .latex,
.markdown-highlighting .math {
 color:var(--gray);
}


/**
 * Editor
 */
.preview__inner-1,
.editor__inner,
.editor,
.layout__panel--button-bar, .layout__panel--preview{
    background-color: var(--bg);
    color: var(--white);
}

.button-bar__button:active,
.button-bar__button:focus{
    color: var(--blue);
}

.explorer__tree, 
.side-bar__inner{
    background-color: var(--bg1);
    color: var(--white);
}

.side-bar__info{
    background-color: var(--bg2);
}

.explorer-node__item, 
.menu-entry,
.menu-entry:hover{
    color: var(--white);
}

.menu-entry__label{
    color: var(--gray);
    
}

.side-title{
    background-color: var(--bg0_h);
    color: var(--white);
}

.textfield[disabled] {
    background-color: var(--bg2);
    cursor:not-allowed;
    color:#999
}

.modal__sponsor-banner {
 font-size:0;
}

.modal__sub-title{
    color: var(--fg2);
}
.modal__info{
    background-color: var(--bg2);
}
.form-entry__label{
    color: var(--white);
}

.textfield{
    background-color: var(--bg2);
}
.button,
.button:hover{
    color: var(--white);
}

.modal__inner-2{
    background-color: var(--bg1);
    color: var(--white);
}

.stat-panel{
    color: var(--white);
    background-color: var(--blue);
}
}
