css:
@-moz-document url-prefix("https://minehut.com/") {
.toolbar-heading[_ngcontent-c13] {
    background: white;
    color: #777;
    position: fixed;
    z-index: 20;
}

.link[_ngcontent-c13] {
    color: #777;
    margin-top: 2px;
    margin-right: 20px;
    text-decoration: none;
    border-bottom: 2px solid transparent;
    font-size: 16px;
}

.mat-icon {
    background-repeat: no-repeat;
    display: inline-block;
    fill: #588cf7;
    height: 24px;
    width: 24px;
}

.mat-toolbar[_ngcontent-c12] {
    height: 48px;
    border-bottom: 3px solid #588cf7;
    background: #ebebeb;
    color: #777;
}

.mat-card .mat-card-subtitle, div, h1, h2, h3, h4, h5, h6, p {
    color: #777;
}

.server-dashboard-content[_ngcontent-c12] {
    background-color: white;
}

.mat-card {
    border-bottom: 4px solid #588cf7;
    -webkit-box-shadow: none!important;
    box-shadow: none!important;
    background-color: #ebebeb;
    color: #777;
}

