}

.nav-list[_ngcontent-c2] a[_ngcontent-c2] {
    font-size: 12px;
    padding-right: 25px;
    font-weight: 400;
    border-right: 0px solid white;
}

textarea[_ngcontent-c31] {
    max-width: calc(100% - 7px);
    width: calc(100% - 7px);
    min-height: 500px;
