@keyframes chuyenMauChu {
    0% {
        color: #f00;
    }
    33% {
        color: #0f0;
    }
    66% {
        color: #00f;
    }
    100% {
        color: #f00;
    }
}

.title.ytd-video-primary-info-renderer {
    /* animation: 'chuyenMauChu' 10s linear infinite; */
    text-shadow: 0 0 10px #fff;
    position: relative;
    display: block;
    border-radius: 10px;
    overflow: hidden;
}

@keyframes chuyenDong {
    0% {
        left: -30%;
    }
    50% {
        left: 70%;
    }
    100% {
        left: -30%;
    }
}



.title.ytd-video-primary-info-renderer *{
    z-index: 2;
}

.title.ytd-video-primary-info-renderer::before {
    animation: 'chuyenDong' 10s linear infinite;
    content: " ";
    display: block;
    height: 100%;
    background: linear-gradient(90deg, transparent 0%, rgba(255, 255, 255, 0.2) 50%, transparent 100%);
    width: 60%;
    position: absolute;
    top: 0;
    z-index: 1;
    border-radius: 20%;
}

