css:
@-moz-document domain("eviebot.com"), domain("boibot.com"), domain("chimbot.com"), domain("williambot.com") {
html {
    color: #fff;
}
#footer,
body {
    background-color: #232449;
}
body > p:first-of-type {
    background-color: #1b1b38;
}
body.cbavatarflashoreap:not(.site-willbot) #avatarwrapper {
    background: url(https://www.fourmilab.ch/fourmilog/archives/2009/06/26/midnight.jpg) 20% 0 no-repeat!important;
    background-size: 3000px auto!important;
}
#wrapper,
#conversationcontainer form img#actionsicon {
    background-color: #2a2b59;
}
#menu {
    background-color: #232449;
    box-shadow: 0 0 2px 0 rgba(0, 0, 0, 0.14), 0 2px 2px 0 rgba(0, 0, 0, 0.12), 0 1px 3px 0 rgba(0, 0, 0, 0.2);
}
#conversationcontainer #line1 {
    color: #fff;
}
#conversationcontainer form input.stimulus {
    color: #fff;
    background: #3d3d79;
    border: 1px solid rgba(255, 255, 255, 0.12);
}
#conversationcontainer img#refreshicon {
    filter: brightness(250%);
}
#menu > li:hover {
    background: #202043;
}
#menu > li#menu-index > ul > li > a {
    background-color: #353568;
    border-color: #3f3f79;
    border-top: 1px!important;
}
}
