css:
@-moz-document url-prefix("https://musi.sh/") {
/* Adapted from Austin Barrett's Musish OLED Black Style from https://userstyles.org/styles/172037/musish-oled-black */

/* main content */
 body {
    background: #000;
}
body, #app-root {
    background: #000 !important;
    color: #bbb !important;
    scrollbar-color: dark !important;
}
body, #app-root #main-content ._8e61 {
    scrollbar-color: dark !important;
}
._1d4f {
    background-color: black !important;
    color: #bbb !important;
}
/* play and shuffle buttons */
 ._8e04, .ac5c {
    background-color: #222 !important;
    border-color: #222 !important;
    color: #aaa !important;
}
._8e04:hover, .ac5c:hover {
    background-color: #222 !important;
    border-color: #222 !important;
    color: #ccc !important;
    box-shadow: 2px 3px 3px #111;
}
._8e04:active, .ac5c:active {
    background-color: #222 !important;
    border-color: #222 !important;
    color: #ccc !important;
}
/* description text */
 ._2949 {
    color: #777 !important;
}
/* song title */
 .a83a {
    color: #bbb !important;
}
/* song album */
 .b7a3 > span:not(.a83a) {
    color: #777 !important;
}
/* album art */
 .a231, .e2f4, .aa5e, ._800c, ._6fd9, ._7fe5 {
    filter: brightness(80%) !important;
}
/* genre box */
 ._1dfe {
    overflow: hidden;
}
._6110 {
    filter: blur(5px) !important;
}
.a101:hover ._6110 {
    opacity: 1.0 !important;
    filter: blur(4px) !important;
}
.f089 {
    background-color: rgba(0, 0, 0, 0.75) !important;
    color: rgba(255, 255, 255, 0.38) !important;
}
.f089:hover {
    color: rgba(255, 255, 255, 0.7) !important;
    filter: none;
}
/* radio wip screen */
 ._9443, ._12d2 {
    color: #d51831 !important;
}
/* artist actions button */
 ._9c93 > .d2b9 {
    background-color: #000 !important;
    border-radius: 3px !important;
}
/* Song Playing Icon */
 ._646c {
    background-color: #222 !important;
}
._9c93 > .d2b9:hover {
    background-color: #050505 !important;
}
/* right click menu */
 .react-contextmenu {
    background-color: #000 !important;
    box-shadow: 1px 1px 5px #000 !important;
}
.react-contextmenu .react-contextmenu-item:hover {
    color: #ccc !important;
    background-color: #222 !important;
}
.react-contextmenu ._9cca > h1 {
    color: #bbb !important;
}
.react-contextmenu ._9cca > h2, .react-contextmenu ._9cca > h3, .react-contextmenu .react-contextmenu-item {
    color: #aaa !important;
}
.react-contextmenu .react-contextmenu-item--divider:hover {
    background-color: #222 !important;
}
.react-contextmenu .react-contextmenu-item--divider {
    border-color: #1f1f1f !important;
    filter: blur(1px) !important;
}
/* Playlist Right Click */
 ._50e6 ._7a15 h2, ._50e6 ._7a15 h3 {
    color: #888 !important;
    font-size: 14px;
}
/* Album Right Click*/
 ._5341 ._3f83 h2, ._5341 ._3f83 h3 {
    color: #888 !important;
    font-size: 14px;
}
/* Album & Playlist Art Preload Color */
 ._50e6 .f7c1 .a230, ._5341 ._1901 ._8e53, ._665d ._2ad7 ._6fd9, ._086c ._76a3 .f003 ._2c7e, ._800c ._0331, .aa5e .a884 {
    background: #222 !important;
}
/* Artist tabs */
 ._5894.f714 {
    background-color: #222 !important;
}
/* Artist Banner Gradient */
._7037 ._8474 {
	background: linear-gradient(180deg,hsla(0,0%,12%,.75) 0,hsla(0,0%,0%,.75) 75%,#010101) !important
}
.d788 > ul > div > div > div {
    background-color: black !important;
}
.c9ae {
    background-color: #222 !important;
    color: #bbb !important;
}
._5894.f714 .e49a {
    background-color: black !important;
    color: #777 !important;
}
/* song hover */
 ._3bf9:hover .d1f2::after {
    background-color: #222 !important;
    box-shadow: 1px 1px 10px #111 !important;
}
/* remove line at bottom of songs in context menus (it was annoying) */
 ._3bf9 .d1f2:before {
    background: hsla(0, 0%, 0%, 0)
}
/* modal */
 ._92f0 {
    background-color: black !important;
    box-shadow: 1px 1px 10px #111 !important;
}
._1a01 {
    background-color: rgba(0, 0, 0, 0.6) !important;
}
._8e61 {
}
._8e61:only-child {
    filter: none !important;
}
/* header */
 ._3383 {
    background-color: black !important;
}
/* logo */
 .dcfd > a > span, .dcfd > i, .bf8b {
    color: #777 !important;
}
.dcfd > a:hover > span, .dcfd > i:hover, .bf8b > span:hover {
    color: #aaa !important;
}
/* search bar */
 ._8a29 > form > input, ._8a29 > form > button {
    background-color: #222 !important;
    border-color: #222 !important;
}
._8a29 > form > input, ._8a29 > form > button:hover {
    color: #ccc !important;
}
._8a29 > form > input::placeholder, ._8a29 > form > button {
    color: #aaa !important;
}
/* search popup */
 .edf0 ._4288 {
    margin-top: -4px !important;
    margin-right: 0 !important;
    background-color: #000 !important;
    box-shadow: 0 0px 0px 0px #222 !important;
}
/* initial search popup */
 .f82d ._8a29 ._3afe {
    background-color: #111 !important;
    box-shadow: 0 0px 0px 0px #222 !important;
}
/* search popup section titles */
 ._56e4 {
    background-color: #111 !important;
    color: #bbb !important;
}
/* search artists */
._7cab {
    background: #111;
}
/* searh hover */
.d5cd:hover {
	background: #333 !important;
    color: #fff;
}
.de20:hover {
	color: #fff;
}
.f82d ._8a29 ._3afe .edf0 .d5cd:hover {
	color: fff;
}
/* settings */
 .cdb4>.d16b {
    background: #000 !important;
    box-shadow: 0 0px 0px 0px rgba(0, 0, 0, 0);
    color: #bbb;
}
.cdb4>.d16b ._72fe h5, .cdb4>.d16b ._72fe ._4f55 {
    color: #bbb;
}
/* change branding */
.c2bb ._69de {
	color: #d51831 !important;
}
/* sidebar */
 ._4ed7, ._72ce {
    background-color: black !important;
    color: #bbb !important;
    border: none !important;
}
._3d7f ul {
    background-color: black !important;
    color: #bbb !important;
    border: none !important;
}
/* section title */
 ._3d7f h3 {
    color: #d51831 !important;
    background-color: black !important;
}
/* global section scrollers and thumbs */
 .a92b::-webkit-scrollbar, ._657c::-webkit-scrollbar, ._5496::-webkit-scrollbar {
    width: 3px;
    height: 3px;
    border-radius: 1px;
}
.a92b::-webkit-scrollbar-track, ._657c::-webkit-scrollbar-track, ._5496::-webkit-scrollbar-track {
    background: #000 !important;
    border-radius: 1px;
}
.a92b::-webkit-scrollbar-thumb, ._657c::-webkit-scrollbar-thumb, ._5496::-webkit-scrollbar-thumb {
    background: #222 !important;
    border-radius: 1px;
    cursor: pointer;
}
.a92b::-webkit-scrollbar-thumb:hover, ._657c::-webkit-scrollbar-thumb:hover, ._5496::-webkit-scrollbar-thumb:hover {
    background: #333 !important;
}
.a92b:hover::-webkit-scrollbar-thumb:hover, ._657c::-webkit-scrollbar-thumb:hover, ._5496::-webkit-scrollbar-thumb:hover {
    background: #222 !important;
}
/* browse selector border */
 ._9ec9, ._9ec9 .e3c6 {
    border-bottom: 2px solid #333 !important
}
._9ec9 .e3c6._29ea {
    border-bottom: 2px solid #fe2851 !important;
}
/* inactive tab */
 li a:not(._40a8) {
    color: #777 !important;
}
li a:not(._40a8):hover {
    color: #aaa !important;
}
/* active tab */
 li a._40a8 {
    border-color: #222 !important;
    background-color: #222 !important;
    color: #bbb !important;
}
/* remove weird focus behavior */
 .ddf5 ._1083 ._70a2 ul li a._86ea {
    border: 1px solid transparent !important;
}
._3d7f {
    border: 1px solid transparent !important;
    background-color: black !important;
}
/* footer */
 ._46bd {
    margin-bottom: 24px !important;
}
/* player controls */
 ._7312 {
    background-color: black !important;
    color: #bbb !important;
    margin-top: 0;
}
/* album art */
 ._8750 {
    filter: brightness(80%) !important;
}
/* progress bar */
 ._0fd3 {
    filter: brightness(80%) !important;
}
/* control icons */
 ._4af2 .fas {
    color: #bbb !important;
}
/* volume slider */
 ._2155>._95ef {
    background-color: black;
    box-shadow: 0 3px 10px -2px rgba(255, 255, 255, .25)
}
/* draggables - main / top bar */
 .c046, .e7c0 {
    background: black;
    color: #bbb;
    border-bottom: #000;
    box-shadow: 0 0 0 0 hsla(0, 0%, 0%, .0);
    border: 1px #fff;
}
/* artist / album text */
 ._5c25 {
    color: #777 !important;
}
/*something about a background on  a song */
 ._4ed7 {
    background: black !important;
}
/* something about a song title */
 ._45e1 {
    color: #bbb;
}
/* queue now playing */
 .f63e {
    background-color: #222 !important;
    color: #fff;
}
/* header queue and lyrics */
 ._45e1, .handle {
    background: #000 !important;
}
/* lyrics */
 .rg_embed_body * {
    background-color: #000 !important;
    color: #fff !important;
    pointer-events: none !important;
    cursor: default !important;
}
}
