css:
@-moz-document regexp(".*thatnovelcorner.com/"), regexp(".*thatnovelcorner.com/page/.+") {
/* Entry */
 .hentry, .post {
    background: #fff;
    border-radius: 12px;
}
.post-header .post-title a:hover, .post-header .post-title a:focus {
    color: #e0136d;
}
.posts .post-meta a:hover, .posts .post-meta a:focus {
    color: #e0136d;
}
.posts-navigation a:hover, .posts-navigation a:focus {
    background: #e0136d;
    color: #fff;
}
}

@-moz-document regexp(".*thatnovelcorner.com/"), regexp(".*thatnovelcorner.com/page/.+") {
/* Only For Entries on Start */
 .featured-media a img, .featured-media img {
    filter: grayscale(100%);
}
.featured-media a:hover img, .featured-media a:focus img {
    filter: grayscale(0%);
}
}

@-moz-document regexp(".*thatnovelcorner.com/.+") {
/* Tag Color */
 .single .post-meta p a:hover, .single .post-meta p a:focus {
    color: #e0136d;
}
}

@-moz-document regexp("https://thatnovelcorner.com/.*") {
/* Site Header */
 .site-title a:hover, .site-title a:focus {
    color: #e0136d;
}
.site-description {
    color: white;
    font-size: 1.1em;
    font-weight: 400;
    line-height: 110%;
    margin: 20px 0 0 0;
    text-align: center;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .25);
}
body {
    background: #2b2727;
}
.header .cover {
    background: rgb(238, 174, 202);
    background: radial-gradient(circle, rgba(238, 174, 202, 1) 0%, rgba(148, 187, 233, 1) 100%);
}
body a {
    color: #e0136d;
    font-weight: bold;
    text-decoration: none;
}
body a:hover, body a:focus {
    color: #a022e0;
    font-weight: bold;
    text-decoration: none;
}
}
