css:
@-moz-document domain(szukajka.tv) {


body {
    color: #c6c6c6;
    background-color: #151515;
}
body>footer {
    background-color: #212121;
    border-top: 1px solid #d7d7d7;
}
footer .row a {
    color: #c6c6c6;
}

.list a.square.active .fa,
.list a.square:hover .fa,
.search h1 {
    color: #008bff;
    background-color: #151515
}
.search .form {
    background-color: #fff0;
}
.search .list-view .element .link {
    color: #c6c6c6;
    background-color: #151515;
}
.search .box input {
    padding: 0 85px 0 18px;
    height: 83px;
    width: 100%;
    font-size: 28px;
    border: none;
    border-radius: 2px;
    background-clip: padding-box;
}
body {
    color: #c6c6c6;
    background-color: #151515;
}
select {
    color: #c6c6c6;
    background-color: #151515;
}
button,
input,
select,
textarea {
    border: 1px solid #151515;
    border-radius: 2px;
    background-clip: padding-box;
}
.search .list-view .element:nth-child(odd) .link {
    background-color: #151515;
    border: 1px solid #c6c6c6;
}
button,
input,
select,
textarea {
    border: 1px solid #c6c6c6;
    border-radius: 2px;
    background-clip: padding-box;
}
input {
    background-color: #151515;
}
.search .box {
    background: #151515;
    border: 1px solid #c6c6c6;
}
.search .box input {
    color: #c6c6c6;
}
body > header {
    background-color: #151515;
    border-bottom: 1px solid #c6c6c6;
}
.navigation a {
    color: #c6c6c6;
}
.search .filters input,
.search .filters select {
    border-bottom: 1px solid #c6c6c6;
}
.search .filters {
    overflow: hidden;
    background: #151515;
}
.search .grid-view .element .link {
    color: #c6c6c6;
    background-color: #151515;
    border: 1px solid #c6c6c6;
}
body {
    font-size: 16px;
    color: #c6c6c6;
    background-color: #151515;
}
.search .grid-view .element span {
    color: #c6c6c6;
}
.search .grid-view .element span .fa {
    color: #c6c6c6;
}
body {
    font-size: 16px;
    color: #c6c6c6;
    background-color: #151515;
}
.file .left {
    background: #151515;
}
.file .keywords a {
    color: #c6c6c6;
}
.file h2 {
    color: #c6c6c6;
}
.file .title {
    color: #c6c6c6;
}
.list .square {
    color: #c6c6c6;
    background: #151515;
}
.file .comments, .file .reports {
    background: #151515;
}
textarea {
    color: #c6c6c6;
    background-color: #151515;
}
.popup .overlay {
    background-color: #151515;
}
.popup .thumb {
    background-color: #151515;
    border: 1px solid #c6c6c6;
}
.popup {
    background-color: rgba(15,15,15,.8);
}
.search .list-view .element .link:visited {
    color: #8c8c8c;
}
.mini-search input, .most_popular .element span, .search .grid-view .element span {
    color: #c6c6c6;
}
.fix {
    background: #151515 !important;
}
.most_popular .element .link, .search .grid-view .element .link, option, #massVersion {
    background: #202020 !important;
    color: #fff;
    border-color: #0000;
}
.brand:hover, li:nth-of-type(1):hover, [href="http://szukajka.tv/dodaj-linki"]:hover, [href="http://szukajka.tv/uzytkownik"]:hover, [href="http://szukajka.tv/kontakt"]:hover, .current, .box > button:hover {
    background: #202020 !important;
    color: #fff !important;
}
.input input, textarea {
    color: #fff;
}
}
