css:
@-moz-document domain("clipwatching.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 0px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 5px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 0px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #0459ab;
        }

#container, body, .upload_slot, .tabberlive .tabbertab {
    background-color: #151515;
}
h3:nth-of-type(1) > strong > [href^="https://aparat.cam/"] > span {
	background: #151515 !important;
}
h4 {
    color: #c6c6c6;
}
.tabs li {
    color: #b5b5b5;
    border: 1px solid #c6c6c6;
}
body {
    color: #c6c6c6;
    background-color: #151515;
}
.popup-text {
    color: #c6c6c6;
}
.popup-title {
    color: #8a8a8a;
}
table tr td {
    background: #151515;
    border: #c6c6c6 1px solid;
}
input[type="text"], input[type="password"], textarea {
    border: 1px solid #c6c6c6;
    background-color: #151515;
}
table tr:hover td {
    background: #181818;
}
table, table tr td {
	border-color: #222;
}
input[type="text"], input[type="password"], textarea {
	border-color: #222;
}
td:nth-of-type(1) > div {
	background: #222 !important;
}
.lightgray {
	background: #181818 !important;
}
.dropdown-menu>li>a:focus, .dropdown-menu>li>a:hover {
	background: #222;
	color: #eee;
}
.dropdown-menu>li>a {
	color: #eee;
}
.navbar-default .navbar-nav>.active>a, .navbar-default .navbar-nav>.open>a, .plan div div:hover, .navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:focus, .navbar-default .navbar-nav>.open>a:hover {
	background: #222;
}
.plan {
    border: 1px solid #222;
    box-shadow: 0 0 5px #111;
}
.make-money h1 {
	background: #222;
}
h1 {
	color: #eee;
}
.make-money, .ppd h1, .pps h1, .mix h1, .rules h1 {
	color: #eee;
}
.ppd > span:nth-of-type(3) {
	color: #005eff !important;
}
table th, .tbl1 tr td, .tbl1 tr th, .folders table th {
	border-color: #222;
}
table.accountable tr td {
	background: #202020;
}
select:not([size]), select {
    filter: invert(100);
	color: #111;
	border-color: #fff;
}
select option {
	background: #151515 !important;
	color: #eee;
}
.tbl1 tr:hover td {
	background: #252525;
}
.tbl1 tr td {
	background: #181818;
}
.tbl1 tr.hi td, .tbl1 tr:nth-child(odd) td, .folders table th, .files table th {
	background: #222;
}
.files table th {
	border-color: #252525;
}
input[type="submit"] {
	background: #151515;
	border-color: #222;
	color: #eee;
}
.tbl1 tr.hdr2 td, .tbl1 th, .hdr2 {
	background: #222;
}
input[type="button"]:disabled {
	color: #444;
}
.upload_menu a {
	border-color: #222;
}
.upload_menu a:hover {
	background: #222;
}
.upload_menu a.active {
	background: #222;
	border-color: #222;
	color: #eee;
}
.upload_slot, .upload_slot input:not([type=checkbox]):not([type=radio]), .upload_slot textarea, .tabberlive .tabbertab, .result_slot input, .result_slot textarea, ul.tabs, .box, .tabs li, .tabs li.current, .tabs li:first-child {
	border-color: #222;
}
.btn2, .btndiv input[type=button] {
	background: #222;
	border-color: #252525;
}
.result_slot input, .result_slot textarea {
	color: #ccc;
}
.btndiv {
	background: #222;
	border-color: #252525;
}
ul.tabbernav li a {
	background: #181818;
}
ul.tabbernav li.tabberactive a, ul.tabbernav li.tabberactive a:hover {
	background: #151515;
	color: #004aff;
}
.tabs li.current {
	color: #eee;
}
.tabs li:hover {
	background: #222;
}
}
