css:
@-moz-document regexp("https?://.*\\.atlassian\\.net/issues/.*") {
/* List issues screen */

#page-body {
    background-color: #333;
}
#content .navigator-body {
    background-color: #333;
}
#content .navigator-body table {
    background-color: #333;
}
#content .navigator-body table tr {
    background-color: #333;
}
#jira-frontend * {
    background-color: #333;
}
.dQwSVN {
    background-color: #333;
}
.dQwSVN * {
    background-color: #333;
}
html {
    background-color: #333;
}

[data-test-id="ContextualNavigation"] .css-6vu1zn{
    background-color: #004278;
}
[data-test-id="ContextualNavigation"] .css-6vu1zn [data-test-id="NavigationItem"] {
    background-color: #004278;
}

[data-test-id="ContextualNavigation"] .css-6vu1zn [data-test-id="NavigationItem"]:hover {
    background-color: #03579c;
}

.ajs-layer.box-shadow.active * {
    background-color: #666 !important;
    color: #fff !important;
}
.ajs-layer.box-shadow.active ::-webkit-input-placeholder {
    /* Chrome/Opera/Safari */
    color: #DDD !important;
}
#content span, #content h1, #content h2, #content h3, #content h4, #content h5 {
    color: #fff !important;
}
#content a, #content div, #content td, #content time {
    color: #ddd !important;
}
#content button {
    color: #ddd !important;
    background-color: #444;
}
#content td.labels li a span {
    color: #333 !important;
}
#content .jira-issue-status-lozenge.jira-issue-status-lozenge-blue-gray {
    background-color: #ebecf0 !important;
    border-color: #ebecf0 !important;
    color: #42526e !important;
}
#content .jira-issue-status-lozenge.jira-issue-status-lozenge-yellow {
    background-color: #deebff !important;
    border-color: #deebff !important;
    color: #0049b0 !important;
}
#content .jira-issue-status-lozenge.jira-issue-status-lozenge-green {
    background-color: #e3fcef !important;
    border-color: #e3fcef !important;
    color: #064 !important;
}
#content .navigator-body table tr.focused {
    background-color: #555;
}
#content .navigator-body table tr th:hover, #content .navigator-body table tr th.active {
    background-color: #444 !important;
}
#jira-frontend img, #jira-frontend .image {
    color: #000 !important;
}
}
