@-moz-document url-prefix("https://brick-hill.trade/") {
/* Created by Thx64#3883 (Leaf) in a few hours. Started 8/5/2019 2:50 AM, ended around 3:07 AM on the same day */


/* Sidebar */

.sidebar[data-color=blue]:after,
.off-canvas-sidebar[data-color=blue]:after {
    background: #1e1e1e;
}

.sidebar .nav li.active>a,
.off-canvas-sidebar .nav li.active>a {
    background-color: #1d1d1d;
}

.sidebar .nav li:hover:not(.active)>a,
.sidebar .nav li:focus:not(.active)>a,
.off-canvas-sidebar .nav li:hover:not(.active)>a,
.off-canvas-sidebar .nav li:focus:not(.active)>a {
    background-color: rgba(60, 60, 60, 0.1);
}


/* Panel (Head Basically */

.panel-header {
    background: #1b1b1b;
}


/* Side text */

.text-muted {
    color: #d3d3d3!important;
}

a {
    color: white;
    font-weight: bold;
}

a:hover,
a:focus {
    color: white;
}


/* Part of Panel Header (Search buttons) */

.navbar .form-group.no-border .form-control,
.navbar .input-group.no-border .form-control {
    background-color: #222222;
}


/* User controls hover */

.navbar .navbar-nav .nav-item.active .nav-link:not(.btn),
.navbar .navbar-nav .nav-item .nav-link:not(.btn):focus,
.navbar .navbar-nav .nav-item .nav-link:not(.btn):hover,
.navbar .navbar-nav .nav-item .nav-link:not(.btn):active {
    background-color: rgba(37, 37, 37, 0.92);
    border-radius: 1.3rem;
    color: #fff;
}


/* Background */

.main-panel>.content {
    background: #1c1c1c;
}


/* Card w/ infow */

.card {
    background: #1a1a1a;
    color: white;
    box-shadow: unset!important;
}


/* User dropdown */

.dropdown.show .dropdown-menu:not(.inner),
.dropdown-menu.bootstrap-datetimepicker-widget.bottom.open,
.navbar .dropdown.show .dropdown-menu {
    background: #202020;
    color: white;
}


/* User dropdown content hover */

.dropdown-menu .dropdown-item:hover,
.dropdown-menu .dropdown-item:focus,
.bootstrap-select .dropdown-menu.inner li a:hover,
.bootstrap-select .dropdown-menu.inner li a:focus {
    background-color: rgb(35, 35, 35);
}


/* User dropdown ::before */

.dropdown-menu:before {
    border-bottom: .4em solid #202020;
}


/* Dropdown content control */

.navbar a.dropdown-item {
    color: white;
}


/* Icons section */


/* Money money money */

.now-ui-icons {
    color: green;
}


/* Exit exit exit */

i.now-ui-icons.ui-1_simple-remove {
    color: #890000!important;
}

i.now-ui-icons.media-2_sound-wave {
    color: yellow;
}

i.now-ui-icons.ui-1_simple-add {
    color: forestgreen;
}

i.now-ui-icons.arrows-1_refresh-69 {
    color: blue;
}

i.now-ui-icons.ui-1_bell-53 {
    color: rebeccapurple;
}

i.now-ui-icons.design_bullet-list-67 {
    color: #007979;
}

i.now-ui-icons.shopping_cart-simple {
    color: brown;
}

i.now-ui-icons.design_app {
    color: chartreuse;
}

i.now-ui-icons.users_single-02 {
    color: white!important;
}

i.now-ui-icons.ui-1_send {
    color: #00a4a4;
}


/* hr, etc (::after) */

.logo:after {
    border: 1px solid #1c1c1c;
}

.card-chart .table td,
.table td,
.table th {
    border-color: #1c1c1c;
}


/* Divider user dropdown */

.dropdown-menu .dropdown-divider {
    border-color: #202020;
}


/* Sidebar background-shadow */

.sidebar {
    box-shadow: 0 2px 22px 0 rgba(0, 0, 0, .2), 0 2px 30px 0 rgba(0, 0, 0, 0.04);
}


/* Actual form drop downs */

.form-control {
    background-color: #1d1d1d5e;
    border: 1px solid #171717;
    color: #cecece;
}

.form-control:focus {
    border: 1px solid #171717;
    color: #cecece;
    background: #1b1b1b;
}


/* Plus button on item/user page */

.btn-outline-default:hover,
.btn-outline-default:focus,
.btn-outline-default:not(:disabled):not(.disabled):active,
.btn-outline-default:not(:disabled):not(.disabled).active,
.btn-outline-default:not(:disabled):not(.disabled):active:focus,
.btn-outline-default:not(:disabled):not(.disabled).active:focus,
.btn-outline-default:active:hover,
.btn-outline-default.active:hover,
.show>.btn-outline-default.dropdown-toggle,
.show>.btn-outline-default.dropdown-toggle:focus,
.show>.btn-outline-default.dropdown-toggle:hover,
button.btn.btn-outline-default.dropdown-toggle.btn-simple.btn-icon.no-caret {
    background-color: transparent;
    color: #979797;
    border-color: #97979700;
}


/* Alert popup */

.alert.alert-warning 
{
    background-color: #b62626;
}

/* Extra dropdown */
.dropdown-menu.show 
{
    background: #202020;
    color: white;
}

/* Footer */
footer.footer 
{
    background: #202020;
    color: #cfcfcf;
}
}
