css:
@-moz-document domain(streamango.tv) {

body {
    background: #151515;
    font-size: 14px;
    font-weight: 400;
    color: #c6c6c6;
    margin: 0;
    padding: 0;
    -webkit-text-size-adjust: none;
    position: relative;
}
#mv-info .mvi-content {
    background: #151515;
    padding: 20px;
    font-size: 12px;
    position: relative;
    width: 100%;
    margin-top: -5px;
}
#mv-info .mvi-content .mvic-desc .desc {
    width: 100%;
    margin-bottom: 10px;
    font-size: 12px;
    font-style: italic;
    color: #c6c6c6;
}
#mv-keywords {
    margin-bottom: 20px;
    padding: 20px;
    display: block;
    clear: both;
    background: #151515;
}
body {
    margin: 0;
    line-height: 20px;
    background-color: #151515;
}
body {
    background: 0, 0;
    color: #c6c6c6;
    background: #151515;
    font-family: "Helvetica Neue",arial,sans-serif;
    font-size: 15px;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}
#commentfb {
    padding: 10px 20px;
    margin: 20px 0;
    background: #151515;
    display: block;
}
#reactions .reaction-items .reaction-item .reaction-item__button, #reactions-promotion .reaction-items .reaction-item .reaction-item__button {
    border: 1px solid #8d32c5;
    padding: 5px 10px;
    margin: 5px 6px 10px;
    border-radius: 4px;
    white-space: nowrap;
}
#reactions .reaction-items .reaction-item .reaction-item__text, #reactions-promotion .reaction-items .reaction-item .reaction-item__text {
    margin-left: 8px;
    color: #c6c6c6;
    border: 1px solid #8d32c5;
}
.main-detail {
    width: 100%;
    background: #151515;
}
.breadcrumb>.active {
    color: #c6c6c6;
}
#bread .breadcrumb a {
    color: #a5a5a5;
}
#ad_buts button {
    background-color: #8d2ec7;
    border: none;
    border-radius: 5px;
    padding: 10px;
    border: 3px solid #c6c6c6;
    font-size: 20px;
    font-weight: bold;
    -webkit-transition: all .1s ease 0s;
    -khtml-transition: all .1s ease 0s;
    -moz-transition: all .1s ease 0s;
    -ms-transition: all .1s ease 0s;
    -o-transition: all .1s ease 0s;
    transition: all .1s ease 0s;
}
.placeholder {
    color: #c6c6c6;
    line-height: 30px;
    font-size: 15px;
    position: absolute;
    top: 7px;
    left: 10px;
    right: 9px;
    white-space: nowrap;
    text-overflow: ellipsis;
    overflow: hidden;
    cursor: text;
}
body.dark {
    color: #8d32c5;
}
.dark .tab-conversation.active>a {
    color: #c6c6c6!important;
}
.dark .input--text {
    background: #313131;
    box-shadow: none;
    border-color: #8d32c5
}
.container {
    width: 100%;
    background: #151515
}
#main {
    min-height: calc(100vh - 371px);
    background: #151515;
}
.mobile-menu, .mobile-search {
    display: inline-block;
    cursor: pointer;
    height: 34px;
    width: 34px;
    text-align: center;
    background: #8d32c5;
    border-radius: 3px;
    position: absolute;
    top: 17px;
    left: 15px;
}
.infopage .content {
    color: #c6c6c6;
    background: #313131;
    padding: 15px 20px;
}
.infopage .infopage-head {
    display: block;
    padding: 10px 20px;
    border-bottom: 1px solid #8d32c5;
    font-size: 20px;
    background: #222
}
.form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #c6c6c6;
    background-color: #313131;
    background-image: none;
    border: 1px solid #8d32c5;
    border-radius: 2px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
    -o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}
#search {
    float: none;
    position: absolute;
    bottom: -64px;
    left: 0;
    right: 0;
    width: auto;
    padding: 15px;
    background: #313131;
    margin: 15px;
    display: none;
}
.news-list .news-list-item .info h2 {
    margin: 0 0 8px;
    font-family: 'Montserrat';
    color: #c6c6c6;
    background: #313131;
    font-size: 22px;
    line-height: 24px;
}
.news-list-item .info h2 a {
    word-wrap: break-word;
    color: #c6c6c6
}
.news-list-item:hover {
    background: #8d32c5c4;
}
.news-list-item {
    padding: 10px 15px;
    border-bottom: 1px solid #8d32c5;
}
.news-list .news-list-item {
    background: #313131;
    padding: 20px;
}
.box {
    margin-bottom: 20px;
    background: #313131;
    display: block;
    overflow: hidden;
}
footer {
    background: #151515;
    padding: 20px 0 40px;
    color: #8d32c5;
    margin-top: 30px;
}
.footer-social a {
    color: #e83939;
}
.home-main #hm-search .form-control {
    height: 56px;
    background: #313131;
    font-size: 20px;
    padding-left: 20px;
    padding-right: 90px;
    box-shadow: none;
    color: #c6c6c6;
    border: 1px solid rgba(0,0,0,.2);
    border-radius: 5px;
}
.movies-list-wrap .ml-title span {
    background: none;
    color: #c6c6c6;
}
.pagination>li>a, .pagination>li>span {
    position: relative;
    float: left;
    padding: 6px 12px;
    margin-left: -1px;
    line-height: 1.42857143;
    color: #c6c6c6;
    text-decoration: none;
    background-color: #313131;
    border: 1px solid #8d32c5;
}
.pagination>.active>a, .pagination>.active>a:focus, .pagination>.active>a:hover, .pagination>.active>span, .pagination>.active>span:focus, .pagination>.active>span:hover {
    background: #9e39e8;
    border: 1px solid #c6c6c6;
}
.qtip-bootstrap {
    font-size: 14px;
    line-height: 20px;
    color: #c6c6c6;
    padding: 1px;
    background-color: #313131;
    border: 1px solid #8d32c5;
    border: 1px solid rgba(0,0,0,.2);
    -webkit-border-radius: 6px;
    -moz-border-radius: 6px;
    border-radius: 6px;
    -webkit-box-shadow: 0 5px 10px #313131;
    -moz-box-shadow: 0 5px 10px #313131;
    box-shadow: 0 5px 10px #313131;
    -webkit-background-clip: padding-box;
    -moz-background-clip: padding;
    background-clip: padding-box;
}
.qtip .qtip-content {
    font-size: 12px;
    padding: 12px;
    overflow: visible;
}
.qtip-bootstrap {
    font-size: 14px;
    line-height: 20px;
    color: #c6c6c6;
    padding: 1px;
    background-color: #313131;
    border: 1px solid #8d32c5;
    border: 1px solid rgba(0,0,0,.2);
    -webkit-border-radius: 6px;
    -moz-border-radius: 6px;
    border-radius: 6px;
    -webkit-box-shadow: 0 5px 10px rgba(0,0,0,.2);
    -moz-box-shadow: 0 5px 10px rgba(0,0,0,.2);
    box-shadow: 0 5px 10px rgba(0,0,0,.2);
    -webkit-background-clip: padding-box;
    -moz-background-clip: padding;
    background-clip: padding-box;
}
.qtip .qtip-title {
    margin-right: 60px;
    background: #313131;
    color: #8d32c5;
    font-size: 16px;
    font-weight: 600;
    padding: 0 0 12px;
    line-height: normal;
}
.jtip-top {
    padding: 5px 12px;
    background: #151515;
    color: #c6c6c6;
    font-weight: 700;
    margin: 0 -13px 12px;
    font-size: 12px;
}
.jtip-top .jt-info.jt-imdb {
    padding: 1px 7px;
    background: #f7d62b;
    color: #fff;
    margin-right: 15px;
}
#ad_buts button {
    background-color: #8d2ec7;
    border: none;
    border-radius: 5px;
    padding: 10px;
    border: 3px solid #7124a0;
    font-size: 20px;
    font-weight: bold;
    -webkit-transition: all .1s ease 0s;
    -khtml-transition: all .1s ease 0s;
    -moz-transition: all .1s ease 0s;
    -ms-transition: all .1s ease 0s;
    -o-transition: all .1s ease 0s;
    transition: all .1s ease 0s;
}
.player_nav ul.idTabs li {
    padding: 20px;
    background: #151515;
    border-bottom: 1px solid #8d32c5;
}
  #menu ul.top-menu li a {
    height: 70px;
    line-height: 70px;
    margin: 0;
    padding: 0 5px;
    font-family: 'Oswald';
    font-size: 16px;
    color: #c6c6c6;
    text-transform: uppercase;
    display: inline-block;
}
.tvseason {
    padding: 20px;
    border-bottom: 1px solid #8d32c5;
    background: #151515;
}
.tvseason .les-content a {
    border: 1px solid #8d32c5;
    border-bottom: 3px solid #8d32c5;
    background: #151515;
    padding: 7px 12px 5px;
    min-width: 72px;
    font-size: 12px;
    color: #c6c6c6;
    border-radius: 3px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    display: inline-block;
    margin: 5px;
}
	}
