css:
@-moz-document domain(streamango.com) {


body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: #c6c6c6;
    background-color: #151515;
}
#title-area .page-title {
    font-family: montserrat,sans-serif;
    font-size: 20px;
    color: #8a76f5;
    background: #151515;
    font-weight: 700;
    text-transform: uppercase;
}
#title-area {
    text-align: left;
    padding-top: 10px;
    padding-bottom: 10px;
    position: relative;
    background-color: #151515;
    border: 1px solid #8a76f5;
    -webkit-box-shadow: 0 3px 8px 0 rgba(0,0,0,.23);
    -moz-box-shadow: 0 3px 8px 0 rgba(0,0,0,.23);
    box-shadow: 0 3px 8px 0 rgba(0,0,0,.23);
}
#header .main-menu-holder {
    padding-top: 45px;
    padding-bottom: 33px;
    background: #151515;
}
#header .logo-holder {
    padding-top: 40px;
    background: #151515;
    padding-bottom: 30px;
}
.container-fluid {
    padding-right: 15px;
    padding-left: 15px;
    margin-right: auto;
    margin-left: auto;
    background: #151515;
}
#header .main-menu li a, #header .main-menu li span {
    color: #c6c6c6;
    text-decoration: none;
    text-transform: uppercase;
    opacity: .7;
    font-size: 11px;
    letter-spacing: 2px;
    cursor: pointer;
}
.col-sm-12 {
    background: #151515;
    width: 100%;
}
#page-content p {
    font-size: 15px;
    color: #664bf1;
    font-family: open sans,sans-serif;
    font-weight: 400;
    margin-bottom: 30px;
}
#page-content {
    background-color: #151515;
}
#page-content .form-items-holder .form-text input {
    padding: 14px 18px;
    border-radius: 10px;
    text-align: left;
    background-color: #313131;
    border: 1px solid #664bf1;
    font-size: 16px;
    color: #664bf1;
}
.form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #c6c6c6;
    background-color: #151515;
    background-image: none;
    border: 1px solid #664bf1;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
    -o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}
#page-content .form-items-holder .form-text textarea {
    padding: 14px 18px;
    border-radius: 10px;
    text-align: left;
    background-color: #313131;
    border: 1px solid #664bf1;
    font-size: 16px;
    color: #664bf1;
}
element.style {
    width: 250px;
    height: 40px;
    background: #151515;
    border: 1px solid rgb(193, 193, 193);
    margin: 10px 25px;
    padding: 0px;
    resize: none;
    display: block;
}
#login-box .form-items-holder {
    position: relative;
    background-color: #313131;
    padding: 20px;
    border-radius: 3px;
    -webkit-box-shadow: 0 0 34px 0 rgba(0,0,0,.24);
    -moz-box-shadow: 0 0 34px 0 rgba(0,0,0,.24);
    box-shadow: 0 0 34px 0 rgba(0,0,0,.24);
}
#login-box .form-items-holder h4 {
    color: #664bf1;
    margin-top: 0;
    font-size: 16px;
}
#login-box .form-items-holder .form-text input {
    padding: 10px;
    border-radius: 3px;
    text-align: left;
    background-color: #535353;
    font-size: 15px;
    color: #664bf1;
}
#features .title-holder {
    padding-top: 30px;
    color: #c6c6c6;
    background: #151515;
    padding-bottom: 30px;
}
#features {
    padding-top: 50px;
    padding-bottom: 20px;
    text-align: center;
    background-color: #151515;
}
#monetize-features {
    padding-top: 40px;
    padding-bottom: 60px;
    text-align: center;
    background-color: #151515;
    position: relative;
}
	}
