css:
@-moz-document url-prefix("https://www.brick-hill.com") {
/* Made for Heartbreaker on the request of Googie  */

::selection 
{
    background-color: #8e00a8;
    color: #f400ff;
}

.darkest-gray-text 
{
color: #9602c4;
}

/* Nav Bar */

.nav-notif, .notif 
{
    background-color: #ba00c6;
}

nav div.secondary
{
    background-color: #55006a!important;
    color: #fff!important;
}

nav div.primary, nav .info
{
    background-color: #47005e;
    color: #fff;
}

nav div.secondary ul li a:hover 
{
    border-color: #eb00d9;
}

span.bucks-icon.img-white, span.bits-icon.img-white 
{
    filter: brightness(0) invert(1)!important;
}

.bits-icon, .bucks-icon
{
    filter: brightness(0) invert(1);
}

.bits-text
{
    color: #ff00ff;
}

.bucks-text
{
    color: rgb(255, 0, 127);
}

.card .item-card-details .price, .card .item-content .price 
{
    color: #8a00ff;
}

body 
{
    background-image: url(https://i.imgur.com/mtCxn2A.png)!important;
}

.card .top:not(.absolute).blue, .card .top:not(.absolute).orange, .card .top:not(.absolute).red, .card .top:not(.absolute).green, .card .top:not(.absolute)
{
    background-color: #4f0069!important;
    color: #fff!important;
    border-bottom: 1px solid #e71eff;
}

.agray-text
{
    color: #9700c9;
}

.favorite-text 
{
    color: #fc00e4;
}

.read-more-desc
{
    color: #ff00db;
}

.toggle-user-desc:not(.open):after 
{
    background-color: #181818;
}

.fa, .fas 
{
    color: white;
}

.trade-serial 
{
    color: #ff00ff;
}

.button.tab-button.transparent:hover, button.tab-button.transparent:hover 
{
    color: #ff00f3;
}

i.f-right.light-gray-text.far.fa-edit, i.far.fa-caret-square-down.dropdown-arrow 
{
    color: #6c008c;
}

.dropdown-content li, .dropdown-content li :hover, .dropdown-content.logout-dropdown li :hover 
{
    background-color: #1e1e1e;
    color: white;
}

.dropdown-content.logout-dropdown .dropdown-arrow
{
    border-bottom: 7px solid #161616;
}

.dropdown-content li *, .dropdown-content.logout-dropdown li * 
{
    background-color: #161616;
    color: white;
}

.red-text, .ablack-text 
{
    color: #8a00ff;
}

.button.red, button.red
{
    background-color: #670088;
}

.button.red:hover, button.red:hover 
{
    color: #e200ff;
    background-color: #8c00b9;
    border-color: #000000A1;
}

.button.orange, button.orange 
{
    background-color: #ab00b7;
    border-color: #1f1f1f;
}

.button.orange:hover, button.orange:hover
{
    background-color: #cf00dd;
    color: #9b00c3;
}

.darkest-gray-text {
    color: #9b00c3;
}

.black-text 
{
    color: #ff00f5;
}

.fieldset.green legend 
{
    color: #e600c8;
}

.fieldset.red legend 
{
    color: #e60096;
}

a.very-bold.dark-gray-text.block 
{
    color: #a40087;
}

.thread-label.blue 
{
    color: #a10098;
}

.pages .page.active, a.download h5
{
    color: #ff00c1!important;
}

.mem-cols .col,.membership-info
{
    border-color: #9990;
    background-color: #202020;
}

.mint-gradient
{
    background-color: #202020;
    background-image: none!important;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/forum/") {
.mb1
{
    color: #ba00cd!important;
}

.forum-bar .fa-angle-double-right 
{
    color: #ff09e6;
}

.blockquote.blue, blockquote.blue 
{
    border-color: #cc00ff;
}

blockquote.blue a {
    color: #f300da!important;
}

input, textarea
{
    background-color: #141414;
    border: 1px solid #141414;
    color: white;
}

}

@-moz-document url-prefix("https://www.brick-hill.com/play/"), url-prefix("https://www.brick-hill.com/sets/") {
.card .footer .playing 
{
    color: #e900d3;
}

.card .name 
{
    color: #ffffff;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/customize/") {
.avatar-card.crate img, .avatar-card.crate .img-holder 
{
    background-color: #171717;
}

.avatar-card.crate .img-holder 
{
    border-color: #131313;
}

.button, button
{
    background-color: #ff00eb;
    border-color: #18181800;
}

input#outfit_name {
    background-color: #191919;
    border-color: #181818;
    color: white;
}

.outfit-overlay
{
    background-color: hsla(288, 100%, 50%, 0.87);
}

.outfit-overlay li:hover 
{
    background-color: #f84ffa78;
}
}
