css:
@-moz-document url-prefix("https://www.brick-hill.com/") {
body 
{
    background-image: url(https://i.imgur.com/3DrJBdp.png)!important;
}

nav div.secondary 
{
    background-color: #006700!important;
    color: #0ed100!important;
}

nav div.primary, nav .info 
{
    background-color: #004d00!important;
}

nav div.secondary ul li a:hover 
{
    border-color: #ffffff!important;
}

.nav-notif, .notif 
{
    background-color: #323232!important;
}

.pages .page.active 
{
    color: #ffffff;
}

.card .top:not(.absolute).blue, .card .top:not(.absolute).orange, .card .top:not(.absolute).red, .card .top:not(.absolute).green
{
    background-color: #ffffff;
    border-color: #f8f8f8;
    color: #505050;
}

.card .content 
{
    height: inherit;
    overflow: auto;
    padding: 10px;
    background-color: #fafafa;
}

.card .thumbnail.dark 
{
    background-color: #f0f0f0;
}

.card .item-card-details, .card .item-content
{
    border-top: 1px solid #f0f0f0;
}

a.download h5
{
    margin: 0;
    color: white!important;
}

.button.orange, button.orange 
{
    color: #fff;
    background-color: #008604;
    border-color: #04ff00;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/forum/") {
.thread-label.blue 
{
    background-color: #d7d7d730;
}
.thread-label 

{
    font-size: .6em;
    color: #00d01d;
}

.forum-bar a {
    color: white;
}

.forum-bar .fa-angle-double-right 
{
    color: #00d445;
}
}
