css:
@-moz-document url-prefix("https://www.mobile01.com"), url-prefix("https://m.mobile01.com") {
main.l-main,
.content-side,
.c-burger,
.l-listTable__tbody,
.l-memberIntro,
.c-productStatus,
.c-dropdown--sort .c-dropdown__select ul {
    background-color: #222;
}
.l-separationBg {
    background: linear-gradient(to bottom, #202020 0, #282828 3%, #222 100%);
}
body {
    font-family: 'Noto Sans CJK TC';
    color: #a8a8a8;
    background-color: #222;
}
.gsc-webResult.gsc-result,
.gsc-results .gsc-imageResult,
.cse .gsc-control-cse,
.gsc-control-cse,
.c-authorCard {
    background-color: #222;
    border: 1px solid #606060;
}
.gs-webResult .gs-snippet,
.gs-imageResult .gs-snippet,
.gs-fileFormatType {
    color: #a8a8a8;
}

.l-selectedList li,
.l-listTable__tr,
.gsc-above-wrapper-area {
    border-bottom: 1px solid #444;
}
.l-header__group {
    border-right: 1px solid #000;
}
.l-headerTools__login {
    border-left: 1px solid #000;
}
.sf-menu a {
    color: #bbb;
    background-color: #111;
    border-left: 1px solid #333;
    border-top: 1px solid #333;
}
.sf-menu a:hover,
.l-pagination .is-active a {
    background-color: #294a32;
    border: 1px solid #30a651;
}
.l-header__nav:after {
    border-bottom: 1px solid #333;
}
.l-content__main {
    border-right: 1px solid #535353;
}
.l-content__side,
.l-footer {
    display: none;
}
.c-iconLink--dark {
    color: #a8a8a8;
}
.l-header__main {
    background-color: #111;
    border-top: 3px solid #444;
    border-bottom: 1px solid #333;
}

.o-hashtag--lightGray,
.c-filter,
.l-listTable__thead .l-listTable__td,
.gsc-results .gsc-cursor-box,
.gsc-results,
.gsc-cursor-box .gsc-cursor-page,
.gsc-webResult.gsc-result:hover,
.gsc-imageResult:hover,
.l-personalPanel,
.l-docking.is-docking,
.l-qnaEditBlock,.l-helpLink {
    color: #a8a8a8;
    background-color: #333;
}
.c-link,
.c-group__title {
    color: #a8a8a8;
}
.c-articleCard__desc,
.c-dropdown__select > li > span {
    color: #a8a8a8;
    background-color: #111;
}
.l-menu,
.c-textarea--wt,
.c-productStatus,
.c-dropdown__select ul li {
    color: #a8a8a8;
    background-color: #111;
}
.l-menu__block {
    border-bottom: 1px solid #000;
}
.c-btn--menu,
.c-dropdown--bar .is-valued + .c-dropdown__select > li > span,
.l-listTable--msg .l-listTable__tr:hover {
    color: #eee;
    background-color: #111;
}
.l-menu__tools {
    display: none;
}
html.is-menuOpened .l-header__main {
    background-color: #333;
    border-bottom-color: #000;
}
.c-menuLv2,
.l-searchBox,
.c-searchBox.is-active .c-searchBox__search,
.c-menuTab__control > li.is-active a {
    background-color: #111;
}
.c-menuLv3__title span,
.l-searchCard {
    border-bottom: 1px solid #636363;
}
.c-articleCard__thumbnail {
    background-color: #2f2f2f;
}
.c-textbox,
.o-hashtag.is-heightLight,
.o-hashtag,
.gsc-results .gsc-cursor-box .gsc-cursor-page {
    color: #a8a8a8;
    background-color: #000;
}
.o-hashtag--heightLight,
.o-hashtag.is-notice,
.c-btn--primary,
.group-img-thumbs .l-thumbGallery__item {
    background-color: #294a32;
}
.l-background,
.c-newsCard__main,
.c-filterBox,
.c-group__options,
.ad-side {
    background-color: #111;
}
.sf-menu > li > ul > li,
.sf-menu ul ul li,
.l-listTable__thead .l-listTable__tr,
.l-memberIntro__interact + .l-memberIntro__record,
.l-publish__content {
    border-top: 1px solid #535353;
}
a {
    color: #cbcbcb;
}
.l-articlePage {
    border-bottom: 1px solid #37523e;
}
.c-more:before,
.u-overflow:after {
    background-image: linear-gradient(to bottom, rgba(2, 2, 2, 0), #222);
}
}
