css:
@-moz-document domain("www.rpc-wiki.net") {
body {
    font-style: normal x-small verdana,arial,helvetica,sans-serif;
    background: white !important;
    color: #222 !important;
    overflow-x: hidden;
}

.hovertip {
    background: white !important;
    color: #666;
}

div#main-content {
    margin: 15px 350px 0 15px;
    padding: 0 30% 0 35px;
    border-bottom: 1px dotted gray;
}

@media only screen and (max-width: 1400px) {
    div#main-content {
        padding-right: 20%;                                        
    }
}

@media only screen and (max-width: 1200px) {
    div#main-content {
        padding-right: 10%;                                        
    }
}

div#page-content {
    background-color: #fafafa;
    border: 1px solid #369;
    border-radius: 7px;
    min-height: 40px;
    padding: 5px 10px;
    font-size: 14px;
    color: #222 !important;
}

div#page-title {
    color: #0000ff;
    font-size: medium;
    font-weight: normal;
    border: none;
    margin-bottom: 1px;
    cursor: pointer;
}

div#page-title:after {
    content: "(self.rpcwiki)";
    color: #888;
    font-size: x-small;
    white-space: nowrap;
}

#content-wrap {
    margin: 0;
    max-width: unset;
    background: url(http://rpcsandbox.wikidot.com/local--files/reddit-theme/premium-ad.png) right 10px top 50px no-repeat, url(http://rpcsandbox.wikidot.com/local--files/reddit-theme/reddit-votebar.png) 15px -2px no-repeat, url(http://rpcsandbox.wikidot.com/local--files/reddit-theme/reddit-community.png) right 140px top 220px no-repeat;
}

#page-options-container * {
    text-align: left;
}

#page-info, .page-watch-options {
    display: none;
}

#page-options-bottom {
    margin: 4px 0px 15px;
}

.page-options-bottom a, .page-options-bottom a:hover {
    color: #888;
    font-weight: bold;
    font-size: 10px;
    text-transform: lowercase;
}

.page-options-bottom a:hover {
    text-decoration: underline;
}

#search-top-box {
    width: 310px;
    height: 30px;
    border: 1px solid gray;
}

#search-top-box-form {
    height: 100%;
}

#search-top-box-input, #search-top-box-input:hover, #search-top-box-input:focus {
    width: 84%;
    height: 30px;
    padding: 0px 5px;
    font-size: 13px;
    background: white;
    box-shadow: none;
    color: black;
    border: none;
    font-family: Verdana, sans-serif;
    outline: none;
}

#search-top-box input.empty {
    text-transform: lowercase;
}

#search-top-box-form input[type=submit], #search-top-box-form input[type=submit]:hover, #search-top-box-form input[type=submit]:focus {
    width: 10%;
    height: 30px;
    color: transparent;
    background: white;
    box-shadow: none;
    border: none;
    background: url(http://rpcsandbox.wikidot.com/local--files/reddit-theme/reddit-search.png) center no-repeat;
    outline: none;
}

#page-info-break {
    clear: none;
}

div#container-wrap {
    background: url(http://rpcsandbox.wikidot.com/local--files/reddit-theme/reddit-head2.png) 0px 14px repeat-x !important;
}

#header {
    background: url(http://rpcsandbox.wikidot.com/local--files/reddit-theme/rpceddit.png) 0px 19px no-repeat;
    height: 64px;
    width: 100%;
    padding-bottom: 0;
    max-width: 100%;
}

#header h1 {
    margin: 0 !important;
    height: 40px !important;
    width: 150px !important;
    font-size: 0 !important;
}

#header h1 a {
    padding-top: 20px !important;
    margin-top: 19px !important;
}

#header h1 a::before {
    content: none;
}

#header h2 {
    display: none !important;
}

#top-bar {
    top: 25px;
    left: 0px;
    margin: 14px 170px;
}

#top-bar ul {
    float: left;
}

#top-bar ul li {
    font-weight: bold;
    margin: 0px 3px;
    text-transform: lowercase;
}

#top-bar ul li a {
    padding: 9px 7px;   
}

#top-bar ul li a, #account-options ul li a {
    background-color: #eff7ff;
    color: #369;
    border: 1px solid transparent;
    border-bottom: none;
    transition: none;
}

#top-bar ul li.sfhover a:hover, #top-bar ul li:hover a:hover, #account-options ul li.sfhover a:hover, #account-options ul li:hover a:hover {
    background-color: white;
    color: orangered;
    border: 1px solid #5f99cf;
    border-bottom: 1px solid white;
}

#login-status {
    top: 40px;
    right: 0;
    background-color: #eff7ff;
    padding: 3px 8px;
    border-top-left-radius: 7px;
    color: #369;
}

#login-status a {
    color: #369;
}

a#my-account {
    font-weight: bold;
}

#side-bar {
    float: right;
    margin-top: 290px;
    width: 300px;
}

#side-bar .heading {
    border-top: 1px solid gray;
    font-size: 13px;
    color: #222;
    margin: 1em 0 0.5em 0;
}

#side-bar .heading p {
    margin: 5px 0;
} 

#side-bar div.menu-item a {
    color: black;
    font-size: 12px;
}

#side-bar .menu-item>a[href="https://discord.gg/wkXBuxt"]::before {position: unset; margin-right: 4px}
#side-bar .menu-item>a[href="https://www.reddit.com/r/TheRPCAuthority/"]::before {position: unset; margin-right: 4px}
#side-bar .menu-item>a[href="https://steamcommunity.com/groups/ORPCGP"]::before {position: unset; margin-right: 4px}
#side-bar .menu-item>a[href="https://twitter.com/RPCAuthority"]::before {position: unset; margin-right: 4px}
#side-bar .menu-item>a[href="https://www.redbubble.com/people/RPCAuthority/portfolio?asc=u"]::before {position: unset; margin-right: 4px}

#side-bar .side-block {
    padding-right: 0;
    width: 300px;
}

#side-bar .side-block:first-of-type {
    border-top: 1px solid gray;
    padding-top: 10px;
}

#side-bar .side-block:last-of-type {
    border-bottom: 1px solid gray;
    padding-bottom: 8px;
}

#side-bar:after {
    content: "a community for 1 year";
    float: right;
    font-size: 80%;
    color: gray;
}

#edit-page-form {
    margin: 0;
}

#footer {
    position: absolute;
    width: 99%;
    top: 0;
    margin-top: 0;
    color: white;
    text-transform: uppercase;
    border-bottom: 1px solid gray;
    background: #f0f0f0 url(http://rpcsandbox.wikidot.com/local--files/reddit-theme/reddit-footer-bgr.png) no-repeat;
}

#footer a {
    color: white;
}

#footer .options {
    color: #222;
}

#footer .options a {
    color: black;
}

.content-panel, .content-panel.standalone {
    background: white !important;
    border: 1px solid #aaa;
    color: black !important;
}

.content-panel .content-toc {
    background: #fbfbfb;
    border: 1px solid #666;
}

.code {
    background: #f4f9ff;
    border: 1px dashed #676767;
}

.image-block .image-caption {
    background: #eee !important;
    color: black !important;
}

blockquote {
    color: #333; 
    background-color: white;
}

.rate-module-container {
    border: 1px solid #5f5f5f !important;
}

.rate-module-stars {
    background-color: #ffffff !important;
}

.rate-module-counter, .translationStyle {
    background-color: #eee !important;
}

.well {
    background-color: #eee!important;
    color: #000 !important;
    border: solid 1px #666!important;
}

.hcell {
    background-color: #ddd;   
}

.cell {
    background-color: #eee;   
}

.infobox, .infoboxSide {
    display: none !important;   
}

.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
    background-color: #eee;
    color: #000;
}

.yui-navset .yui-content {
    background-color: #ebebeb;
}

h1, h2, h3, h4, h5, h6 {
    color: black !important;   
}

.image-block {
    clear: unset !important;   
}

.forum-category-box table tr:nth-child(odd), .forum-group table tr:nth-child(odd) {
    background: #e6e6e6;
}

.forum-category-box table tr:nth-child(even), .forum-group table tr:nth-child(even) {
    background: #f7f7f7;
}

.forum-start-box {
    border: 1px solid gray;
}

.forum-group {
    margin: 0;
    width: 100% !important;
}

.forum-group table td.name .description {
    color: #313131 !important;
}

.pager .current {
    background-color: #ececec;
    color: #424242;
}

#history-form-1 .btn-sm, .well .btn-sm, .change-textarea-size a, .buttons .btn-small, .action-area .btn-default, .action-area .btn-primary, .col-lg-10 .form-control, .form-actions .btn-danger, .buttons .btn-default, .target a, .buttons .btn-danger, #edit-cancel-button, #edit-save-button, .button-close-message, .button-close-window, .button-cancel, .button-insert-code, #wd-ed-codewizard-type, #wd-ed-imagewizard-position, #wd-ed-imagewizard-uri, #wd-ed-imagewizard-byuri .button, #wd-ed-imagewizard-flickr, #wd-ed-imagewizard-byflickr .button, .query-area .button, .button-close, .button-rename, .button-no\,-cancel, .button-yes\,-delete, #file-upload-form .btn-primary, .button-move, #action-area .buttons .btn-primary, #edit-page-form .buttons .btn-info, #view-diff-div .button, .button-yes\,-revert, .modal-footer .btn-danger, .modal-footer .btn-default, .modal-footer .btn-primary, #mba-apply, .yui-content .button, .btn-warning {
    background-color: #FFFFFE!important;
    color: black;
}

#h-perpage, .optionstd a, .new-post .btn {
    background-color: #ffffff;
    color: black;
}

.forum-category-box table tr.head td {
    color: #eee;
}

.head .title {
    color: rgb(60, 60, 60);
}

.forum-group .head .title {
    color: #ddd;
}

#top-bar ul li.sfhover a, #top-bar ul li:hover a, #account-options ul li.sfhover a, #account-options ul li:hover a {
    background: #eff7ff;
    color: #369;
    border: none;
}

#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
    color: #369;   
}

#top-bar ul li ul {
    box-shadow: none;
}

div#top-bar:before {
    content: "rpcwiki";
    float: left;
    margin-left: -8px;
    margin-right: 12px;
    padding-top: 8px;
    font-family: Verdana, Arial, sans-serif;
    font-size: 1.2em;
    font-variant: small-caps;
    font-weight: bold;
    cursor: pointer;
}

.owindow {
    background: #eee !important;   
}

.modal-header {
    background: #cee3f8 !important;   
    border-bottom: 1px solid #5f99cf;
}

.modal-body {
    background: white !important;   
}

#edit-page-textarea, #edit-page-title, #edit-page-comments, .query-area .text, #membership-by-apply-text, .yui-content .text {
    background-color: #f9f9f9;
    color: #000;
}

#action-area-top .preview-message {
    background-color: #fff;
}

.hl-code, .hl-identifier, .hl-brackets {
    color: #171717;
}

table.wiki-content-table th {
    background-color: #f1f1f1;
    color: #2d2d2d;
}

table.wiki-content-table td {
    background-color: #ffffff;
    color: #000;
}

.block404 {
    background-color: #e8e8e8;
    color: black;
    margin-top: 25px;
    margin-bottom: -30px;
}

.block404 img {
    filter: invert(1);
}

pre {
    font-family: consolas,"Courier New",courier,monospace;
    font-size: 1em;
}
}
