css:
@-moz-document url-prefix("https://lainon.life") {
* {
    box-shadow: none !important;
    text-shadow: none !important;
}

body {
    background: #008080;
}

a,
a:active,
a:link,
a:hover,
a:visited {
    color: #ccc !important;
}

.box,
body > footer {
    background-color: #000 !important;
    border: 1px solid #ccc;
    color: #ccc !important;
}

#lainplayer,
h1#nowplaying,
#lainplayer i,
#lainplayer h2 {
    background-color: #ccc;
    color: #000 !important;
    text-align: left;
    font-size: 12pt;
    text-transform: uppercase;
}

#nowplaying,
#nowalbum {
    margin-left: 5px;
}

#nowalbum {
    margin-bottom: -10px;
}

#track-progress {
    border-radius: 0;
    background-color: #000;
}

.progressbar {
    background: none;
    border-radius: 0;
    border: .5px solid #000;
    margin-left: 5px;
}

#lainplayer {
    margin-bottom: 10px;
}

.fa-arrow-circle-o-left:before {
    content: "\f177";
}

.fa-pause:before {
    content:"\f28b";
}

.fa-play:before {
    content:"\f144";
}

select, 
input, 
textarea {
    background-color: #000;
    color: #ccc;
}

select,
input,
audio,
hr,
textarea {
    border: .5px solid #ccc;
}

.inlineheading,
#playlist,
.fa {
    color: #ccc;
    text-transform: lowercase;
}

.track.current_track {
    color: #ccc;
    text-transform: uppercase;
    font-weight: bold;
    border-top: .5px solid #ccc;
    border-bottom: .5px solid #ccc;
}

.dur.current_track {
    border-top: .5px solid #ccc;
    border-bottom: .5px solid #ccc;
    padding: 5px;
}

body > footer {
    border-top: none !important;
    width: 994px;
    height: 30px;
    margin-left: auto !important;
    margin-right: auto !important;
    position: relative !important;
    padding: 0.3em;
    text-align: left;
}

}
