css:
@-moz-document domain("meneame.net") {
/* bordes */
    div,a,span {
         border:none !important;
                                        } 
	button,select {
         border-color:#444 !important;
    }

	form, fieldset {
        background-color: #5071A8;
    }

	form.form {
        padding: 1em;
        border-radius: 8px;
    }
    /* fondos */
     .section-profile .contents-layout,.pages a, .pages span,.news-summary .warn, .story-blog .warn,#toplink,.news-details a.comments,.sidebox .header,.comment-body,.topbox,.header-top-wrapper, select,.sneaker-legend {
         background:#414280; /* !important;*/
    }

.comment-header, .comment-header a, .comment-header span, .comment-header strong {
    
}

.widget-popular-links-slider .link.slick-slide.slick-active {
    background: #6b6be4;
}
    /* fondos_secundarios */
     .comment.high .comment-body, .comment.high .comment-header, .comment.high .comment-header .comment-date, .comment.high .comment-header a, #toplink a.footer,.header-menu-wrapper, .subname, .upname a:visited, .subname a:link ,legend, legend a, legend a:visited {
         background: #3B4C5B; /* !important;*/
    }
    /* botones */
     .section-profile .contents-layout,.sidebox-sub {
         background:#2F3C48 !important;
    }
    /* botones */
     .voters-list,.menealo, .menealo a,.section-profile .contents-layout .contents-menu a.selected, tr:hover td, table.keyvalue tr:hover th, .section-profile .contents-layout .contents-menu a:hover, .section-profile .contents-layout .contents-menu a:hover span .sidebox .clics, .btn.btn-mnm.btn-inverted, .news-shakeit span,.news-body .box {
        background:#2F3C48 !important;
        color: white !important;
    }
    /* textos */
     body,.news-content,p, span:not(.voted),.menealo span, .comment-text,.section-profile .contents-layout .contents-menu a span {
         color: black !important;
    }

select {
    color: white;
}

.news-summary .warn, .story-blog .warn,.note {
    color: white;
}

.header-menu01 .selected a, .header-menu01 ul li a:hover { background:none; border-bottom:3px solid #ccc !important; margin-bottom:-3px; padding-bottom:6px; }
    /* enlaces */
     .comment-header a,.news-details, .news-details.main {
         color:#3B4C5B !important;
    }
    /* textos */
    .menealo span,.menealo a, a:not(.vote), .news-details button, .news-details span.karma-letter {
         color: white !important;
    }
.news-details {
    background:#4E6F95;
}
.news-summary.promoted-article .center-content { 
    background: #D5E2EF; 
}
    /* votos */
     .sidebox .votes span, .sidebox .clics span,.news-shakeit .votes a,.alert p,.comment.high .comment-header, .comment.high .comment-body .comment-text, .comment.high .comment-header .comment-date, .comment.high .comment-header a {
         color:#EFF4F9 !important;
    }
    /* titulos */
     h1, h2, h3, h4, h4 a, h5, h6, .sidebox-sub .description {
         color:#3B4C5B !important;
    }

	.section-profile .contents-layout .contents-menu a span {
        color: white !important;
    }
    /* extras */
    .comment.high .comment-text, .news-shakeit * /*::placeholder,*/ {
         color: #333 !important;
    }

	textarea, .sidebox .clics {
         background-color: #c3c3c3 !important;
    }

     .subname.big {
         color:#fff !important;
         font-size: 10px;
         padding: 3px 12px;
    }
    /* .current,.item span {
         color:#ff6600 !important;
    }*/
     #footcol1,.sneaker {
         background:none;
    }
    /* perfil */
     .section-profile .contents-layout .contents-body,body,.topbox,.sidebox-rounded.orange .header {
         background:#9C9EB7;;
    }
	#notifier_panel div a {
        color: #000 !important;
	}

	.sidebox .header h4 a{
        color: white !important;
    }

    .comment.comment-avatar-large  {
		background-color: #7395CE;  
        border-radius: 10px;
    }
    .comment.comment-avatar-large  .comment-body {
        background-color: #7395CE; /*  !important;*/
    }
    .comment.comment-avatar-large  .comment-header, .comment.comment-avatar-large .comment-header span, .comment.comment-avatar-large .comment-header a {
        background-color: #7395CE; /* !important;*/ 
    }
.cell .votes {
    background-color: #3c47d7;
    border-radius: 5px;
}

#notifier_panel ::before, .finished {
    color: black !important;
}

.notifications {
    border-radius: 30%;
}

.fa.fa-search {
    color: #ccc !important;
}


#footthingy p {
    border-color: #e35614 !important;
}

.sidebox div ul {
    background-color: #5071A8;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
}

.sidebox.brown .header {
    background: #3c38db;
}

.sidebox-rounded.orange .header {
    background-color: #4f64ff !important;
}

.sidebox-rounded.orange .header h4 a {
    color:white !important;
}

.sidebox .body {
    background-color: #5071A8;
}

.alert-warning {
    color: #fff;
    background-color: #0035CB;;
    border-color: #faebcc;
}


.section.section-article-submit .btn-gray, .section.section-article-submit .story-blog-aside .dropdown-menu button{
    color:black;
}

.commentform a.emoji-tab {
    color: #333;
    background-color: grey;
}

.commentform a.emoji-tab.active {
    color: white;
    background-color: #4d4846;
}

.emojiCat li {
    border: 1px solid #b9b4ab;
        border-bottom-color: rgb(185, 180, 171);
        border-bottom-style: solid;
        border-bottom-width: 1px;

}

.header-top-wrapper {
    background: #2F3C48!important;
}

.mini-icon-text i.fa.fa-edit {
    color: #fff;
}

#cboxContent {
    background-color: grey;
}

.clics {
    border-radius: 5px;
}

.header h4 span {
    color: white !important;
}

.news-details span.votes-up i, .news-details span.votes-anonymous i, .news-details span.votes-down i {
    color: #c8bfba;
}

.news-details a i, .news-details button i {
    color: #c8bfba;
    margin-right: 5px;
}

#comunicado .white_box {
    background: linear-gradient(to right, #7d6fe4 0%, #525ba2 100%) !important;
}

.body.mainsites ul li, .popular-link-widget-right div.subtitle {
    color: #ccc;
}

a.vote.up::hover {
    color: green !important;
}

.comment.high.comment-avatar-large {
    background-color: #3e3eb7;
}

.comment.high.comment-avatar-large .comment-body, .comment.high.comment-avatar-large .comment-header, .comment.high.comment-avatar-large .comment-header a, .comment.high .comment-header .comment-date {
    background-color: rgba(255, 255, 255, 0);
}

.comment.author .comment-body, .comment.author .comment-body .comment-header {
    background-color: #7891EA;
}

.comment.high.author .comment-body, .comment.high.author .comment-body .comment-header, .comment.high.author .comment-header a {
    background-color: #263C7E;
}



.pages span.current {
    background-color: #3B4C5B;
    color: white;
}

.pages span {
    color: #3B4C5B;
}

.pages span.nextprev {
    color: grey;
}

.news-shakeit.mnm-queued  .clics, .news-shakeit.mnm-queued .votes, .news-shakeit.mnm-queued .clics {
    background-color: #77AAE6;
}

.news-shakeit.mnm-published .clics, .news-shakeit.mnm-published .votes, .news-shakeit.mnm-published * {
	background-color: #4E6F95;
}

.news-shakeit.mnm-discarded .clics, .news-shakeit.mnm-discarded .votes, .news-shakeit.mnm-discarded * {
	background-color: black;
    color: grey !important;
}

.sidebox-rounded .show-responses, .news-tags {
    color: #bbb;	
}


ul#userinfo a.notifications span {
    color: black;
}

#toplink p, .popular-link-widget-right div.description {
    color: #3B4C5B;
}

div.article-container div#editor {
    background-color: #3B4C5B;
    border-radius: 5px;
}

.poll-vote {
    background-color: #8181aa;
}

label, .poll-vote footer span {
    color: white;
}

#footwrap a{
    color: #4a4a97  !important;
}

.dropdown-menu, a.menu-more-button:hover, div.dropdown.menu-more.open a.menu-more-button {
    background-color: #3B4C5B;
}

div.dropdown.menu-more.open ul.dropdown-menu.menu-subheader a:hover {
    color:#3B4C5B !important;
}

#footthingy li a {
    color: #e35614 !important;
}

li .submit_new_post, li .submit_new_article {
    color: #3B4C5B !important;
}

li .submit_new_post:hover, li .submit_new_article:hover, .ql-editor p {
    color: white !important;
}

.section,.section-profile-header-info p {
    color: #3B4C5B;
}

.fa-bell {
    color: #8e83db;
}

h2 a {
    color: #2F3C48 !important;
}

.news-summary {
    background-color: #6985A2;
    margin-bottom: 1em;
}

.story-blog h1 a, .text a {
    color: #3B4C5B  !important;
}

a.title {
    color: #2F3C48 !important;
}

.comment-footer a:not(.vote) {
    color: #adcedf !important;
}

.menu01-itemsl span.button-new {
    background: #cdcddf;
}

.commentform form {
    padding-left: 5px;
    padding-top: 1em;
    border-radius: 8px;
}

table.subs-listing {
    background-color: #D0DAEA;
}
.sidebox-rounded .guest-name {
    color: #fff;
}
.sidebox-rounded .guest-description {
    color: #000;
}
.sidebox-rounded .body {
    background-color: #5071A8;
}

#footthingy li {
    color: #3e42d7;
}
}
