css:
[aria-label='Image'] {
    margin: 0 !important;
    max-width: 100%;
    box-sizing: content-box;
    align-content: stretch;
    flex-basis: 0 !important;
    display: flex;
    flex-wrap: wrap;
}

[aria-label='Image'] > div:first-child {
    background-size: contain;
}

[aria-label='Image'] > div:nth-child(2) {
    max-width: 100%;
    max-height: 100%;
}

div[style="padding-bottom: 56.25%;"] {
    padding-bottom: 70em !important;
    width: 100%;
    position: relative;
}

[role='banner'] {
    flex-grow: 0;
}

[role='banner'] > div:first-child {
    width: 5.5vw;
}

[role='banner'] > div:first-child > div:first-child > div {
    width: auto;
}

nav[role='navigation'] > * {
    width: auto;
}

nav[role='navigation'] > * > div:first-child > div:last-child {
    display: none;
}

a[href='/compose/tweet'][role='button'] {
    width: 100%;
    min-width: 0;
    padding: 0;
}

a[href='/compose/tweet'][role='button'] span {
    font-size: 90%;
}

[role='main'] > div:first-child {
    width: 100%;
}

[role='main'] > div:first-child > div:nth-child(2) > div:first-child {
    width: 100%;
    max-width: 100%;
    margin: 0;
}

[data-testid='primaryColumn'] {
    max-width: initial;

    flex-shrink: 1;
}

[data-testid='sidebarColumn'] {
    display: none!important;
    margin: 0;
}

[data-testid='primaryColumn'] > div:first-child > div:first-child > div:first-child > div:first-child > div:first-child > div:first-child > div:first-child > div:first-child {
    max-width: 100%;
    margin: 0;
}

[data-testid='primaryColumn'] > div:first-child > div:first-child > div:last-child {
    width: 100%;
    max-width: 100%;
    margin: auto;
}

[aria-labelledby='master-header'] {
    max-width: 100%;
    margin: 0;
}

[aria-labelledby='detail-header'] {
    width: 80%;
    max-width: 100%;
    margin: 0;
}

[aria-labelledby='detail-header'] > div:nth-child(2) > div:first-child > div:first-child {
    max-width: 100%;
}

[aria-labelledby='detail-header'] > div:nth-child(2) > div:first-child > div:first-child > div:first-child > div:first-child > div:first-child > div:nth-child(2) {
    max-width: 60%;
}

[aria-labelledby='detail-header'] > div:first-child > div:first-child > div:first-child > div:first-child > div:first-child {
    max-width: 100%;
    margin: 0;
}

[data-testid='primaryColumn'] > div:first-child > div:first-child > div:last-child > div:first-child > div:last-child {
    max-width: 100%;
}

div[aria-labelledby="modal-header"] {
    max-width: 100%;
    width: 65%;
    max-height: 80%;
    height: 65%;
}

[aria-labelledby="modal-header"] > div:first-child > div:last-child {
    margin-left: 0;
    margin-right: 0;
    max-width: 100%;
}

div[aria-labelledby="modal-header"] > div:first-child > div:last-child > div:first-child > div:first-child > div:first-child > div:first-child > div:first-child > div:first-child > div:last-child {
    display: table;
}

div[aria-labelledby="modal-header"] > div:first-child > div:last-child > div:first-child > div:first-child > div:first-child > div:first-child > div:first-child > div:first-child > div:last-child > div:last-child {
    display: table-header-group;
}

div[aria-labelledby="modal-header"] > div:first-child > div:nth-child(2) > div:first-child > div:first-child > div:first-child {
    margin-left: 0;
    margin-right: 0;
}

div[data-testid="toolBar"] {
    max-width: 90%;
}
