@-moz-document domain("www.youtube.com") {
/*[[hide-author-name]]*/
yt-live-chat-text-message-renderer:not([author-is-owner]) #author-photo img[src^="https://yt3.ggpht.com/-"],
yt-live-chat-legacy-paid-message-renderer #author-photo img[src^="https://yt3.ggpht.com/-"],
yt-live-chat-ticker-paid-message-item-renderer #author-photo img[src^="https://yt3.ggpht.com/-"],
yt-live-chat-ticker-paid-sticker-item-renderer #author-photo img[src^="https://yt3.ggpht.com/-"],
yt-live-chat-ticker-sponsor-item-renderer #author-photo img[src^="https://yt3.ggpht.com/-"],
yt-live-chat-paid-sticker-renderer #author-photo img[src^="https://yt3.ggpht.com/-"] {
  box-sizing: border-box;
  padding-left: 24px;
  background-image: url(data:image/png;base64,);
}
#author-photo[height="40"] img[src^="https://yt3.ggpht.com/-"],
#author-photo img[src^="https://yt3.ggpht.com/-"][height="40"] {
  padding-left: 40px;
  background-image: url(data:image/png;base64,);
}

/* Red */
#author-photo img[src^="https://yt3.ggpht.com/-a"],
#author-photo img[src^="https://yt3.ggpht.com/-b"],
#author-photo img[src^="https://yt3.ggpht.com/-c"] {
