css:
@-moz-document regexp(".*board\\.([A-z]{2})\\.nostale\\.gameforge\\.com.*") {
.boxMenuHasChildren .badgeUpdate
{
    width: 0;
	visibility: hidden;
    padding: 0;
}

.pageHeaderFacade .layoutBoundary
{
    padding-top: 70px;
    padding-bottom: 35px;
}

.layoutBoundary
{
    max-width: 1500px;
}

.pageNavigation
{
    max-width: 1500px;
    border-radius: 15px 15px 0 0;
    padding-bottom: 10px;
    padding-top: 10px;
}

.contentHeader .contentTitle
{
    font-size: 1.75rem;
    font-weight: bold;
}

.section .sectionTitle
{
    color: rgb(80, 80, 80)
}

dl.dataList > dt, dl.dataList > dd
{
    color: rgb(190, 190, 190)
}

.userTitleBadge
{
    border-radius: 10px;
    text-align: center;
}

.userProfileUser .userTitleBadge
{
    max-width: 170px;
}

.breadcrumbs > ol > li > a
{
    font-size: 0.9rem;
}

.userLink
{
    font-size: 1.2rem;
    color: rgb(40, 25, 0);
}

.userLink:hover
{
    font-size: 1.2rem;
    color: rgb(0, 0, 0);
}

.containerHeadline time
{
    color: rgb(110, 110, 110);
}

.wbbPostLink, .wbbTopicLink
{
    color: rgb(125, 0, 255);
    font-size: 1.15rem;
    font-weight: bold;
}

.wbbPostLink:hover
{
    color: rgb(75, 0, 175);
    font-size: 1.15rem;
    font-weight: bold;
}

.recentActivityList > li
{
    padding: 13px;
}

.sidebar .wbbTopicLink
{
    font-size: 1.2rem;
    color: rgb(80, 175, 230)
}

.sidebar .userLink
{
    font-size: 1rem;
    color: rgb(30, 120, 160)
}

.badge, a.badge
{
    border-radius: 5px;
}

#main .sidebar
{
    background: rgba(0, 0, 0, 0.75);
    border-radius: 0 0 15px 0;
}

.sidebar
{
    flex: 0 0 320px;
}

#content
{
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.9) 0%, rgba(210, 230, 255, 0.9) 100%);
}

.content:not(:last-child)
{
    flex-basis: calc(100% - 320px);
    max-width: calc(100% - 320px);
}

.content + .sidebar
{
    margin-left: 0;
}

.boxesSidebarLeft .box:not(.boxBorderless), .boxesSidebarRight .box:not(.boxBorderless)
{
    background: rgb(0,0,0);
	background: linear-gradient(180deg, rgba(0,0,0,0) 0%, rgba(0,0,0,0.5) 7%, rgba(0,0,0,0.5) 93%, rgba(0,0,0,0) 100%);
}

.boxesSidebarLeft .box:not(.boxBorderless), .boxesSidebarRight .box:not(.boxBorderless):first-child
{
    background: rgb(0,0,0);
	background: linear-gradient(180deg, rgba(0, 0, 0, 0.6) 0%, rgba(0, 0, 0, 0.5) 50%, rgba(0, 0, 0, 0.5) 93%, rgba(0, 0, 0, 0) 100%);
}

.messageGroupList .messageGroupLink
{
    font-size: 1.3rem !important;
}

.messageGroupInfo
{
    font-size: 0.9rem;
}

.messageGroupTime
{
    color: rgb(110, 110, 110);
}

.messageGroupAuthor .userLink
{
    font-size: inherit;
}

.tabularListRowHead, .tabularList > li
{
    padding: 3px 10px 3px 0;
}

.paginationTop
{
    margin-top: 10px;
}

.paginationTop + .section
{
    margin-top: 10px;
}

.messageList:not(.messageReducedList) > li
{
    padding: 15px 0 0 0;
}

#content
{
    padding: 20px 30px 30px 30px;
    border: 1px rgb(0, 0, 0) solid;
}

.content
{
    border-radius: 0 0 0 15px;
}

.content:only-child
{
    border-radius: 0 0 15px 15px;
}

.pageHeaderPanel
{
    background-size: 100% 72px;
    height: 60px;
    margin-top: -9px;
}

.mainMenu
{
    padding-top: 0;
    margin-top: -1px;
}

.userPanel
{
    padding-top: 9px;
}

.messageSidebar
{
    padding: 15px;
    background: rgb(210, 230, 245);
    background:
        repeating-linear-gradient(
            -45deg,
            rgba(206, 229, 255, 0.4) 0%,
            rgba(206, 229, 255, 0.4) 3%,
            rgba(150, 180, 220, 0.4) 0%,
            rgba(150, 180, 220, 0.4) 6%
       );
    border: 1px rgb(0, 0, 0) solid;
    border-radius: 6px 6px 6px 6px;
    margin: 2px;
    
}

.userAvatar
{
    padding: 1px;
  	background: 
      radial-gradient(circle at 50% 0,
        rgba(255, 0, 0, 0.5),
        rgba(255, 0, 0, 0) 70.71%),
      radial-gradient(circle at 6.7% 75%,
        rgba(0, 0, 255, 0.5),
        rgba(0, 0, 255, 0) 70.71%),
      radial-gradient(circle at 93.3% 75%,
        rgba(0, 255, 0, 0.5),
        rgba(0, 255, 0, 0) 70.71%) #eaeaea;
  border-radius: 50%;
}

.userAvatarImage
{
    padding: 2px;
  	background: 
      radial-gradient(circle at 50% 0,
        rgba(255, 0, 0, 0.5),
        rgba(255, 0, 0, 0) 70.71%),
      radial-gradient(circle at 6.7% 75%,
        rgba(0, 0, 255, 0.5),
        rgba(0, 0, 255, 0) 70.71%),
      radial-gradient(circle at 93.3% 75%,
        rgba(0, 255, 0, 0.5),
        rgba(0, 255, 0, 0) 70.71%) #eaeaea;
  border-radius: 50%;
}

.message
{
    background: rgb(240, 245, 255);
}

.messageContent
{
    border: 1px rgb(0, 0, 0) solid;
    border-radius: 6px 6px 6px 6px;
    margin: 2px;
    padding: 20px;
    background: rgb(210, 230, 245);
}

.messageReduced .messageContent
{
    padding: 10px 10px 0 10px;
}

.messageBody
{
    border-top: 1px rgba(0, 0, 0, 0.1) solid;
    padding-top: 20px;
    padding-bottom: 10px;
}

.messageHeader + .messageBody
{
	margin-top: 5px;
}

.messageSignature
{
    border-top: 1px rgba(0, 0, 0, 0.1) solid;
}

.htmlContent hr, .messageBody > .messageText hr, .messageSignature > div hr, .redactor-layer hr
{
    border-top: 1px rgba(0, 0, 0, 0.1) solid;
}

.pagination > ul > li:not(:last-child):not(:first-child)
{
    background: rgb(220, 220, 220);
    border-radius: 5px;
    box-shadow: inset 0 0 0 1px rgb(200, 200, 200);
}

.pagination > ul > li.active > a, .pagination > ul > li.active > span, .pagination > ul > li > a:hover
{
    border-radius: 5px;
    box-shadow: inset 0 0 0 1px rgb(77, 177, 235);
}

.messageAuthor + *
{
    margin-top: 5px;
    border-top: 1px rgb(220, 220, 220) inset;
    padding-top: 5px;
}

.userCredits dl.dataList > dt, .userCredits dl.dataList > dd
{
    color: rgb(30, 30, 30);
}

.messageSidebar a
{
    color: rgb(0, 145, 235);
}

.messageSidebar a:hover
{
    color: rgb(0, 97, 169)
}

.messageSidebar .username
{
    font-weight: bold;
    font-size: 1.5rem;
    color: rgb(0, 97, 169)
}

.messageSidebar .username:hover
{
    font-weight: bold;
    font-size: 1.5rem;
    color: rgb(0, 67, 139)
}

.popover, .popoverContent
{
    border-radius: 10px;
}

.pageFooterCopyright, .boxesFooter, .boxesFooterBoxes
{
	background: rgba(0, 0, 0, 0.8);
}

.likesSummary, .likesSummary > .icon
{
    color: rgb(105, 140, 97);
}

.messageFooterGroup:not(:first-child) > .likesSummary, .messageFooterGroup:not(:first-child) > .messageFooterButtons
{
    margin-top: 10px;
}

.messageSignature
{
    margin-top: 10px;
}

.messageText a, a, .messageTitle a
{
    color: rgb(125, 0, 255);
}

.messageText a, .messageText a:hover
{
    padding: 0;
}

.messageText a.button, .messageText a:hover.button
{
    padding: 6px 8px;
}

.messageText a:hover, a:hover, .messageTitle a:hover
{
    color: rgb(75, 0, 175);
    text-decoration: underline;
}

.messageText .active a:hover, .active a:hover, .messageTitle .active a:hover
{
    text-decoration: none;
    color: rgb(75, 0, 175);
}

.active a:hover :not(.pageHeaderPanel)
{
    text-decoration: underline;
}

.containerList > li.comment
{
    padding: 13px;
}

.containerList > li.commentResponse
{
    padding: 13px;
}

button, input[type="button"], input[type="reset"], input[type="submit"], .button, a.button
{
    border-radius: 5px;
    box-shadow: inset 0 0 0 1px rgb(200, 230, 255);
}

.dropdownMenu
{
    background-color: rgb(200, 200, 200);
}

.dropdownMenu li:hover:not(.dropdownDivider):not(.dropdownList):not(.dropdownText) > a, .dropdownMenu li.dropdownList > li:hover:not(.dropdownDivider) > a, .dropdownMenu li.dropdownNavigationItem > a, .dropdownMenu li.active > a
{
    color: rgb(0, 122, 200);
}

.messageReduced .messageHeader
{
    padding: 5px;
    background: rgba(75, 160, 255, 0.5);
    border-radius: 10px;
}

.messageTitle
{
    font-size: 1.2rem;
    font-weight: bold;
}

.messageReduced .messageBody
{
    padding: 5px 5px 0 5px;
    margin-top: 10px;
}

.messageReduced .messageFooter
{
    padding: 0;
    margin-top: 0;
}

.redactor-box > textarea:not(.wysiwygTextarea)
{
    background: rgb(20, 35, 50) !important;
}

.redactor-toolbar > li > a:hover
{
    background: rgb(220, 220, 220);
    color: rgb(0, 0, 0);
}

.newMessageBadge
{
    background-color: rgb(255, 60, 60);
    background:
        repeating-linear-gradient(
            -45deg,
            rgb(120, 225, 60) 0%,
            rgb(120, 225, 60) 10%,
            rgb(80, 200, 60) 0%,
            rgb(80, 200, 60) 20%
       );
    font-size: 1.2rem;
    width: 75px;
    text-align: center;
    font-weight: bold;
    margin-left: 10px;
}

.ignoredUserContent:not(:hover)
{
    opacity: 1.0 !important;
}

.ignoredUserContent
{
    -webkit-filter: none !important;
    filter: none !important;
}

.ignoredUserMessage > *
{
    display: initial;
}

.ignoredUserMessage
{
    background: rgb(240, 245, 255) !important;
    border: 2px rgb(0, 0, 0) solid !important;
    cursor: default !important;
    color: black !important;
}

.ignoredUserMessage::before
{
    display: none;
}

.userNotice .codeBox
{
    padding: 0;
    border-radius: 15px;
}

.userNotice center div
{
    width: 800px;
}

.pollContainer .pollResultItem .pollMeter
{
    background-color: rgba(50, 50, 50, 0.8);
}

.pollContainer .pollResultItem .pollResultItemCaption > .pollOptionRelativeValue
{
    color: inherit;
}

.pollContainer .pollResultItem .pollMeter > .pollMeterValue
{
    background-color: rgba(150, 120, 255, 0.8);
}

.pollContainer
{
    background: rgba(0, 0, 0, 0.1);
    padding: 15px;
    border-radius: 5px;
}
}
