css:
@-moz-document domain("jestjs.io") {
/* i really want this to be global */
:root {
    --BG: #2A2A2E;
    --BG_DARK: #222;
    --TEXT: #ddd;
    --ACCENT: #1B901D;
    --ACCENT_LIGHT: #3ad637;
}

.jest-card .jest-card-front {
    background-color: var(--BG_DARK);
}
.jest-button-container {
    background: transparent !important;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    transform: translateY(60px);
}
.jest-button-container .jest-button {
    border-radius: 2px;
}

.container.lightBackground {
    background-color: var(--BG_DARK);
}

html, body {
    background: var(--BG_DARK);
}

.fixedHeaderContainer header h3, .navigationSlider .slidingNav ul li a,
.navigationSlider .slidingNav ul,
.navigationSlider .slidingNav ul li,
.navigationSlider .slidingNav ul li a,
.navigationSlider .slidingNav ul li a:hover,
.navigationSlider .slidingNav ul li a:focus,
.navigationSlider .slidingNav ul li.siteNavGroupActive a,
.slidingNav ul.nav-site .siteNavGroupActive a,
.slidingNav ul.nav-site .siteNavGroupActive,
.slidingNav ul.nav-site a,
.slidingNav ul.nav-site
{
    background: var(--BG_DARK);
    color: var(--TEXT);
    border: none !important;
}
.slidingNav .nav-site .siteNavItemActive, .slidingNav .nav-site siteNavItemActive a {
    background: var(--ACCENT);
}
.fixedHeaderContainer {
    background-color: var(--BG_DARK);
}

.navPusher,
.docMainWrapper.wrapper {
    background: var(--BG);
    color: var(--TEXT);
}

.navGroup h3, .navGroup a {
    color: var(--TEXT) !important;
}

pre, code.hljs {
    background: var(--BG_DARK);
    border: none;
    color: var(--TEXT);
    border-radius: 5px;
    border: 0.5px solid rgba(0,0,0,.1);
}

.hljs-keyword {
    color: #42ce42;
}

.hljs-built_in {
	color: #ba43ec;
}
.hljs-subst {
    color: #2284ca !important;
}
.hljs-comment {
    color: #a6acb1 !important;
}
body .hljs-addition {
    background-color: var(--ACCENT);
    color: white;
    border-radius: 2px;
    padding: 2px 4px;
}
body .hljs-deletion {
    background-color: #bf0909;
    color: white;
    border-radius: 2px;
    padding: 2px 4px;
}


.onPageNav a{
    color: var(--TEXT);
}

blockquote {
    background-color: var(--BG_DARK);
    border-radius: 1px;
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
}

blockquote p {
    color: white;
}

blockquote p a {
    color: var(--ACCENT_LIGHT);
}
}
