css:
@-moz-document domain("mixer.com") {
* {
	box-shadow: none !important;
	border-radius: 0 !important;
	--opa-90: .9;
	--opa-high: .8;
	--opa-mid: .5;
	--opa-low: .2;
	--text-shadow-set: 1px 1px 2px black;
	--text-shadow: var(--text-shadow-set), var(--text-shadow-set), var(--text-shadow-set), var(--text-shadow-set), var(--text-shadow-set), var(--text-shadow-set), var(--text-shadow-set), var(--text-shadow-set);
}

body {
/* 	background: black url("https://images5.alphacoders.com/523/thumb-1920-523395.jpg") no-repeat fixed center 80% / cover; */
	background: black url("https://wallpaperplay.com/walls/full/8/a/e/17914.jpg") no-repeat fixed center / cover;
	/* 	background-color: lightgreen; */
}

.background-full {
	background: none !important;
}

[class="content"] > b-homepage >b-homepage-banner {
	display: none;
}

/*Header*/
	/*Front-page*/ b-desktop-header, [class="stay-collapsed collapsed"] {
/* 		background: rgba(41, 55, 74, var(--opa-high)) !important; */
		background: rgba(0, 0, 0, var(--opa-high)) !important;
		position: relative !important;
		height: unset !important;
	}

	/*Front-page*/ b-desktop-header > .content {
		transform: unset !important;
	}

	.collapsed .content {
		transform: translateY(20px) !important;
		background:transparent !important
	}

	/*Popup menu*/
		.menu-area[_ngcontent-c13] {
			transition: unset;
			top: -20px;
			border:0
		}
	/*Popup menu*/
/*Header*/

/*Featured carousel*/
	/*Selected*/
		.carousel > .player {
			border: 0!important;
		}
		.carousel > .player {
			opacity: var(--opa-high);
		}

		.carousel > .player:hover {
			opacity: var(--opa-90);
		}
	/*Selected*/

	.carousel > b-delve-featured-panel {
		opacity: var(--opa-low);
/* 		filter: blur(5px) ; */
	}

	.carousel > b-delve-featured-panel:hover {
		opacity: var(--opa-high);
/* 		filter: blur(.5px); */
	}

	.carousel > .carousel-arrow {
		background: transparent !important;
	}
/*Featured carousel*/

/*Content*/
	/*Search*/
		.control.input > [class="ng-pristine ng-valid ng-touched"]{
			background: rgba(19, 29, 44, var(--opa-high)) !important;
			border: 0 !important
		}

		.input[_ngcontent-c25] input[_ngcontent-c25] {
					background: rgba(19, 29, 44, var(--opa-high)) !important;
			border-radius: 50px !important
		}
		
		[class="is-stuck"] > header, .is-stuck[_nghost-c92] header[_ngcontent-c92]::after {
			position: relative !important;
/* 			visibility: hidden !important; */
		background: transparent;
			top: 0 !important;
		}

		.is-stuck header {
			background: transparent !important;
		}
	/*Search*/

	/*Browse games*/
		b-game-content > .background {
			height: 100vh !important;
			position: fixed !important;
		}

		b-game-content > .background::before {
			background: linear-gradient(rgba(33, 44, 61, .1), rgba(33, 44, 61, .3)) !important;
	/* 		background: none !important; */
		}
	/*Browse games*/

	/*game-card*/
		b-game-card > .game-card, b-simple-browse-card > a {
			background: transparent !important;
		/* 	background: #43526d; */
		}

		b-simple-browse-card.card > a {
			border: 0 !important;
			border-radius: 0 !important;
		}

		.games-row > b-game-card > a.game-card > .thumbnail, b-simple-browse-card > a > .thumbnail
, b-browse-content-grid > b-hide-outside > b-game-card > a > .thumbnail
, .footer > b-avatar {
			opacity: var(--opa-mid)
		}

		.games-row > b-game-card > a.game-card > .footer, b-simple-browse-card > a > .footer, b-browse-content-grid > b-hide-outside > b-game-card > a > .footer {
/* 					background: rgba(67, 82, 109, var(--opa-mid)); */
		}

		/*Hover*/
			b-game-card > .game-card:hover .thumbnail, b-simple-browse-card > a:hover > .thumbnail, b-browse-content-grid > b-hide-outside > b-game-card > a:hover > .thumbnail, b-simple-browse-card:hover .footer > b-avatar, b-game-card:hover .footer > b-avatar {
				opacity: var(--opa-90) !important;
			}

			b-game-card > .game-card:hover .footer, b-simple-browse-card > a:hover > .footer {
/* 				background: rgba(67, 82, 109, var(--opa-mid)) !important; */
				background: rgba(0, 0, 0, var(--opa-mid)) !important;
				
			}

			b-game-card > .game-card .footer *, b-simple-browse-card > a > .footer * {
				text-shadow: var(--text-shadow);
			}

			b-game-card > .game-card:hover .footer *, b-simple-browse-card > a:hover > .footer * {
			/* 				background: rgba(67, 82, 109, var(--opa-high)) !important; */
				text-shadow: none !important;
			}


			/*Only on Mixer*/ div.content > b-homepage > b-delve-row:nth-child(5) > b-delve-oom-channels > div > b-oom-card > a:not(:hover) {
				opacity: var(--opa-mid);
			}
		/*Hover*/
	/*game-card*/

	/*Player*//*Player profile*/
		body > b-app > b-route-loading-indicator {
			display: none !important
		}

		.layout-align-space-between-start {
			position: relative !important
		}

.collapsed .content {
	transform: none !important;
}


		.stay-collapsed {
/* 			height: 85px !important; */
		}

		div.content:nth-child(4) > b-channel-page-wrapper:nth-child(2) > b-channel-web-page:nth-child(2) {
			opacity: var(--opa-90);
		}
	/*Chat*/ div.chat-container div.transcluded-section b-leaderboard-banner-host button.bannerContainer_Y6ne6, div.chat-container div.chat-component-wrapper b-chat-client-host-component div.chatContainer__24UL3 div.webComposerBlock__3lT5b {
		border: 0;
}

	/*Player*/
/*Contert*/

/*Text*/
	[class="title-row layout-row layout-align-start-center"] > .title, .content header > h1, .content .flex > h1{
/* 		color: white; */
		text-shadow: var(--text-shadow);
	}
}

@-moz-document regexp("https://mixer.com/browse/games/.*/(web-show)") {
b-game-content > .background {
	background: url("https://wallpaperaccess.com/full/93316.jpg") fixed no-repeat center top /cover !important;
/* 	background: transparent !important; */
}
}

