@-moz-document domain("apnews.com") {
/******************************************
******************************************
        BACKGROUND RELATED FIXES
******************************************
*******************************************/

/* Article background color */
body {
    background-color: #2f2f2f;
}

/* AP banner logo background color */
.Header {
    background: #151515;
}

/* Main site body background color */
.c016 {
    width: 100%;
    background: #2f2f2f;
}


/* Ad related hovers between articles scrolls */
.FeedCard.PressReleasesCarousel,
.FeedCard.ThirdPartyCarousel {
    background: #2f2f2f;
}

/******************************************
******************************************
          LINK RELATED FIXES
******************************************
*******************************************/

/* Main article big hover link */
.CardHeadline .headline:active,
.CardHeadline .headline:hover,
.CardHeadline .headline:link,
.CardHeadline .headline:visited {
    color: #4387ff;
}

/* Links on additional article columns */
.RelatedTopics .tag a:active,
.RelatedTopics .tag a:hover,
.RelatedTopics .tag a:link,
.RelatedTopics .tag a:visited {
    color: #4387ff;
}

/* Links within articles */
.Article p a:active,
.Article p a:hover,
.Article p a:link,
.Article p a:visited {
    color: #4387ff;
    text-decoration: none;
}

/* Main site bottom link cluster */
.HubTag,
.HubTag:active,
.HubTag:hover,
.HubTag:link,
.HubTag:visited {
    color: #4387ff;
}

/******************************************
******************************************
          TEXT RELATED FIXES
******************************************
*******************************************/

html {
    color: #8c8c8c;
}

