css:
@-moz-document url-prefix("https://bobrtc.live/"), url-prefix("https://bobrtc.live/phonebook") {
/*MADE BY: LIVIXIS (BOBRTC/TLS MEMBER) */

/* HALLOWEEN THEME BY: LIVIXIS (rework of the original) */

/* This is basically a donation of my time to bobrtc and it's wonderful community.
   Please enjoy this free Work IN Progress. Mostly Copy Pasta.
===========================================================================================
   If you want to change the wallpaper please right click on your image and click 
   open in new TAB and copy that link and paste it below.*/

html, body {

    background-color: #000000;
    
    /*=======================| Background |========================*/
    
    background: url(http://myzeroone.com/myzeroone/18296-dark-halloween-wallpaper_36922.jpg);
    
    /*========| change the link to the direct image link. |========*/
        
    margin: 0;
    font-size: 100%;
    font-family:'Source Sans Pro', sans-serif;
    background-repeat: no-repeat;
    background-attachment: fixed!important;
    color: #ff7800
    
}
.row {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
    margin-right: -10px;
    margin-left: -10px;
}
.banner {
    background: url(https://i.imgur.com/RDQaBz0.jpg)no-repeat center;
        background-color: #3381c500;
    	background-repeat-x: no-repeat;
    	background-repeat-y: no-repeat;
    	background-attachment: initial;
   		background-origin: initial;
    	background-clip: initial;
    	background-size: cover;
        position: inherit;


}
.mt-4, .my-4 {
    margin-top: 0rem!important;
    margin-left: 0rem;
}
.slider-info a.btn {
    background: 0 0;
    padding: 10px 374px;
    color: #ff7800;
    border: 2px solid #ff7800;
    font-size: 14px;
    letter-spacing: 5px;
    border-radius: 5rem;
}
[data-aos][data-aos][data-aos-duration="1000"], body[data-aos-duration="1000"] [data-aos] {
    transition-duration: 5s;
}
.col-lg-8 {
    -webkit-box-flex: 0;
    -ms-flex: 0 0 66.6666666667%;
    flex: 0 0 66.6666666667%;
    max-width: 66.6666666667%;
    margin: 335px 0px -230px 240px;
}
h3.txt-w3_agile {
    text-align: center;
    font-size: 2em;
    color: #fff;
    text-transform: capitalize;
    font-weight: 100;
}
.agileinfo-logo h2 {
    color: #00000000;
    display: inline-block;
    font-size: 0rem;
    text-transform: capitalize;
    font-weight: 400;
    line-height: 0px;
}
.agileinfo-logo h2 span {
    color: #00000000;
    font-size: .7em;
    display: block;
    background: rgba(0,0,0,.4);
    width: 85px;
    line-height: 90px;
    height: 0px;
    border-radius: 50%;
}
.card {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column;
    min-width: 0;
    word-wrap: break-word;
    background-color: #00000090;
    background-clip: border-box;
    border: 5px solid #000000;
    border-radius: 2rem;
}
.f1 {
    box-shadow: 0px 10px 16px 0 rgba(73, 39, 74, 255);
    border: none;
    border-radius: 2rem;
}
.f1 h3 {
    font-size: 27px;
    font-weight: 600;
    color: #a0a0a0;
}
.f1 i {
    background: #58585800;
    width: 65px;
    height: 65px;
    line-height: 65px;
    text-align: center;
    font-size: 30px;
}
.card {
    box-shadow: 0px 10px 16px -4px #ff7800;
    border: none;
}
.badge-success {
    color: #666666;
    background-color: #262626;
    -webkit-animation: glow3 5s ease-in-out alternate;
    -moz-animation: glow3 5s ease-in-out alternate;
    animation: glow3 5s ease-in-out alternate; 
}
#addbutton {
    margin-top: -10px;
    width: auto;
    margin-left: 33px;
    background-color: #000000;
}
.phone-icon-inner {
    color: #49274a;
    /*-webkit-animation: glow1 1s ease-in-out alternate;
    -moz-animation: glow1 1s ease-in-out alternate;
    animation: glow1 1s ease-in-out alternate;*/

}
.list-group-item {
    position: relative;
    display: block;
    padding: 1rem 1.5rem;
    margin-bottom: -1px;
    background-color: #fff0;
    border: 1px solid #ff7800;

}
.card-header {
    border-bottom: 1px solid #ff7800;
}
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    margin-bottom: .5rem;
    font-family: inherit;
    font-weight: 500;
    line-height: 1.3;
    color: #ff7800;
}

.dialbannerstatus {
    font-size: 32px;
    font-weight: bold;
    text-align: center;
    padding-left: 0;
    padding-right: 0;
    height: 70px;
	border-style: solid;
    border-width: 6px;
    border-radius: 20px;
    margin-left: 250px;
    margin-right: -45px;

    -webkit-animation: glow1 1s ease-in-out infinite alternate;
    -moz-animation: glow1 1s ease-in-out infinite alternate;
    animation: glow1 1s ease-in-out infinite alternate;

	color: #ff7800
                                            
}
.dialbannertext {
    text-align: center;
    font-size: 68px;
    padding: 0 0 0 15vw;
    color: #49274a;
    height: 110px;
    margin: -10px;
	-webkit-animation: glow2 5s ease-in-out infinite alternate;
	-moz-animation: glow2 5s ease-in-out infinite alternate;
    animation: glow2 5s ease-in-out infinite alternate;
}
/*=================================================GLOW SETUP #1================================================================================*/
@-webkit-keyframes glow1 {
  from {
    text-shadow: 0 0 10px #000, 0 0 10px #ff7800, 0 0 10px #ff7800, 0 0 10px #ff7800, 0 0 10px #ff7800, 0 0 10px #ff7800, 0 0 10px #ff7800;
  }
  to {
    text-shadow: 0 0 20px #000, 0 0 30px #662c4400, 0 0 40px #662c4400, 0 0 50px #662c4400, 0 0 60px #662c4400, 0 0 70px #662c4400, 0 0 80px #662c4400;
  }
}
/*=================================================GLOW SETUP #2=========(800 NUMBERS)=========================================================*/
@-webkit-keyframes glow2 {
  from {
    text-shadow: 0 0 10px #000, 0 0 10px #000, 0 0 10px #000, 0 0 10px #000, 0 0 10px #000, 0 0 10px #000, 0 0 10px #000;
  }
  to {
    text-shadow: 0 0 20px #000, 0 0 30px #662c4400, 0 0 40px #662c4400, 0 0 50px #662c4400, 0 0 60px #662c4400, 0 0 70px #662c4400, 0 0 80px #662c4400;
  }
}
/*=============================================================================================================================================*/
@-webkit-keyframes glow3 {
  from {
    text-shadow: 0 0 10px #000, 0 0 10px #36b37e, 0 0 10px #36b37e, 0 0 10px #36b37e, 0 0 10px #36b37e, 0 0 10px #36b37e, 0 0 10px #36b37e;
  }
  to {
    text-shadow: 0 0 20px #000, 0 0 30px #00000000, 0 0 40px #00000000, 0 0 50px #00000000, 0 0 60px #00000000, 0 0 70px #00000000, 0 0 80px #00000000;
  }
}
.navbar-dark {
    background-color: #00000000;
}
.dialbanner {
    border-bottom-left-radius: 2.5rem;
    background-color: #49274a00;
    width: 100%;
    padding-right: 0;
}
/* you shouldnt be able to see this element*/
#stats {
    color: #fff;
    font-weight: 700;
    font-size: 0px;
    align-content: center;
    padding-left: 10px;
    padding-top: 10px;
    text-align: center;
    letter-spacing: .2px;
    color: #000000
}
.dropdown {
    color: #fff;
    background-color: #f9f9f900;
    border-color: #49274a;
    width: 100px;
    height: 30px;
}
.dtmf-interface li {
    float: left;
    margin: 10px;
    list-style: none;
    width: 80px;
    height: 80px;
    text-align: center;
    line-height: 42px;
    font-size: 32px;
	border: solid 2px #ff7800;
    border-radius: 3px;
    padding: 15px;
    transition: all .5s;

}
.container {
    max-width: 1400px;
}
.display-3, .display-2, .display-1 {
    font-size: 4.5rem;
    font-weight: 300;
    line-height: 1.3;
    margin: auto;
}
.custom-select {
     color: #ff7800;
     background-color: #49274a7d;   
}
.a {
    color: #000000;
    background-color: transparent;
}
.footer_w3layouts_section_1its {
    background: #00000000;
}
.cpy-right {
    background: #00000000;
}
.action:hover {
    z-index: 1;
    color: #8492a6;
    text-decoration: none;
    background-color: #00000066;
}
section {
    background-color: #f8f8fb00;
}
.bg-light {
    background-color: #00000000!important;
}
.quote h4 {
    font-size: 34px;
    font-weight: 600;
    color: #a0a0a0;
    text-transform: capitalize;
    display: inline-block;
    letter-spacing: 0;
    line-height: 50px;
    width: 80%;
    float: left;
}
.process-grids h4 {
    font-size: 34px;
    font-weight: 600;
    color: #a0a0a0;
}
h3.heading {
    font-size: 45px;
    font-weight: 600;
    color: #a0a0a0;
    text-transform: capitalize;
    letter-spacing: 0;
}
h5.mb-0 {
    color: #a0a0a0;
}

.bg-dark {
    background-color: #00000000!important;
}
.phone-icon :hover {
    background: #ff7800;
    color: #000;
}

.navbar .dropdown-menu {
    min-width: 2rem;
    background-color: #0000;
    border: 0 solid transparent;
    border-radius: .9rem;
    -webkit-box-shadow: 0 10px 30px 0 rgba(31,45,61,.1);
    box-shadow: 0 10px 30px 0 rgba(31,45,61,.1);
    margin-left: 0px;
}

.dropdown-menu, .dropdown-item {
    color: #ff7800;
    background-color: #000000;
}

.text-body, .text-muted {
    color: #49274a!important;
    font-weight: bold;
}
.card-body {
    -webkit-box-flex: 1;
    -ms-flex: 1 1 auto;
    font-weight: bold;
    flex: 1 1 auto;
    padding: 1.5rem;
    color: #49274a;
}

.process-grids h4 {
    font-size: 34px;
    font-weight: 600;
    color: #ff7800;
}
.process-grids p {
    font-size: 16px;
    color: #b35400;
}
h5.mb-0 {
    color: #ff7800;
}
.navbar-expand-lg .navbar-collapse .dropdown-item {
    font-size: 2rem;
    font-weight: 600;
    background-color: #0000;
    margin-left: -0px;
    margin-bottom: -1rem
}
.navbar .dropdown-item {
    padding: .2rem;
    font-size: 1.5rem;
}
}
