@-moz-document url-prefix("https://account.microsoft.com"), url-prefix("https://account.microsoft.com/rewards/") {
body,
p,
html > body,
#page,
input,
.shell-header,
div#tocnav div.tocselected,
#BodyBackground, #Search,
#instantAnswer .examples .code,
.code, #simplemodal-container,
#ux-footer, #MainContent,
th, #Fragment_HeroHeadlines,
.heropanel, div#ux-footer,
.ResultItemSourceRefinement,
.inner, #breadcrumbDropDownMenu,
#breadcrumbDropDownMenu>ul>li.current,
#JelloWrapper,
.tabHeaders div,
.tabHeaders .current,
#bodyContainer,
table #mainContentTable,
#sideNav,
#directoryList,
.gallery-home-page-view .tab-collection,
.homepage-searchbox-wrapper .homepage-searchbox-container,
.gallery-item-card,
.category-entry,
.item-details-main-content,
.searchbar,
#msdn-technet-en-thread #threadPageContainer li.message div.menu.thread,
#msdn-technet-en-thread #threadPageContainer li.message div.menu.message,
div.expandedThreadContainer li.message div.menu.thread,
div.expandedThreadContainer li.message div.menu.message,
.hljs,
.section div.alert,
li.message,
.or-t-ml .or-c-pf,
.toc .selectedHolder>ul,
.codeHeader,
#ux-header header div.toc nav ul.navL2,
#ux-header header div.toc nav ul.navL3 {
    background-color: #181818 !important;
    background: #181818 !important;
    color: #fff;
}
.c-uhfh>div:first-child .c-uhf-nav-link {
    color: #fff;
}
/*Barre Navigation*/
.c-uhfh>div:first-child {
    background-color: #181818;
}
/*Barre Navigation Cote*/
.context-uhf *, .context-uhf *:before, .context-uhf *:after {
    color: #fff;
}
/*Bouton Compte*/
.c-uhfh>div:first-child .c-uhf-menu a, .c-uhfh>div:first-child .c-uhf-menu button {
    color: #fff;
}
/*Bouton Paiement et facturation*/
.c-uhfh.c-sgl-stck .c-uhfh-gcontainer-st {
    background-color: #181818;
}
/*Case Menu*/
button.c-action-trigger.c-glyph[aria-label], a.c-action-trigger.c-glyph[aria-label] {
    color: #fff;
}
/*Bouton Menu*/
.mectrl_root *, .mectrl_root div * {
    background-color: #181818;
}
/*Bouton Compte*/


/*Page Principal Debut*/
    
    mee-banner .amc-banner .banner-padded-content {
        padding-top: 20px;
        padding-bottom: 20px;
        background-color: #181818;
    }
    /*Banniere*/
    mee-banner .amc-banner {
        background: linear-gradient(to bottom,#181818 0,#181818);
        box-shadow: 0 12px 45px -8px rgba(0,120,215,.35);
    }
    /*Cote Banniere n2*/
    main#rewardsAngular {
        color: #fff;
        background-color: #181818;
    }
    /*Bordures*/
    nav.rewardsNavigation .c-pivot .f-active {
        color: #fff;
        border-bottom: 2px solid #0067b8;
    }
    /*Bouton Gagner*/
    nav.rewardsNavigation .c-pivot a  {
        color: #fff;
    }
    /*Autres Boutons*/
    .m-card-group .c-card .c-card-content:before {
        outline: 1px solid #0067b8;
        outline-offset: -1px;
        background-color: #181818;
    }
    /*Top Bordure Case*/
    .c-heading-5, .c-paragraph-1 {
        font-size: 0px;
        line-height: 0px;
    }
    /*Lignes Texte Fidelite Activites*/
   .c-carousel.f-multi-slide.f-scrollable-previous, .c-carousel.f-multi-slide.f-scrollable-next {
    -ms-touch-action: pan-y;
    touch-action: pan-y;
    -webkit-user-select: none;
    -webkit-user-drag: none;
    -webkit-tap-highlight-color: transparent;
    visibility: hidden;
    height: 0px;
    }
    /*Offres fidelite*/

/*Page Principal Fin*/

/*Popup Points Debut*/
    .c-dialog.f-flow [role="dialog"] {
        -webkit-backface-visibility: hidden;
        background-color: #181818;
        color: #fff;
    }
    /*Page Générale*/
    .circleContainer.medium .overlayCircle {
        background-color: #181818;
    }
    /*Fonds Logos*/
    .c-dialog.f-flow [role="dialog"]>.c-glyph:before, .c-dialog.f-lightbox [role="dialog"]>.c-glyph:before {
        color: #fff;
    }
    /*Bouton Fermeture*/

/*Popup points Fin*/

/*Page Redeems Debut*/
       #main-content-landing {
        background: #181818;
    }
    /*Page Redeems Body*/
    #rewards-redeem-catalog[data-grid~="container"] {
        background-color: #181818;
        color: #fff;
    }
    /*Page générale*/
    #rewards-redeem-catalog[data-grid~="container"] .c-pivot #reward_pivot_redeem span {
        color: #fff;
    }
    /*Bouton Utiliser*/
    .c-drawer>button, .c-drawer .f-toggle, .c-drawer>header>button, .c-drawer>header .f-toggle, .c-drawer .c-drawer-toggle {
        background: #181818;
    }
    /*Bouton Afficher et Tri*/
    button.c-refine-item, a.c-refine-item {
        color: #fff;
        background-color: #181818;
    }
    /*Boutons Tri*/
    ol, ul {
        background-color: #181818;
    }
    /*Bordures Entre Cases*/
    #redeemRewards .nav-links li a.c-hyperlink, #redeem-set-goal .nav-links li a.c-hyperlink {
        color: #fff;
    }
    /*Boutons Code*/

/*Page Redeems Fin*/

/*Page Compte Debut*/

    mee-media [media-heading] {
        color: #fff;
    }
    /*Cases appareils et facturation*/
    .help-footer {
        background-color: #181818;
    }
    /*Cote Footer*/

/*Page Compte Fin*/


.c-uhff-base {
    background-color: #181818;
}
/*Footer*/
.c-uhff {
    background: #181818;
}
/*Cote Footer*/



