css:
/* i really want this to be global */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("client.wedos.com") {
/*
*    @title   WEDOS Dark Arc Green
*    @url     https://client.wedos.com
*    @version	v1.0 [2019/08/10]
*    @fork		https://userstyles.org/styles/138230/wedos-dark-client
*    @theme		https://github.com/go-gitea/gitea-darkmode
*/

h2 {
	border-bottom: 1px solid #383C4A;
}

#header a img{
  top:16px;
  height:100px;
	content: url('data:image/png;base64,');
}

#header {
    background-color: #383C4A;
    background-image: none;
}

#wrapper, .wrap {
    background-color: #383C4A;
    background-image: none;
}

body, td {
  color: #9e9e9e;
}
#header {
	margin-top: -1em;
}
#site-actions {
	position: relative;
	padding-top: 8.5em;
  margin-left: 3em;
}
a,
a:visited,
.list_title a, .list_title a:visited {
  text-decoration: none;
  color: #87ab63;
  position: relative;
}
a:focus,
a:hover,
.list_title a:focus, .list_title a:hover {
  text-decoration: none !important;
  color: #a0cc75;
  transition: all 0.3s ease;
  cursor: pointer !important;
}
h1, h2, h3, h4 {
  color: #dbdbdb;
}

/* disable dotted border to actived links */
/* does not apply to images MUST HAVE class="image" */
a.image:before {
  content: none !important;
  cursor: pointer;
}
a,
a:hover,
a:focus,
a:active {
  outline: none;
  transition: all 0.3s ease;
}
a,
button,
a:link,
a:visited,
a:hover,
a:focus,
a:active {
  outline: none !important;
  border-style: none;
  vertical-align: middle;
  -ms-interpolation-mode: bicubic;
}
button,
input {
  outline: none !important;
  border-style: none;
  vertical-align: middle;
}
input[type=text], input[type=tel], input[type=number], input[type=password] {
  color: #9e9e9e;
  background: #404552 !important;
  border: 2px solid #353945;
  border-radius: 3px !important;  
}
input[type=text]:focus, input[type=tel]:focus, input[type=number]:focus, input[type=password]:focus {
  border: 2px solid #4b505f;
  color: #dbdbdb;
}

#menu {
  background: transparent;
  border: 0 none;
}
.wrap #content, .mbox .mwrap {
	background-color: #353945;
  border: 1px solid #404552;
  border-radius: 3px;
}
.wrap #content {
  border-top: 0 !important;
  padding-top: 6em;
  border-top-left-radius: 5px;
  border-top-right-radius: 5px;
}
#content h1 {
  background-color: #404552 !important;
  margin: 0 !important;
  position: absolute;
  left: 0;
  top: 0;
  right: 0;
  padding: .5em 1em;
  border: 1px solid #404552;
  border-bottom-left-radius: 0 !important;
  border-bottom-right-radius: 0 !important;
  border-radius: 5px;
}
.mbox .mwrap {
  margin: 0;
}
.mbox {
  background-color: #404552;
  padding-top: 1px;
  margin-bottom: 10px;
  border-radius: 5px;
}

table {
	border: 0 none;
}
td {
	border-bottom: 0 none;
}

#nav ul li.current a, #nav ul li.current a span  {
	background-image: none;
}

.list_div {;
  border-width: 0;
}

.list_nav {
  margin: 0;
  padding: 10px;
  background-color: #353945;
}

.list_nav form table,
.list_nav form table td {
	border-width:0 !important; 
} 

fieldset {
  border: 1px solid #4c505c;
  border-radius: 3px;
}

/* input button */
/*   background: #404552 !important; */
/* disabled button */
/*   color: #797979 !important; */
input, select {
  background: #383C4A !important;
  border: 1px solid #4c505c;
  border-radius: 3px;
  padding: .5em;
  margin: 1px;
  color: #9e9e9e;
}
input:hover, select:hover {
  color: #dbdbdb !important;
  cursor: pointer;
}

.ibox li a {
    font-size: 11px;
    color: #ccc;
    height: 92px;
    width: 92px;
    display: block;
    background: #232323 none 0 0 no-repeat;
    border: 4px solid #232323;
    border-radius: 5px;
}

.ibox li a:hover {
    color: #ccc !important;
    background-image: none !important;
    background-color: #383C4A !important;
    border: 4px solid #383C4A;
}

#nav,
.news li,
.news li.even {
  background-color: #2A2E3A;
  padding: 0px;
  width: 100%;
}
#nav ul {
  margin-left: 1em;
}
#nav li {
	padding: 0px 13px;
  border: 1px solid transparent;
  border-bottom: 0 none;
  margin-right: .2em;
}
#nav li:hover {
	background-color: #383C4A;
  color: #dbdbdb;
  margin-top: 3px;
  border: 1px solid #404552;
  border-radius: 3px;
  border-bottom-right-radius: 0px;
  border-bottom-left-radius: 0px;
  border-bottom: 0 none;
}
#nav ul li:hover a span {
  background-color: #383C4A;
	color: #dbdbdb;
  padding-right: 0px;
  border-top: 1px solid #404552;
  margin-top: -1px;
  text-decoration: none;
}
#nav ul li.current{
  background-color: #383C4A;
  padding: 0px;
  border: 1px solid #404552;
  border-radius: 3px;
  border-left: 0 none;
  border-bottom-right-radius: 0px;
  border-bottom: 0 none;
  margin-top: 3px;
}
#nav ul li.current a span {
  background-color: #383C4A;
	color: #87ab63;
  padding-right: 0px;
  border: 1px solid #404552;
  border-radius: 3px;
  border-right: 0 none;
  border-bottom-left-radius: 0px;
  border-top-right-radius: 0px;
  border-bottom: 0 none;
  margin-top: -1px;
}
.lookup_bar {
  position: absolute;
  top: 137px;
  padding: 8px;
}
.lookup_bar input {
  color: #dbdbdb;
  background: #404552 !important;
  border: 2px solid #353945 !important;
  border-radius: 3px !important;
}
.lookup_bar input:focus {
  border: 2px solid #4b505f !important;
}

/* Tables */
.frm_table, .rtable {
  background-color: #353945;
  border: 1px solid #404552 !important;
  border-radius: 3px;
  padding: 0 !important;
  margin: 0 !important;
  border-top-left-radius: 5px;
  border-top-right-radius: 5px;
}
.frm_table td, .rtable {
  padding: .2em .5em;
}
.frm_title, .rtable_header td,
.box-header, .box-header h2 {
  background-color: #404552;
  color: #dbdbdb !important;
  border: 1px solid #404552 !important;
  border-radius: 5px;
  border-bottom-left-radius: 0;
  border-bottom-right-radius: 0;
}
.box-header h2 {
  margin: 0;
  padding-left: .5em;
}
.list_header {
  background-color: transparent;
}
.list_row_2,
.list_header tr,
.list_title,
.frm_subtitle {
  background-color: #404552;
  box-shadow: none;
  text-shadow: none;
}
.frm_v, .frm_i {
  border: 0 none !important;
}
.list_header td {
  border: 0 none;
}
.list_header_row td {
  color: #dbdbdb;
  padding: .5em 0 .5em .3em;
  border: 0 none;
	border-right: 1px solid rgba(34,36,38,.1);
}
.list_header_row td:last-child {
	border-right: 0 !important;
}
.list_header_row td:first-child {
  border-radius: 5px 0 0 0;
}
.list_header_row td:last-child {
  border-radius: 0 5px 0 0;
}
.list_header_row td:only-child{
  border-radius: 5px 5px 0 0;
}
.list_data,
.box-content,
.box-header + span {
  border: 1px solid #404552 !important;
  border-radius: 3px;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
}
.list_data,
.list_data tr.list_row_1,
.list_data tr.list_row_2 {
  background-color: #2A2E3A;
  border-width: 0px;
	border-bottom: 1px solid rgba(34,36,38,.1);
}
.list_data td:first-child {
  padding-left: .5em;
}
.list_data td {
  padding: .5em 0;
}
.list_data td,
.list_data tr.list_row_2 td {
	border-right: 1px solid rgba(34,36,38,.1);
} 
.list_div .list_row_1:hover, .list_div .list_row_2:hover {
  background-color: #393d4a;
}

/* Dashboard */
.box-content,
.box-header + span {
  margin-bottom: .5em;
  background-color: #2A2E3A;
  padding: 0 1em;
}
.box-header + span {
  display: block;
  padding: .5em;
}
.box-content li {
  margin-left: 0;
  margin-right: 1em;
}
.box-content li a {
  background-color: #262831;
  border: 1px solid #262831;
}
.box-content li a:hover {
  background-color: #1b1c23 !important;
  border: 1px solid #1b1c23;
}
.box-content li a span {
  transition: all 0.3s ease;
}
.box-content li a:hover span {
  color: #a0cc75;
}


.news li {
  border-bottom: 1px solid rgba(34,36,38,.1);
}
.news li:last-child {
  border: 0;
}
.news li a {
  background-color: transparent !important;
  border: 0 !important;
  width: 300px;
  display: inline-block;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  padding: .2em 0;
  vertical-align: text-top;
}
.news li a:hover {
  background: transparent !important;
  color: #a0cc75 !important;
}
.news span {
  float: right;
}

/* Modal */
.gui-modal-wrapper {
  background: transparent !important;
  color: #dbdbdb !important;
/*   border: 1px solid #404552 !important; */
  border-radius: 5px;
  border-bottom-left-radius: 0;
  border-bottom-right-radius: 0;
}
.gui-modal-wrapper-title {
  background: #404552;
}
.gui-modal-wrapper-content {
  border: 0 none !important;
  border-radius: 3px;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
}
#content pre {
  background-color: #2A2E3A;
  border: 1px solid #404552 !important;
  border-radius: 5px;
  word-wrap: break-word;
  white-space: pre-wrap;
}

#chat-support {	
    top: 37px;
    height: 41px;
    padding: 0;
    color: #000;
    background: #609926;
    background: -webkit-gradient(linear, 0 0, 0 100%, from(#609926), to(#a0cc75));
    background: -moz-linear-gradient(#609926, #a0cc75);
    background: -o-linear-gradient(#609926, #a0cc75);
    background: linear-gradient(#609926, #a0cc75);
    -webkit-border-top-left-radius: 50px 50px;
    -webkit-border-top-right-radius: 50px 50px;
    -webkit-border-bottom-right-radius: 50px 50px;
    -webkit-border-bottom-left-radius: 50px 50px;
    -moz-border-radius: 50px / 50px;
    border-radius: 50px / 50px;
}
#chat-support a:hover {	
    color: #eee;
    text-decoration: none;
}
#chat-support:before {
    content: "";
    position: absolute;
    bottom: -30px;
    right: 40px;
    border-width: 0 0 30px 50px;
    border-style: solid;
    border-color: rgba(0, 0, 0, 0) #a0cc75;
    display: block;
    width: 0;
}
#chat-support:after {
    content: "";
    position: absolute;
    bottom: -30px;
    right: 70px;
    border-width: 0 0 30px 20px;
    border-style: solid;
    border-color: rgba(0, 0, 0, 0) #383C4A;
    display: block;
    width: 0;
}
#chat-support a {
    display: block;
    height: 22px;
    width: 221px;
    line-height: 12px; 
    text-decoration: none;
    text-align: center;
    margin: -15px 0px;
    padding: 31px 0px;
    font-size: 15px;
    font-weight: bold;
    color: #333;
}

#client-service {
    width: 411px;
    height: 80px;
    background: #609926 none 0 0 no-repeat;
    background: #609926;
    background: -webkit-gradient(linear, 0 0, 0 100%, from(#609926), to(#a0cc75));
    background: -moz-linear-gradient(#609926, #a0cc75);
    background: -o-linear-gradient(#609926, #a0cc75);
    background: linear-gradient(#609926, #a0cc75);
}
#client-service input{
	border-width: 0;
}
#client-service p{
	color: #eee;
}
#client-service p a{
	color: #232323;
    text-decoration: none;
}
#client-service p a:hover{
	text-decoration: none;
  color: #eee;
}
#backlinks a {
  color: #9e9e9e;
  text-decoration: underline;
}
#footer {
	background-color: #2E323E;
  background-image: none;
  color: #888;
  padding: 5px 15px 40px;
}
.info_blue, .info_bar {
  color: #9e9e9e;
  border: 1px solid #404552;
  border-radius: 3px;
  background-color: #404552;
}

}

#info_bar_client_top {
  height: 100% !important;
}
.frm_button {
	background-color: transparent !important;
}
#client-service label {
  padding: 6px 0;
}
#client-service input {
	border: 0 none !important;
}
#client-service {
	height: auto;
  margin-top: -1em;
}
#client-service div {
  padding: 15px 0 5px;
}

.lookup_bar img {
  filter: invert(70%);
}

.kb_help img,
#h1_help img {
  filter: invert(80%);
}
/* hide bullshit */
#footer img,
#footer #copy,
.adsys {
  display: none;
}

/* wide screen tables */
.list_div,
.list_div table,
.list_div .list_data {
  width: 100% !important;
}

.status_active {
  font-weight: 600;
}

.folder-list-manage-link {
  background: transparent !important;
}
.folder-list-manage-link a, .folder-list-manage-link a:visited {
  color: #87ab63 !important;
}
.folder-list-manage-link a:hover, .folder-list-manage-link a:focus {
  color: #a0cc75 !important;
  transition: all 0.3s ease;
}

#content a {
  color: #87ab63;
}
hr {
  display: none;
}
