css:
@-moz-document url-prefix("https://www.brick-hill.com/") {
*
  {
  }

element.style {
    border-radius: 5px;
}
 body, html {
     background-image: url('https://cdn.discordapp.com/attachments/582605365145894922/610398123847843851/background3.png');
     background-position: top;
     margin: auto;
     margin-top: auto;
     margin-top: 10px;
     width: 1200px;
 }
 nav li {
     bottom: 20px;
 }
 nav .primary li {
     bottom: 20px;
     font-weight: 700;
     font-size: 16px;
 }
 nav li a:not(.active):hover {
     text-decoration: underline;
     border-color: transparent;
 }
 nav li a {
     padding: 8px 5px;
     padding-right: 0;
     padding-left: 0;
 }
 nav li:first-child {
     padding-left: 10px;
 }
 nav {
     width: 900px;
     margin: auto;
     margin-left: auto;
     margin-right: auto;
     margin-top: 50px;
     margin-top: 0px;
 }
.card .top:not(.absolute).blue {
    background-image: url('https://web.archive.org/web/20120106111125im_/http://www.roblox.com/images/cssspecific/rbx2/tab_black_33h_t2.png');
    height:33px;
}
 .primary, .secondary {
     background-color: transparent;
 }
 nav div.primary {
     background-color: transparent;
     background-image: url('https://cdn.discordapp.com/attachments/582605365145894922/609843369551593473/robloxbanner.png');
     background-size: 100%;
     padding-top: 18%;
     overflow: hidden;
     background-repeat: no-repeat;
 }
 nav div.primary div.grid {
 width:900px;
 height:30px;
 background:url(https://web.archive.org/web/20120206150139im_/http://www.roblox.com/images/cssspecific/rbx2/topNav_blue.png);
 background-position:0 0;
 line-height:20px;
}
 nav div.primary div.grid li :hover{
 background:url(https://web.archive.org/web/20120206150139im_/http://www.roblox.com/images/cssspecific/rbx2/topNav_blue.png);
 background-position:0 30px;
}
.bucks-icon,
.bits-icon,
.friends-icon,
.messages-icon {
    background-image: url('http://i.imgur.com/2ow3ncT.png');
}
.bucks-icon {
    background-position: -2px -42px;
}
.bucks-icon:hover {
    background-position: -2px -61px;
}
.info #bucks-amount a.header-data {
    color: #080 !important;
}
.info #bucks-amount a.header-data:hover {
    color: #0d0 !important;
}
.bits-icon {
    background-position: -2px -123px;
}
.bits-icon:hover {
    background-position: -2px -141px;
}
.info #bits-amount a.header-data {
    color: #a61 !important;
}
.info #bits-amount a.header-data:hover {
    color: #fb6 !important;
}
.messages-icon {
    background-position: -2px -3px;
}
.messages-icon:hover {
    background-position: -2px -20px;
}
.info div:nth-of-type(3) a.header-data {
    color: #444 !important;
}
.info div:nth-of-type(3) a.header-data:hover {
    color: #aaa !important;
}
.friends-icon {
    background-position: -2px -82px;
}
.friends-icon:hover {
    background-position: -2px -81px;
}
.info div:nth-of-type(4) a.header-data {
    color: #05b !important;
}
.info div:nth-of-type(4) a.header-data:hover {
    color: #0cf !important;
}
.img-white {
    -webkit-filter: unset;
}
.main-holder.grid {
    margin-top: 20px;
}
.username {
    float: left;
    color: white;
    text-align: left;
    font-size: 13px;
    font-weight: 600;
    position: absolute;
    top: 130px;
    left: 2%;
}
 nav .username {
     display:inline-block;
     margin:6px 0 0 28px;
     cursor:pointer;
     left: 330px;
 }
.arrow-down {
     color: transparent;
}
 .bottom-bar {
     color: white;
     text-align: center;
     border-top: none;
     background-image: url('https://cdn.discordapp.com/attachments/582605365145894922/610025376290439168/bottombar.png');
     line-height: 0.8;
     font-size: 16px;
     margin-top: -5px !important;
     height: 30px;
 }
.bottom-bar li :hover{
 background:url(https://cdn.discordapp.com/attachments/582605365145894922/610025376290439168/bottombar.png);
 background-position:0 30px;
}
 .secondary .grid {
     padding-top: 4px;
 }
 .primary #info {
     float: none;
     line-height: 20px;
 }
 nav .info {
     border-radius: 0;
     padding: 0px 10px 5px;
     background-image: url('https://web.archive.org/web/20120320185807im_/http://www.roblox.com/images/cssspecific/rbx2/head_bkg_t2.png');
     display: inline-block;
     width: 280px;
     top: 110px;
     position: absolute;
     right: 150px;
 }
.card .top:not(.absolute), .card .top:not(.absolute).blue, .card .top:not(.absolute).green, .card .top:not(.absolute).orange, .card .top:not(.absolute).red {
    background: url(https://cdn.discordapp.com/attachments/582605365145894922/615834552287494154/BlackBarRBX.png) no-repeat right top;
    height: 30px;
    color: #fff !important;
    font-family: Arial,Helvetica,sans-serif;
    font-weight: bold;
    font-size: 20px;
}
}

@-moz-document url("https://www.brick-hill.com/dashboard/"), url("https://www.brick-hill.com/play/") {
.card .top:not(.absolute).blue {
    background-image: url('https://web.archive.org/web/20120106111125im_/http://www.roblox.com/images/cssspecific/rbx2/tab_black_33h_t2.png');
    height:33px;
    color:#fff;
}
.card .top:not(.absolute).orange {
    background-image: url('https://web.archive.org/web/20120106111125im_/http://www.roblox.com/images/cssspecific/rbx2/tab_black_33h_t2.png');
    height:33px;
    color:#fff;
}
.card .top:not(.absolute).red {
    background-image: url('https://web.archive.org/web/20120106111125im_/http://www.roblox.com/images/cssspecific/rbx2/tab_black_33h_t2.png');
    height:33px;
    color:#fff;
}
}

@-moz-document url("https://www.brick-hill.com/play/") {
.button.blue, button.blue {
    color: transparent;
    background-image: url('https://web.archive.org/web/20120106110528im_/http://www.roblox.com/images/PlayThis.png?v=2');
}
.card .top:not(.absolute).blue {
    color: #fff;
}
footer {
color: #e6e6e6;
 }
}
