css:
@-moz-document domain("joanasworld.com") {
#content {
    background-color: #222 !important;
    border: solid 1px #333 !important;
    border-radius: 0 !important;
    box-shadow: none !important;
    margin-top: 30px !important;
    padding-top: 10px !important;
}

#fb-box + p + div a {
    color: #F5A83B !important;
}

body {
    font-size: 0.5rem !important;
}

#left-side a, #footer a, .nextpage-txt a, .bottom-menu-container a, #author-date a {
    color: #F5A83B !important;
}

#pagetype, #author-date {
    font-style: normal !important;
}

#top h2 a {
    color: #fff !important;
}

.highlight {
    background-color: #222 !important;
}

.lvl-zone {
    display: block !important;
    margin-bottom: 5px !important;
    margin-top: 30px !important;
}

.membermenu li a, .guidemenu a {
    background-color: #111 !important;
    color: #F5A83B !important;
}

.steps-container > label:after {
    background-color: #333 !important;
    content: "" !important;
    display: block !important;
    height: 1px !important;
    margin-bottom: -8px !important;
    margin-top: 10px !important;
}

body * {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol" !important;
}

h2 span {
    background-color: #F5A83B !important;
    margin-top: -8px !important;
}

label.completed-step {
    text-decoration: line-through !important;
}

#left-side * {
    border: none !important;
    padding-left: 0 !important;
    margin-left: 0 !important;
    text-align: left !important;
}

#left-side img {
    display: none !important;
}

#content > p, #page-heading, #author-date, .membermenu {
    display: none !important;
}

#fb-box ~ * {
    display: none !important;
}

.tar-button {
    background: linear-gradient(to bottom, #333 5%, #555 100%) !important;
    border: solid 1px #FF8C00 !important;
    box-shadow: none !important;
    transform: scale(1.2) !important;
    margin-left: 5px !important;
    margin-right: 5px !important;
}

#top h2 {
    display: none !important;
}


}
