#WikiaBarWrapper,.wds-global-navigation-wrapper,.WikiaTopAds,#WikiaRailWrapper,.page-header__contribution{
    display:none !important
}
body{
    background:none !important
}
.WikiaPage{
    background:none !important;
    border:0 !important
}
#WikiaMainContent{
    width: 100% !important;
    line-height: 3.6 !important;
}
#WikiaMainContent p,#WikiaMainContent li{
    line-height:inherit !important;
}
#mw-content-text{
    color:#50585C !important
}
a{color:#2393e8 !important}
body{
 font-family: "PT Serif", "Verdana", serif !important;   
}

.WikiaPage .WikiaArticle{
    font-size:18px;
    line-height:28px;
}

h2 {
    color: #515151 !important;
    
}

h2, h3, #firstHeading {
    font-family: "PT Sans Narrow", "Helvetica", "Arial", sans-serif !important;
    font-weight: bold;
    
}
#mw-content-text h2{
    line-height: 1.5;
    font-size:28px !important;
    font-family: "PT Sans Narrow", "Helvetica", "Arial", sans-serif !important;
    font-weight: bold;
}
