/* Select menus */
.SelectMenu-modal{
	background: var(--bg4)
}
@media (min-width: 544px){
	.SelectMenu-modal {
		border: 1px solid var(--bg4);
	}
}
.SelectMenu-tab[aria-selected=true] {
    color: rgba(255, 255, 255, 0.7);
    background-color: var(--bg3);
    box-shadow: 0 0 0 1px var(--bg4);
}
@media (min-width: 544px) {	
	.SelectMenu-tab[aria-selected=true] {
		border-color: var(--bg3);
	}
}
@media (hover: hover) {
	.SelectMenu-tab:not([aria-checked=true]):active, .SelectMenu-tab:not([aria-checked=true]):hover {
		background-color: var(--bg6);
		color: rgba(255, 255, 255, 1);
		border-color: var(--bg6);
	}
}
.SelectMenu-item {
    color: rgba(255,255,255,0.7);
    background-color: var(--bg3);
    border-bottom: 1px solid var(--bg4);
}
.SelectMenu-list {
	background-color: var(--bg3);
	border-top: 1px solid var(--bg3);
}
.SelectMenu-filter {
    border-top: 1px solid var(--bg3);
}
@media (hover: hover){
	.SelectMenu-item:active {
		background-color: var(--bg5);
	}
}
@media (hover: hover){
	.SelectMenu-item:hover, body:not(.intent-mouse) .SelectMenu-item:focus {
		background-color: var(--bg5);
	}
}
.SelectMenu-blankslate, .SelectMenu-loading, .SelectMenu-message {
    padding: 24px 16px;
    text-align: center;
    background-color: var(--bg2);
}

.SelectMenu-message {
    border-bottom: 1px solid var(--bg3);
}
.SelectMenu-divider {
    color: rgba(255,255,255,0.6);
    background-color: var(--bg2);
    border-bottom: 1px solid var(--bg1);
}
.Label--gray {
    color: #eaecef;
    background-color: #586069;
}
.form-control.focus, .form-control:focus, .form-select.focus, .form-select:focus {
    box-shadow: inset 0 1px 2px var(--bg2), 0 0 0 0.2em var(--btn3);
}
.reaction-sort-item:focus, .reaction-sort-item:hover {
    background-color: var(--selected2);
}
.discussion-item-icon {
    color: rgba(255, 255, 255, 0.6);
}
.repository-content .commit-group-title .octicon-git-commit {
    background: var(--bg3) !important;
}
.dropdown-divider {
    border-top: 1px solid var(--bg4);
}
.jump-to-suggestions-results-container .navigation-item {
    border-bottom: 1px solid var(--bg4);
}
.autocomplete-item.navigation-focus,.autocomplete-item.selected,.autocomplete-item:hover,.autocomplete-item[aria-selected=true],
.suggester li.navigation-focus,.suggester li:hover,.suggester li[aria-selected=true],
.select-menu-item.navigation-focus,.select-menu-item.navigation-focus .description-inline,.select-menu-item.navigation-focus.select-menu-action,.select-menu-item.navigation-focus.selected,.select-menu-item.navigation-focus[aria-checked=true],.select-menu-item:focus,.select-menu-item:focus .description-inline,.select-menu-item:focus.select-menu-action,.select-menu-item:focus.selected,.select-menu-item:hover,.select-menu-item:hover .description-inline,.select-menu-item:hover.select-menu-action,.select-menu-item:hover.selected,.select-menu-item[aria-checked=true]:focus,.select-menu-item[aria-checked=true]:hover,.select-menu-item[aria-selected=true] .description-inline,.select-menu-item[aria-selected=true].select-menu-action,.select-menu-item[aria-selected=true]:focus,.select-menu-item[aria-selected=true]:hover,
.integrations-install-target .navigation-focus.private,.integrations-install-target [aria-selected=true].private,
.card-filter-autocomplete-dropdown .navigation-focus,.card-filter-autocomplete-dropdown [aria-selected=true],
.projects-reset-query:hover .projects-reset-query-icon,
.typeahead-result.navigation-focus,.typeahead-result:hover,.typeahead-result[aria-selected=true],
.jump-to-suggestions-results-container .navigation-focus .jump-to-suggestions-path,.jump-to-suggestions-results-container [aria-selected=true] .jump-to-suggestions-path{
    background: var(--selected2) !important;
}

/* PULSE */
.code-frequency .addition {
    fill: #2cbe87 !important;
}
.code-frequency .deletion {
    fill: #cb244b !important;
}
.cadd, .summary-stats li .octicon-issue-opened, .summary-stats li .octicon-git-branch, .authors-and-code .insertions{
	color: #2cbe87 !important;
}
.cdel, .summary-stats li .octicon-issue-closed, .authors-and-code .deletions{
	color: #cb244b !important;
}
.bg-green, .State--green {
	background: #2cbe87 !important;
}
.bg-red, .State--red {
	background: #cb244b !important;
}
.summary-stats li, .summary-stats li .num, ul.summary-stats li a,.diffstat-summary {
    color: rgba(255,255,255,0.6) !important;
}
.diffstat-summary strong {
    color: rgba(255, 255, 255, 0.8);
}
.graph-canvas text {
	fill: rgba(255,255,255,0.6) !important;
}
.simple-conversation-list>li {
    border-top: 1px solid var(--bg4);
}
.community-checklist .progress {
    background-color: var(--bg5);
}
.community-checklist .Box-row {
	border-top: 1px solid var(--bg3) !important;
}
.graph-canvas .axis line {
    stroke: var(--bg5);
}
.network .current-repository {
    background-color: hsla(52, 100%, 50%, 0.15);
}
.pagination-loader-container {
    background-color: var(--bg3);
    background-image: url(data:image/png;base64,);
}
.Box-row--gray {
    background-color: var(--bg1);
}
}
