style_setting_options.value:
/*Boutons Like*/

/*Like de post*/
 ._666k ._8c74 {
    margin : auto;
    overflow : visible;
}

._666k div a[data-testid='UFI2ReactionLink']::after {
    position : absolute;
    top : 10px;
    font-size : 13px;
}

._666k div a[data-testid='UFI2ReactionLink'] {
    font-size : 0;
    margin-left:10px;
}
/*icone de la reac selectionnee*/
a[data-testid='UFI2ReactionLink'] span i{
	visibility: visible;
	position: relative;
	left : -15px;
}

a[data-testid='UFI2ReactionLink'] span{
	margin-left: 0;
	margin-right: auto;
    left : -25px;
}
/*icone du like*/
.sx_9c2700::before, ._6rk1::before{
    content: "";
    background-image: url('https://image.noelshack.com/fichiers/2019/33/1/1565603313-68381676-863907300657042-4957720151802773504-n.png');
    visibility: visible;
    background-size: 30px 30px;
    display: inline-block;
    width: 30px;
    height: 30px;
    left:-70px;
    top: -10px;
    position : relative;
}
._6rk1::before{
    left:-50px;
    top: -5px;
}


/*like de commentaires*/
 ._8c74 {
    max-width : 16ch;
    overflow : hidden;
    white-space: nowrap;
    top : 4px;
}


/*Tous les like*/
 a[data-testid='UFI2ReactionLink'], ._6qw6  a._3_16 {
    visibility: hidden;
    position : relative !important;
    padding-right : 10ch !important;
}

._6coi {
    line-height : unset;
}

a[data-testid='UFI2ReactionLink']::after {
    content:"Je Schtroumpfe !";
    visibility: visible;
    overflow : visible;
    position : absolute !important;
    left : 0 !important;
    margin : auto;
}

a[data-testid='UFI2ReactionLink']:hover::after {
    text-decoration : underline !important;
}
