css:
@-moz-document domain("meneame.net") {
/* Cabecera */
.header-top-wrapper {
    background-color: #333bae;
}

ul#userinfo a.notifications span {
    color: #333bae;
    border: 3px solid #333bae;
}

.header-menu01 .selected a, .header-menu01 ul li a:hover {

    background: none;

}
.header-menu01 ul li a:hover {

    color: #333bae;
}

.header-menu01 ul li:hover, .header-menu01 ul li.selected {
    box-shadow: 0px -3px #333bae inset;
}


#notifier_panel div::before {
    color: #333bae;
}

.header-menu-wrapper {
    background-color: #FFF;
    border-bottom: 1px solid #D6D6D6;
}

/* fin cabecera */

/* textos con borde en nombre de subs */ 

.tool.sub-name .subname, .upname a:visited, .tool.sub-name .subname a:link {
    text-shadow: -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black;
}

/*fin textos con borde en nombre de subs */

/* cuerpo */
body {
    background-color: #e0e2f7;
}

.pages span.current {
    color: #FFF;
    background-color: #7308bd;
    border: solid 1px #7308bd;
}

.pages a:hover {

    color: #FFF;
    background-color: #7308bd;
    border-color: #7308bd;

}

.pages span.nextprev {

    border: 1px solid #F2C5FF;
    color: #E5C5FF;

}

.pages a {
    border: solid 1px #9442e8;

}
.pages a, .pages span {
    margin-right: 3px;
    color: #9442e8;
    background: #FFF;

}

.sidebox h4 a, .sidebox h4 span {
    color: #fff;

}

.sidebox .header {
    background-color: #ff935b;
    border-right: 1px solid #ff935b;
    border-top: 1px solid #ff935b;
    border-left: 1px solid #ff935b;
}

.sidebox .comments a:hover, .sidebox h5 a:hover {
    color: #333BAE;
}

.sidebox.brown .header {
    background: #dd4949;
    border-left: 1px solid #dd4949;
    border-right: 1px solid #dd4949;
}

.sidebox-rounded.orange .header {
    background: #6c31c4;
    border-left: 1px solid #6c31c4;
    border-right: 1px solid #6c31c4;
}

.sidebox .body {
    border-bottom: 1px solid #FF935B;
    border-left: 1px solid #FF935B;
    border-right: 1px solid #FF935B;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
    padding-top: 10px;
}

.sidebox.brown .body {
    border-bottom: 1px solid #dd4949;
    border-left: 1px solid #dd4949;
    border-right: 1px solid #dd4949;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
    padding-top: 10px;
}

.sidebox.red .body {
    border-left: 1px solid #C91223;
    border-right:  1px solid #C91223;
    border-bottom:  1px solid #C91223;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
    padding-top: 10px;
}

.sidebox .cell {
    border-bottom: 1px solid #C91223;
}

.sidebox-rounded .body {
    border-bottom: 1px solid #595959;
    border-left: 1px solid #595959;
    border-right: 1px solid #595959;
}

.sidebox .comments li {
    border-top: 1px solid #FF935B;
}

.sidebox .comments li:first-child {
    border-top: none;
}

/*.sidebox.brown .body .cell {
    border-top: 1px solid #dd4949;
}*/

.sidebox.brown .body .cell:first-child {
    border-top: none;
}

.sidebox .votes, .sidebox .clics {
    color: #dd4949;
    background-color: #FFF;
    border-radius: 50%;
}

.sidebox .votes span, .sidebox .clics span {
    border: 2px solid #dd4949;
    background-clip: padding-box;
    -webkit-border-radius: 15px;
    -moz-border-radius: 15px;
    border-radius: 15px;
    color: #e31414;
}

.sidebox-rounded div.finished {
    color: #f00;
    background-color: #fff;
}

.sidebox-rounded .show-responses {
    color: #f00;
}

#footcol1 {
    background: none;
}
/* fin cuerpo */

/* Notas */
.comment-header a {
    color: #333bae;
}

.comment.comment-avatar-large {
	background-color: #FFF5ED;
    border-radius: 5px;
}

.comment-footer a.comment-answers, .comment-footer a:hover {
    color: #8980ff;
}

.comment.high .comment-body, .comment.comment-avatar-large.high  {
    background: #B3ADFF url(https://mnmstatic.net/v_149/img/common/comment-high-01.png) 99% 5px no-repeat;
}

.comment-footer .separator-vertical {
    background-color: blue;
}

.comment.comment-avatar-large.high .comment-footer a, .comment.comment-avatar-large.high .comment-footer span, .comment.comment-avatar-large.high .comment-footer span i {
    color: blue;
}

.comment.comment-avatar-large.high .comment-footer a.vote.up:hover, .comment.comment-avatar-large.high .comment-footer a.vote.up.voted {
    color: green;
}

.comment.comment-avatar-large.high .comment-footer a.vote.down:hover, .comment.comment-avatar-large.high .comment-footer a.vote.down.voted {
    color: red;
}

.comment.user .comment-body {
    border:none;
}

.comment.user {
    border: 1px solid #000;
}
/* fin notas */

/* Meneos  */

.news-shakeit {
    color: #1419e3;
}

.news-shakeit.mnm-queued .votes, .news-shakeit.mnm-queued .clics {
    border-color: #3D42FF;
    background-color: #fff;
}

.menealo a, .closed, .menealo span {
    background-image: linear-gradient(-180deg, #7A7CE8 0%, #1200FE9E 100%);
    border: 1px solid #0600ff;
	border-bottom: 1px solid #5f4aff;
}

.news-shakeit .votes a {
    color: #4014e3;
}

.news-shakeit.mnm-published .votes, .news-shakeit.mnm-published .clics {
    background-color: #A090F7;
}

.news-submitted a {
    color: #1423e3;
}

.news-details {
    color: #3c3c3c;
    border-top: 1px solid #8486C8;
    border-bottom: 1px solid #8486C8;
}

.news-details a:link, .news-details a:visited, .news-tags, .news-tags a:link, .news-tags a:visited {
    color: #5c50b9;
}
.news-details a.comments {
    background-color: #bab7f7;
	color: #3d5393;
}

.news-details a i, .news-details button i {
    color: #938BFE;
}

.news-details button {
    color: #3d5393;
}

.news-details span.votes-up i, .news-details span.votes-anonymous i, .news-details span.votes-down i, .news-details span.karma-letter {
    color: #8083ff;
}

.news-details-data-down select {
    background: #FFF;
    color: #2314e3;
}
/* fin meneos*/

/* comentarios noticia */

.comment-header .comment-order {
    color: #1423e3;
}

.comment {
    background-color: #FFF5ED;
    border-radius: 8px;
}

.comment.author {
    background-color: #FBDDDD;
    border-radius: 8px;
}

.comment.high {
    background-color: #7676ce;
}

.comment.author.high {
    background-color: #EC9191;
}


.comment.author .comment-body, .comment.high.author .comment-body, .comment.high .comment-body {
    background-color: transparent;
}
/* fin comentarios noticia */

/* portada */

.sidebox .mainsites ul li {
    display: inline-block;
    color: #FF935B;
    border: 2px solid #FF935B;
    padding: 6px;
    margin-bottom: 10px;
    -webkit-border-radius: 35px;
    -moz-border-radius: 35px;
    border-radius: 35px;
}

#toplink a.footer {
    background-color: #333BAE;
}


/* fin portada */

/* perfiles */

.section-profile-header-stats span {
    color: #000;
}

.section-profile .contents-layout .contents-submenu a {
    color: #0006ff;
}

/* fin perfiles */

/* página subs */

.section h1, .section h2 {
    color: #333BAE;
}

a.action-link, .action-text {
    text-align: center;
    background-color: #333BAE;
    color: white;
}

.subs-listing .name a {
    color: #333BAE;
}

.subs-listing .followers {
    color: #333BAE;
}

.subs-listing .followers span {
    border: 2px solid #333BAE;
    border-radius: 100px;
}


/* fin página subs */
}
