css:
@-moz-document domain("ts.kg") {
.app-show-header {
    border: none;
    border-radius: 0;
    margin: 0;
    padding: 0;
}

.app-show-header-title, .app-show-header-title-original {
    display: inline;
}

.app-show-header-title-original {
    border-left: 2px solid #777;
    padding-left: 5px;
}

.main-container {
    background-color: rgba(0,0,0,0.35);
    box-shadow: 0 0 10px rgba(0,0,0,0.5);
    margin-top: 0;
    width: 1245px;
}

.nav-pills,
.player-4,
iframe,
.app-show-header-years,
footer,
.oops-under,
.oops-area,
.player-overlay,
table > tbody > tr > td > span:nth-child(1),
.navbar-header {
    display: none;
}

.show-title {
    color: #0e7ddc;
}

.well,
.app-news-date,
.category_nav {
    background-color: rgba(0, 0, 0, 0.3);
    border: 1px solid #222;
}

/*  navbar */
.navbar-default {
    background-color: #222;
    border-color: #080808;
}

.navbar-default .navbar-nav>li>a:hover {
    color: #fff !important;
}

.navbar-default .navbar-nav>.open>a:hover {
    color: #000 !important;
}

.navbar-default .navbar-nav>li>a:focus {
    color: #777;
    outline: auto;
}

.navbar-nav>li>.dropdown-menu {
    margin-top: 0;
    border-top-left-radius: 0;
    border-top-right-radius: 0;
    background-color: #333;
}

.dropdown-menu>li>a:hover {
    background-color: #666;
}

.dropdown-menu>li>a {
    color: #337ab7;
    margin-left: 10px;
    list-style-type: circle;
}

.dropdown-menu .divider {
    background-color: #555;
}

.dropdown-header {
    border-top: 1px solid #555;
    margin-top: 6px;
    margin-left: 10px;
}
/*  end navbar */

/*  player */
#player, .player {
    height: calc(100vh - 200px);
    width: 100%;
}

.player-12 {
    width: auto;
}

.player-8 {
    width: 100%;
}
/*  end player */

.news-link {
    color: #777;
}

a:hover {
    color: #1b65a7;
}

.bg-success, .bg-warning, .panel {
   background-color: #dff0d800;
}

.panel-default, .panel-default>.panel-heading {
    border-color: #333;
}

.panel-default>.panel-heading {
    color: #777;
    background-color: #2222229c;
}

.panel-body {
    display: flex;
    justify-content: center;
}

.app-show-tags li>a {
    border: 1px solid #555;
}

.show-shots-galery>div>a>img {
    display: inline-block;
    max-width: 100%;
    height: auto;
    padding: 4px;
    line-height: 1.42857143;
    background-color: #333;
    border: 1px solid #333;
    border-radius: 4px;
    -webkit-transition: all .2s ease-in-out;
    -o-transition: all .2s ease-in-out;
    transition: all .2s ease-in-out;
}


::selection {
    color: #9a9a9a;
    background: #000;
}

.table>tbody>tr>td {
    border-top: 1px solid #555;
}

a.text-muted {
    color: #337ab7;
}

.app-show-episode-title {
    color: #777;
}

body {
    background: #444 url(https://img-fotki.yandex.ru/get/9827/161026817.1/0_dd0dc_2bd26520_orig) center fixed ;
    color: #777;
}
}
