css:
@-moz-document domain("www.wirtualnemedia.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #de5f16 #1c1c1c;
}
a,h1,h2,h3,h4{
    color: #de5f16 !important;
}
a:hover,p,span{
    color: #fff !important;
}
*{
    box-shadow: none !important;
}
input[type="text"],input[type="password"],input[type="email"],input[type="search"],input[type="submit"],textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
}
input[type="submit"]:hover{
    background: #de5f16 !important;
    color: #fff !important;
}
/*HEADER*/
.dropdown-menu{
    background: #1c1c1c;
    border-left: 1px solid #de5f16;
    border-right: 1px solid #de5f16;
}
.navbar-right .dropdown-menu{
    border: 0;
}
.recently-visited{
    background: #0a80a8 !important;
}
.recently-visited > a{
    color: #fff !important;
}
.recently-visited > a:hover{
    color: #de5f16 !important;
}
.parent-menu{
    background: #1c1c1c;
}
.full-menu ul li a{
    color: #1c1c1c !important;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.well,.col-small-tabs,a.news-box,.only-here .news-box,.gray-box{
    background: #1c1c1c;
}
.right-tab-nav.nav-tabs > li.active > a{
    color: #1c1c1c !important;
    border: 0;
}
.right-tab-nav.nav-tabs > li > a{
    border: 1px solid #de5f16;
    border-bottom: 0;
    background: #1c1c1c;
}
.right-tab-nav.nav-tabs > li > a:hover{
    background: #de5f16;
    color: #1c1c1c !important;
}
#set_wm_as_homepage img{
    filter: invert();
}
a.news-box:hover p,.only-here .news-box:hover p,.job-button,.s-advertisement .news-box:hover .news-box-content h2.news-title,.section-btn,#video .section-title a,.section-name a{
    color: #1c1c1c !important;
}
.s-tv .news-row .news-box-item:nth-child(n+8) .news-box:not(:hover){
    border: 1px solid #de5f16;
    background: #1c1c1c;
}
.col-small-tabs:hover .news-desc{
    color: #fff !important;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.comment-button,blockquote,p.cytat,.other-news{
    border: 1px solid #de5f16 !important;
    background: #1c1c1c;
}
.comment-button:hover,.nav-tabs > li.active > a{
    background: #de5f16;
    color: #1c1c1c !important;
}
.most-popular,.recommend,.l-news,#popular .nav-tabs > li:first-child,#popular .nav-tabs > li:last-child{
    background: #1c1c1c;
}
.tags li a{
    border: 1px solid #de5f16;
    background: #1c1c1c;
}
.tags li a:hover{
    background: #de5f16;
    color: #1c1c1c !important;
    text-decoration: none;
}
.wm-pagination .submit,.wm-pagination > li > a,#popular .nav-tabs > li{
    background: #1c1c1c;
}
.wm-pagination > li.active > a,#popular .nav-tabs > li > a:hover{
    color: #1c1c1c !important;
}
#popular .nav-tabs > li a:hover{
    background: #de5f16 !important;
}
#popular .tab-content{
    border-top-color: #de5f16;
}
/*KOMENTARZE*/
.comments-number{
    color: #fff;
}
.comments-number span{
    color: #de5f16 !important;
}
/*KOMENTARZE*/
/*ARTYKUŁ*/
/*PODSTRONY*/
.categories-wrapper,.categories-wrapper .category-sidebar .scrollbar,header.row{
    background: #1c1c1c;
}
#page-about-us .mod-newsletter a{
    color: #1c1c1c !important;
}
body > div,#page-about-us tbody td{
    background: #1c1c1c !important;
}
/*WYSZUKIWANIE*/
.sub-news-sidebar{
    background: #1c1c1c;
}
/*WYSZUKIWANIE*/
/*PODSTRONY*/
/*FOOTER*/
#footer,footer{
    background: #1c1c1c;
}
.copyright:before{
    content: "Ciemny motyw by Maciejozaman";
    color: #de5f16;
    cursor: none;
}
/*FOOTER*/
}
