/* Reset logo position and add a hover effect*/
 #logo a img {
    margin-left: 0px;
}
#logo a img:hover {
    opacity: 0.5;
    transition: opacity .25s ease-in-out;
}
/* Editing main menu */
 .mainMenu > ul > li:not(:last-child) {
    margin-right: -2px;
    border-radius: 5px;
    background: rgba(0, 0, 0, 0.5);
}
.mainMenu > ul > li:not(:last-child):hover {
    transition: .25s ease-in-out;
    background: rgba(255, 0, 0, 0.5);
}
/* Active button */
 .mainMenu > ul > li.active > a {
    color: #fff;
}
.mainMenu > ul > li.active {
    border-radius: 5px;
    background: rgba(255, 0, 0, 0.5);
}
.mainMenu > ul > li.active:hover {
    border-radius: 5px;
}
.mainMenu > ul > li.active > a:hover {
    color: #fff;
}
/* Resource button*/
 .mainMenu > ul > li:not(.active) {
    border-radius: 5px;
    background: rgba(0, 0, 0, 0.5);
    color: #fff;
}
.userPanel {
    position: fixed;
    height: 40px;
    background: rgba(179, 179, 181, 1);
    box-shadow: 0 8px 6px -8px black;
}
.userPanel > div > ul.userPanelItems > li > a, .userPanel > div > ul.userPanelItems > li > div:not(.dropdownMenu) {
    color: #fff;
    position: relative;
	top: 1px;
}
.userPanel .icon {
    color: #fff;
}
ol:not(.containerList), ul.userPanelItems {
    position: relative;
    top: 4px;
    background: #333;
    width: 400px;
    height: 32px;
    border-radius: 5px;
}
.userPanel > div > ul.userPanelItems > li > a, .userPanel > div > ul.userPanelItems > li > div:not(.dropdownMenu) {
    color: #fff;
    transition: .25s ease-in-out;
}
.userPanel .icon {
    color: #fff;
}
.userPanel > div > .searchBar {
    background-color: #333;
    top: 15%;
    border-radius: 5px;
}
.userPanel > div > ul.userPanelItems > li {
    float: left;
    position: relative;
    left: 3%;
}
.userPanel > div > ul.userPanelItems > li > .framed > img {
    border-radius: 5px;
}
.quoteBox.container {
    border-radius: 5px;
}
.interactiveDropdown {
    box-shadow: 0 0px 8px 0 rgba(0,0,0,0.5);
    border-radius: 10px;
}
.framed > canvas, .framed > img, .framed > .icon {
    border-radius: 5px;
}

/* */
article:not(.wbbPostDeleted) > div{
	background: rgba(0,0,0,0.1);    
}
.messageCollapsed {
    color: #888888;
    opacity: .5;
}
.tabularBox, .tabularBoxTitle > header {
    border-radius: 5px;
}
.dialogContent {
    overflow: hidden;
}
.dialogContainer {
    border: none;
}
