css:
@-moz-document domain("wikipedia.org") {
body {
        background: #fff0 url(https://www.barraques.cat/pngfile/big/42-429591_twilight-sparkle-wallpaper-my-little-pony-wallpaper-twilight.png)!important;
	background-attachment: fixed!important;
	background-size: 100% auto!important;
	background-repeat: no-repeat!important;
	background-position: center!important;
	overflow: auto!important;
}

.mw-body, .parsoid-body {
    padding: 1em;
    background-color: #00000061;
    color: #ffffff;
    direction: ltr;
    box-shadow: 0 0 20px 9px #00ffff87;
}

#mw-page-base {
    height: 5em;
    background-position: bottom left;
    background-repeat: repeat-x;
    background-image: none;
    background-color: #f000;
}

.mw-wiki-logo {
    background-image: url(https://i.ibb.co/jzSLrB8/enwiki-MLP-Sci-Twi.png);
    background-size: 100% auto!important;
}

.vectorTabs li {
    float: left;
    line-height: 0em;
    display: block;
    height: 100%;
    margin: 0;
    padding: 0;
    background-image: none!important;
    box-shadow: 0 0 1px 1px #00ffff87;
}

.vectorTabs li a {
    display: block;
    height: 1.9em;
    padding-left: 0.615em;
    padding-right: 0.615em;
    color: #ff00aa;
    cursor: pointer;
    font-size: 0.8125em;
}

.vectorTabs li.selected a, .vectorTabs li.selected a:visited {
    color: #0c34d3;
    text-decoration: none;
}

#pt-anonuserpage {
    color: #ffffff;
}

#simpleSearch input {
    background-color: #ffffff;
    background-color: #00000085;
    color: #fff;
    margin: 0;
}

#searchInput {
    width: 100%;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    border: 1px solid #0ff;
    border-radius: 2px;
}

.suggestions-results {
    background-color: #000;
    cursor: pointer;
    border: 1px solid #0ff;
    padding: 0;
    margin: 0;
}

.suggestions-result {
    color: #ff00aa;
    margin: 0;
    line-height: 1.5em;
    padding: 0.01em 0.25em;
    text-align: left;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

.suggestions-special .special-label {
    color: #fff;
    text-align: left;
}

.suggestions-special {
    position: relative;
    background-color: #000;
    cursor: pointer;
    border: 1px solid #0ff;
    margin: 0;
    margin-top: -2px;
    display: none;
    padding: 0.25em 0.25em;
    line-height: 1.25em;
}

a {
    text-decoration: none;
    color: #ff00aa;
    text-shadow: 0 0 6px black;
    background: none;
}

a:visited {
    color: #0c34d3;
    text-shadow: 0 0 6px black;
    background: none;
}

#mw-panel .portal .body li a {
    color: #ff00aa;
    text-shadow: 0 0 6px black;
}

h1, h2, h3, h4, h5, h6 {
    color: #0033ff;
    	text-shadow:
		-1px -1px 0 #fff,
		1px -1px 0 #fff,
		-1px 1px 0 #fff,
		1px 1px 0 #fff;
    background: none;
    font-weight: bold;
    margin: 0;
    overflow: hidden;
    padding-top: 0.5em;
    padding-bottom: 0.17em;
    border-bottom: 1px solid #0ff;
}

.toc, .mw-warning, .toccolours {
    border: 1px solid #0ff;
    background-color: #00000085;
    padding: 5px;
    font-size: 95%;
}

.tocnumber {
    padding-left: 0;
    padding-right: 0.5em;
    color: #fff;
}

.infobox {
    border: 1px solid #0ff;
    border-spacing: 3px;
    background-color: #00000085;
    color: white;
    margin: 0.5em 0 0.5em 1em;
    padding: 0.2em;
    float: right;
    clear: right;
    font-size: 88%;
    line-height: 1.5em;
}

div.thumbinner {
    border: 1px solid #0ff;
    padding: 3px;
    background-color: #00000085;
    font-size: 94%;
    text-align: center;
    overflow: hidden;
}

html .thumbimage {
    background-color: #0000;
    border: 1px solid #0ff;
}

th.summary {
    text-align: center;
    font-size: 125%;
    font-weight: bold;
    font-style: italic;
    background: #314180;
    padding: 0.25em 1em;
    line-height: 1.5em;
    line-height: normal;
}

element.style {
    text-align: center;
    background: #314180;
    padding: 0.25em 1em;
    line-height: 1.5em;
}

#mw-panel .portal h3 {
    font-size: 0.75em;
    color: #0033ff;
    font-weight: bold;
    margin: 0.5em 0 0 0.66666667em;
    padding: 0.25em 0;
    cursor: default;
    border: 0;
}

.catlinks {
    border: 1px solid #0ff;
    background-color: #00000085;
    padding: 5px;
    margin-top: 1em;
    clear: both;
}

#mw-panel .portal .body li a:visited {
    color: #0c34d3;
}

.mw-ui-button {
    background-color: #00000085;
    color: #ffffff;
    display: inline-block;
    border: 1px solid #0ff;
    border-radius: 2px;
}

.wb-langlinks-link > a:link, .wb-langlinks-link > a:visited {
    color: #ffffff !important;
}

.navbox, .navbox-subgroup {
    background-color: #000000;
}

.navbox-even {
    background-color: #000000;
}

.wikitable > tr > th, .wikitable > tr > td, .wikitable > * > tr > th, .wikitable > * > tr > td {
    border: 1px solid #0ff;
    padding: 0.2em 0.4em;
}

.wikitable {
    background-color: #00000085;
    color: #fff;
    margin: 1em 0;
    border: 1px solid #0ff;
    border-collapse: collapse;
}

table.ombox {
    margin: 4px 10%;
    border-collapse: collapse;
    border: 1px solid #0ff;
    background-color: #00000085;
    box-sizing: border-box;
}

#footer ul li {
    color: #fff;
    margin: 0;
    padding: 0;
    padding-top: 0.5em;
    padding-bottom: 0.5em;
    font-size: 0.7em;
}

.wikitable > tr > th, .wikitable > * > tr > th {
    background-color: #000;
    text-align: center;
}

/* width */
::-webkit-scrollbar {
  width: 15px;
}

/* Track */
::-webkit-scrollbar-track {
  background: #314180; 
}

/* Handle */
::-webkit-scrollbar-thumb {
  background: #0ff; 
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #0ff; 
}


table {
    background-color: #0ff0;
}

tr {
    background-color: #00000085 !important;
}

.infobox, td, th {
    background-color: #00000085 !important;
}

tr {
    background-color: #000;
}
}

@-moz-document url("https://en.wikipedia.org/wiki/Main_Page"), url("https://en.wikipedia.org/wiki/Portal:Contents"), url("https://en.wikipedia.org/wiki/Portal:Featured_content"), url("https://en.wikipedia.org/wiki/Portal:Current_events") {
* {
    background-color: #0000 !important;
    border-color: #0ff !important;
    color: #fff
}
}
