css:
@-moz-document url-prefix("https://www.beeminder.com/") {
.dashboard.content {
	padding: 0;
}
.dashboard.content .expanded-toggle {
  width: 2rem;
  height: 2rem;
  border-bottom: 1px solid white;
  padding: 0;
  margin: 0;
  clip-path: none;
  display: flex;
  align-items: center;
  justify-content: center;
}
.dashboard.content .expanded-toggle .mega-octicon, .dashboard.content .expanded-toggle .mega-octicon.octicon-chevron-down {
	padding: 0;
  font-size: 24px;
}
.dashboard.content .goal.row {
  margin-top: 0;
}
.dashboard.content .goal.row:nth-child(even) {
	background-color: white;
}
.dashboard.content .goal a.slug, .content.archive .goal a.slug, .dashboard.content .goal .descriptors .title, .content.archive .goal .descriptors .title {
  line-height: 1rem;
  height: 1rem;
}
.dashboard.content .goal .slug, .content.archive .goal .slug, .dashboard.content .goal .pledge, .content.archive .goal .pledge, .dashboard.content .goal .baremin, .content.archive .goal .baremin, .dashboard.content .goal .doom, .content.archive .goal .doom, .dashboard.content .goal .last-datapoint, .content.archive .goal .last-datapoint, .dashboard.content .goal a.slug.solo, .content.archive .goal a.slug.solo, .dashboard.content .goal .summary, .content.archive .goal .summary, .dashboard.content .goal div.todayta, .content.archive .goal {
  height: 2rem;
  line-height: 2rem;
}
.dashboard.content .goal .descriptors {
  height: 2rem;
}
.dashboard.content .goal-data {
  max-width: calc(100% - 2rem);
}
.dashboard.content .goal-data > .no-gutters {
  margin: 0 !important;
}
.dashboard.content .dashboard-data {
  z-index: 100;
  margin: 0 0 2px 4px !important;
  height: 2rem;
}
.dashboard.content .dashboard-data > * {
	margin: 0 .5rem 2px 0 !important;
  border: 1px solid #ccc !important;
  font-size: 12px;
  height: 100% !important;
}
.dashboard.content form input, .content.archive form input {
	font-size: 12px;
}
.dashboard .infinibee-parent {
  display: none;
}
.dashboard.content form .octicon-triangle-down, .content.archive form .octicon-triangle-down {
  left: 95px;
  top: 10px;
  border: none !important;
}
.dashboard.content .mega-octicon {
	line-height: 100%;
  font-size: 28px;
}
.dashboard.content .bbtn {
	padding: 0 30px;
}
}
